INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (702, N'成都-西昌-雅砻江-泸沽湖680KM  约 11个小时', 0, 1, 1, N'泸沽湖特色客栈', N'<p><span style="FONT-FAMILY: 新宋体">早上统一集合，经雅西高速，一路感受雅西高速魅力，西昌礼州古镇午餐（30分钟），后乘车前往神秘的“女儿国”泸沽湖，路途不是很远，就是翻过两坐大山，磨盘山和小高山，跨国一条著名的河流―-被《中国国家地理》称作充满女性文化的河流--雅砻江，在当地称大金河，伴随导游生动的介绍泸沽湖摩梭风情,风俗，将有助于您完整的游览泸沽湖精华的景点，晚19：30左右抵达泸沽湖边，晚餐品尝摩梭风味餐（猪膘肉、腊肠、香肠）。后入住酒店，美美的睡上一觉，你会发现泸沽湖的夜晚出奇的静..静的只有自己的呼吸...</span></p><p>&nbsp;<img title="泸沽湖风格v.jpg" src="/ueditor/net/upload/2016-07-14/7634eae4-1245-428c-8550-be836c0e66e4.jpg"/></p>', 1, 75)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (738, N'成都    西双版纳（航程3.5小时）  ', 0, 0, 0, N'景洪市', N'<p>成<span style="FONT-FAMILY: 黑体">都双流机场集合，乘坐航班前往（机上无导游，直飞版纳，全程约2.5小时）素有“绿色明珠”之美誉的西双版纳，抵达后入住酒店。</span></p><p><span style="FONT-FAMILY: 黑体"></span></p><p><span style="FONT-FAMILY: 黑体"></span>&nbsp;</p><p><span style="FONT-FAMILY: 黑体">温馨小提示：版纳的烧烤非常有名，晚上可以自行前往曼听小寨或江北景亮路品尝</span></p><p><img title="d1d13d71-4361-44e1-81c7-2d7651245980_720_540.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-08-03/9db7142d-cd36-4a1d-be6d-fc5e589ddda2.jpg"/></p><p>&nbsp;</p>', 1, 89)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (348, N'成都-- 北京', 0, 0, 0, N'北京', N'<p><span style="font-size:15px;font-family:方正苏新诗柳楷简体">双流机场乘机直飞北京机场</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">，沿途车观北京美景，入住酒店！迎接第二天的皇城之旅！</span></p><p><img src="/ueditor/net/upload/2016-07-12/ffaf8e37-5f77-4800-a917-d2af9df3a831.jpg" title="QQ图片20160712165354.jpg"/>&nbsp; &nbsp; &nbsp; &nbsp;<img src="/ueditor/net/upload/2016-07-12/63f3ccfc-c51d-4575-b83b-dcc7d186719f.jpg" title="QQ图片20160712165405.jpg"/> &nbsp; &nbsp; &nbsp;&nbsp;<img src="/ueditor/net/upload/2016-07-12/3517ad6a-1586-48ee-a561-9826d4eaf04e.jpg" title="QQ图片20160712165418.jpg"/></p>', 1, 71)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (350, N'北京', 1, 1, 0, N'北京', N'<p><strong><span style="font-family:宋体;color:red">☆不朽气魄，登雄伟长城，感受雄伟壮丽的祖国风光</span></strong></p><p><span style="font-size:15px;font-family:方正苏新诗柳楷简体">早观<strong><span style="color:red">【升旗仪式】</span></strong>，感受共和国永不谢幕的典礼(早餐打包)。后乘车赴延庆（车程约90分钟）,游览世界八大奇迹之一的<strong><span style="color:red">【八达岭长城】</span></strong>(约120分钟),体会“不到长城非好汉的”的豪迈。万里长城象一条巨龙盘踞在祖国的北面，绵延数万里，纵贯两千年，</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体;color:#505050">雄伟壮观，气势磅礴。</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">八达岭长城更是“奇迹”中的经典,，登上长城，脚下的崇山峻岭，蜿蜒起伏，四季风光各不相同：春天野花竞开，夏季绿色满眼，秋天层林尽染，冬天雪域莽莽，景色令人叹为观止。前往指定地点用中餐(11:30-12:30左右)。（可自行参观十三陵果园（2.0小时左右），游览<strong><span style="color:red">【奥林匹克公园】</span></strong>。外观2008奥运会开闭幕式场馆<strong><span style="color:red">【鸟巢】</span></strong>及国家游泳馆<strong><span style="color:red">【水立方】</span></strong>（60分钟），自由拍照。</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">水立方和鸟巢分别位于中轴线的两侧，一方一圆，遥相呼应，构成了人文奥运的独特风景线。虽然奥运的圣火已渐行渐远，留给北京的却是无上的荣耀和美好的回忆，还有一座座历经辉煌的场馆建筑，以及永不言败的奥运精神。自费参观<span style="text-decoration:underline;">奥运官方梦之队大型现场演出</span></span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">（60分钟）-<strong><span style="color:red">【中国梦-世界情】</span></strong><span style="color:#7030A0">，</span><strong>这一刻，让我们释放内心的激情，共同沐浴永恒的奥运精神;这一刻，让我们凭借民族文化的光芒，一起放飞希望和梦想; 这一刻，让我们点燃生活的美好祈愿，把大地云海渲染; 这一刻，让我们向世界播洒文明的火种，奏一曲和平和谐发展的乐章; 就在这一刻，凝聚我们共同超越的力量，升</strong></span><strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体">腾中国梦，感动世界情……让我们一起重温奥运激情，再现精彩视界，让您的心灵与舞台共炫丽！</span></strong></p><p><img src="/ueditor/net/upload/2016-07-12/a5becd01-c2ca-4930-9ed2-1588b25a528a.jpg" title="QQ图片20160712174318.jpg"/></p>', 3, 71)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (732, N'九寨沟沟口-特色藏寨-中国古羌城-都汶高速-成都', 1, 1, 0, N'不含', N'<p><span style="font-family: 宋体, SimSun; font-size: 16px;">早餐后前往川主寺途中</span><span style="font-family: 宋体, SimSun; font-size: 16px;">游览</span><strong><span style="font-family: 微软雅黑;font-size: 16px">特色藏寨</span></strong><span style="font-family: 微软雅黑;font-size: 16px">，</span><span style="font-family: 宋体, SimSun; font-size: 16px;">观藏族建筑，体验藏羌文化，随后前往若朴土特产展销中心</span><span style="font-family: 微软雅黑;font-size: 16px">及</span><strong><span style="font-family: 微软雅黑;font-size: 16px">藏羌文化馆</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">了解丝绸文化；享用特色午餐</span><span style="font-family: 微软雅黑;font-size: 16px">“</span><strong><span style="font-family: 微软雅黑;font-size: 16px">牦牛汤锅”</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">而后途径松潘参观</span><strong><span style="font-family: 微软雅黑;font-size: 16px">中国古羌城</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">，而后乘车返回成都结束愉快的旅程！</span></p><p><img src="/ueditor/net/upload/2016-08-03/9af7f062-bb22-4857-bfc9-d971b29f2f7f.jpg" title="1382789725353p187qavm7n1avk16bnmguk2fgfbd.jpg"/></p><p><br/></p><p><img src="/ueditor/net/upload/2016-08-03/a1bb9f32-c2e4-4c72-9ce4-52b5890dcf11.jpg" title="CghzgVUmK42ASBhNABt1_FgixZg428.jpg"/></p>', 3, 88)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (317, N'成都→长沙（飞行时间约1.5小时）', 0, 0, 0, N'长沙', N'<p><span style="font-size:14px;font-family:新宋体;color:#1E4E79">早根据航班时间，成都双流机场乘机赴长沙，抵达后乘车至酒店入住。晚市区自由逛五一大道、黄兴路或太平溪，品尝当地特色小吃臭豆腐、口味虾、辣子鸡、水煮活鱼等等。(参考航班：成都航空EU2205次07:05/08:55或奥凯航空BK2780 次21:40/23:25）以实际出票时间为准。</span></p>', 1, 70)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (900, N'川主寺-茂县-成都', 1, 1, 0, N'不含', N'<p><span style="font-family:宋体"></span></p><p><span style="font-size: 16px; font-family: 宋体;"></span></p><p style="line-height:21px"><span style="font-family: 宋体, SimSun; font-size: 16px;"><span style="font-family: 宋体, SimSun;">早餐后乘车返回成都，途中游览特色藏寨，参观藏族建筑，体验藏羌文化，随后参观</span><span style="font-family: 宋体, SimSun; color: rgb(237, 125, 49);">蜀锦丝绸藏羌文化馆</span><span style="font-family: 宋体, SimSun;">，了解古丝绸之路的丝绸文化，灿烂的蜀锦工艺及蜀锦的历史文化；然后参观土特产，川主寺享用特色午餐，下午参观</span><span style="font-family: 宋体, SimSun; color: rgb(237, 125, 49);">坪头羌寨</span><span style="font-family: 宋体, SimSun;">或者茂县参观</span><span style="font-family: 宋体, SimSun; color: rgb(237, 125, 49);">七星关拉莫央宗皮具店</span><span style="font-family: 宋体, SimSun;">；返回成都结束愉快的旅程。(约18:00-20:00左右抵达成都茶店子散团，由于高原路段有可能堵车，请您预定第二天以后的火车或是飞机)</span></span></p><p><span style="font-size: 16px; font-family: 宋体;"></span><br/></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong style="color: rgb(0, 176, 80);">行程小贴士</strong></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"></span></p><p><span style="color: rgb(0, 176, 80); font-family: 宋体, SimSun; font-size: 16px;"></span></p><p><span style="font-family: 宋体, SimSun; color: rgb(255, 0, 0); font-size: 16px;">1.行车时可能会遇上堵车等情况，行车时间会比平时要长，客人不要购买返程当天的机票或火车票。<br/>2今天是本次大草原之旅游程的最后一站，如若你对此次行程有任何异议，请告诉我们，我们会在第一时间根据你反馈的情况落</span></p><p><span style="font-family: 宋体, SimSun; color: rgb(255, 0, 0); font-size: 16px;">实调查并及时处理；这次旅程是一次心灵的洗礼，是人生观的再次升华，回家后分享给大家吧</span></p><p><span style="color: rgb(0, 176, 80);"></span><br/></p><p><br/></p>', 3, 58)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (701, N'成都―快速通道―都江堰 ―青城山', 0, 0, 0, N'', N'<p><span style="FONT-SIZE: 12pt; FONT-FAMILY: " mso-bidi-font-family:="" mso-font-kerning:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="">上午：</span></p><p><span style="FONT-SIZE: 12pt; FONT-FAMILY: " mso-bidi-font-family:="" mso-font-kerning:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;经灌县古城，南桥至都江堰景区进入离堆公园，游览川西第一名园<span lang="EN-US">-</span>清溪园、堰功道、卧铁、张松银杏（西游记里的人参果树）、伏龙观 。之后来到战国秦昭王时期（公元前<span lang="EN-US">227</span>年）蜀郡守李冰在岷江上修建的中华第一古堰<span lang="EN-US">-</span>被列为“世界文化遗产”的【<strong>都江堰水利工程】</strong>：宝瓶口引水口、飞<span style="FONT-SIZE: 12pt; FONT-FAMILY: " mso-bidi-font-family:="" mso-font-kerning:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="">沙堰泄洪坝、观鱼嘴分水堤，过安澜索桥。自由参观<span lang="EN-US">5.12</span>地震后重建的的秦堰楼、纪念李冰父子的二王庙。 约<span lang="EN-US">12</span>点享用午餐。</span></span></p><p><img src="/ueditor/net/upload/2016-08-03/3affe8b6-7ffa-41ba-a9fc-046f0f62345b.jpg" title="CghzfVUvWO-AQddxAAP62WiCCfs604.jpg"/></p><p><span style="FONT-SIZE: 12pt; FONT-FAMILY: " mso-bidi-font-family:="" mso-font-kerning:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:=""><span style="FONT-SIZE: 12pt; FONT-FAMILY: " mso-bidi-font-family:="" mso-font-kerning:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="">下午：</span></span></p><p style="LINE-HEIGHT: 24px; TEXT-INDENT: 32px"><span style="FONT-SIZE: 16px; FONT-FAMILY: ">午餐后到达青城山景区停车场，换乘环保观光车（往返20元/人，费用自理）游览素有“青城天下幽”之美称的道教发祥地――【<strong>青城山】</strong>（游览时间3个小时左右）经山门进入景区，呼吸着清新的空气，拾阶而上，登高远眺，映入您眼帘是林木葱郁，层峦叠障，经雨亭、天然阁、怡乐窝、引胜亭，便来到了天然图画坊。天然图画坊位于龙居山牌坊岗的山脊上，是一座十角重檐式的清代亭阁；经山荫亭、凝翠桥、五洞天来到道教创始人张道陵结茅传道居住地――<strong>天师洞</strong>。或乘坐游船经月城湖（月城湖船票往返10元/人，费用自理）坐索道至<strong>上清宫</strong>（可根据自身情况选择单程35元/人，往返60元/人）。</span></p><p><img src="/ueditor/net/upload/2016-08-03/bb95c520-6cba-42fc-8541-887d5c8eabbc.jpg" title="c4.jpg"/></p>', 1, 82)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (957, N'[汽车]川主寺-18KM松潘-138KM茂县-147KM成都（约300KM，行车约6小时）', 1, 1, 0, N'不含', N'<p style="TEXT-ALIGN: left; LINE-HEIGHT: 1.5em; TEXT-INDENT: 16px; MARGIN: 0px 4px 0px 0px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><span style="FONT-SIZE: 16px">早餐后感受安多藏族、嘉绒藏族、羌族不同的民族风情，游览<strong>藏族文化特色村寨</strong>（约90分钟，近距离了解藏族民居特色、建筑艺术及生活方式，体验民族地区文化特色及经济建设成就），参观<strong>高原土特产展销中心</strong>（约60分钟）,参观</span><strong><span style="FONT-SIZE: 16px">丝绸博物馆</span></strong><span style="FONT-SIZE: 16px">，了解古丝绸之路厚重的历史文化和复杂的制作工艺，可参与丝绸的拉丝和剥茧等互动活动，后乘车经历史名城――<strong>松潘古城</strong>，观</span>1933<span style="FONT-SIZE: 16px">年茂县地震遗址――<strong>叠溪海子</strong>，重温岷江峡谷风光，感受5.12大地震给沿线带来的巨大重创，以及灾后重建的繁荣，经汶川、映秀返成都结束愉快的旅程！</span></span></p><p style="TEXT-ALIGN: center; LINE-HEIGHT: 1.5em; TEXT-INDENT: 16px; MARGIN: 0px 4px 0px 0px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><span style="FONT-SIZE: 16px"><img title="201504271448094104.jpg" src="/ueditor/net/upload/2016-08-10/65dfd07e-81a8-4807-a76e-b884e08c038f.jpg"/></span></span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 16px; MARGIN: 0px 4px 0px 0px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><span style="FONT-SIZE: 16px"></span></span>&nbsp;</p><p style="LINE-HEIGHT: 21px"><span style="FONT-SIZE: 16px"><strong><span style="FONT-FAMILY: 宋体; COLOR: #ff6600">温馨提示：</span></strong></span></p><p style="LINE-HEIGHT: 24px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">1、此天行车时可能会遇上堵车等情况，行车时间较长，请不要购买当天返程的机票或火车票。<br/>2、景区内配套的商店有旅游纪念品及当地特色产品出售，景区讲解员可能会为游客推荐一并解说，若感兴趣，请您根据自身所需谨慎选购，记得索要发票。</span></p><p><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">3、今天是本次大草原之旅游程的最后一站，如若你对此次行程有任何异议，请告诉我们，我们会在第一时间根据你反馈的情况落实调查并及时处理。</span></p><p></p>', 3, 92)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (266, N'九寨沟沟口-特色藏寨-川主寺-松潘-茂县-成都', 1, 1, 0, N'不含', N'<p style="text-indent:32px;line-height:24px"><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;">早餐后前往川主寺</span><span style="font-size: 16px;font-family: 微软雅黑, sans-serif">途中游览特色藏寨，观藏族建筑，体验藏羌文化，随后参观土特产销售中心，中心主要销售（当地药材、牛肉、水晶工艺品等）游览<strong>藏羌文化馆参观丝绸工坊</strong>，了解古丝绸之路的丝绸文化和历史文化，午餐后乘车前往松潘参观印象南红博物馆，该馆有商品出售，如需购买请认真甄选。而后乘车返回成都结束愉快的旅程！</span></p><p><br/></p>', 3, 62)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (741, N'景洪  -望天树-  景洪  ', 1, 0, 0, N'景洪 ', N'<p><span style="FONT-FAMILY: 宋体">早餐后乘车前往西双版纳<span style="COLOR: #ff6600">【<strong>国家森林公园望天树景区】</strong></span>（车程约2.5小时单程，游览约3小时<span style="FONT-SIZE: 13px; COLOR: black">沿途无导游讲解，配景区导游</span>）。游览北回归线最后的热带雨林―望天树?阿莲雅热带雨林主题国家公园，乘船游东方亚马逊―南腊河<strong><span style="COLOR: red">（单程游船自费40元/人、往返70/人）</span></strong>，运气好可看到最古老的无脊椎动物，“水中大熊猫”――桃花水母；穿越著名的菲利浦小道和蔡希陶小道，领略中国热带雨林的奇迹风光；登亚洲最高空中走廊――世界第一高树冠空中走廊<strong><span style="COLOR: red">（客人自理120元/人）</span></strong>游览结束返回市区。晚入住酒店。</span></p><p><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">温馨提示：单天景洪市区到望天树无导游，到了景点由内导带客人游览。</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">单天中餐、晚餐</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">客人自理</span></p><p><img title="望天树.jpg" src="/ueditor/net/upload/2016-08-03/4bb6b246-0719-411d-bc20-831c8ef466ed.jpg"/></p>', 4, 89)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (337, N'D1：成都--汶川130KM--马尔康245KM--观音桥镇68KM车程约8小时海拔2560米宿：观音桥镇   ', 0, 0, 0, N'神山脚下酒店', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 28px"><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">早上</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">7:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">成都广元宾馆门口统一集合（具体集合时间地点以导游通知为准）出发，上都汶高速，沿途领略阿坝川西风光，沿岷江支流杂谷脑河谷一路向上，感受藏羌风情</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">,</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">远观羌寨、碉楼、民居，经过理县，到达古尔沟用午餐（自理）后经米亚罗、翻越鹧鸪山，至马尔康，沿途观赏杂谷脑河谷峡谷风光，约</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">16:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">到达观音桥镇，换乘当地交通（往返</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">40</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元自理）经煨桑广场、玛尼墙后到达观音庙，登上海拔</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">3685</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">米之纳勒山（含神山之意）半坡，探访古老的观音庙，登上石阶、转动经筒、细数玛尼墙上的玛尼石来到绰斯甲观音菩萨座前朝拜祈福，了解第二个布达拉宫的由来，欣赏藏区高原日落！，晚餐（自理）后，在神山脚下入眠！</span></p><p>&nbsp;<img title="mp10578875_1428930579974_16.jpeg" src="/ueditor/net/upload/2016-07-12/96942466-03e4-4360-95e0-a37a058a0312.jpeg"/></p>', 1, 66)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (903, N'张家界森林公园→黄龙洞→袁家界→天子山 ', 1, 1, 1, N'武陵源', N'<table cellspacing="0" cellpadding="0" vspace="0" hspace="0" uetable="null"><tbody><tr><td style="BORDER-BOTTOM: #f0f0f0; BORDER-LEFT: #f0f0f0; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; PADDING-LEFT: 12px; PADDING-RIGHT: 12px; BORDER-TOP: #f0f0f0; BORDER-RIGHT: #f0f0f0; PADDING-TOP: 0px" valign="top"><p style="LINE-HEIGHT: 25px"><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">早餐后赠送游览享有“世界溶洞全能冠军”之称的【黄龙洞景区】（门票已含，不去不退，未含黄龙洞VIP通道95元/人）洞中灯火阑珊、石笋林立，犹如一株株古木错节盘根、洞中有洞、洞中有河、无奇不有，真乃名副其实的“地下魔宫”，洞中还有投保一亿元的石笋定海神针著名景点。</span></p><p style="LINE-HEIGHT: 25px"><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">下午赴中国第一个国家森林公园―【张家界国家森林公园】（首道大门票已含），从标志门进山游览武陵源自然遗产的最后一块处女地乘亚洲第一梯――百龙天梯上山（自理电梯72元/人）。观【袁家界景区】：游天下第一桥、迷魂台、五女出征、情人谷等景点.观电影《阿凡达》悬浮山取景景观―乾坤一柱。后游览【天子山景区】索道下山（自理天子山索道72元/人）。天子山有“峰林之王”的美称。著名景点：御笔峰、仙女散花、贺龙公园、梦笔生花、天子阁、贺龙铜像、西海峰林、天子点将台、神堂湾等。</span></p></td></tr></tbody></table><p><strong><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79; FONT-SIZE: 14px"></span></strong>&nbsp;</p><p><img style="FLOAT: none" title="QQ图片20160712144407.jpg" src="/ueditor/net/upload/2016-07-12/db563fce-64d8-4689-9f69-e5f3aea51c30.jpg"/>&nbsp;&nbsp;&nbsp; <img style="FLOAT: none" title="QQ图片20160712144433.jpg" src="/ueditor/net/upload/2016-07-12/77450354-159c-4600-8540-6458545f928f.jpg"/>&nbsp;&nbsp; &nbsp;<img style="FLOAT: none" title="QQ图片20160712144436.jpg" src="/ueditor/net/upload/2016-07-12/78b18534-bf3b-460f-9477-b762551e76a8.jpg"/></p><p><strong><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79; FONT-SIZE: 14px">晚间活动推荐：武陵源大型民俗风情歌舞晚会-（门票普座228元/人、贵宾座268元/人 超级VIP座398元/人无优免</span><span style="FONT-FAMILY: 新宋体; COLOR: blue; FONT-SIZE: 14px">）</span></strong></p><p><br/></p>', 3, 68)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (906, N'抵达成都', 0, 0, 0, N'', N'<p><span style="FONT-FAMILY: 宋体; FONT-SIZE: 14px">约中午</span><span style="FONT-FAMILY: &#39;Calibri&#39;,&#39;sans-serif&#39;; FONT-SIZE: 14px">11</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 14px">：</span><span style="FONT-FAMILY: &#39;Calibri&#39;,&#39;sans-serif&#39;; FONT-SIZE: 14px">30</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 14px">分抵达成都，</span><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79; FONT-SIZE: 14px">结束愉快的湖湘之旅！</span></p>', 6, 68)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (705, N'西昌-泸山-邛海-成都 420KM  6小时', 1, 1, 0, N'泸沽湖特色客栈', N'<p><span style="FONT-FAMILY: 宋体">早餐后出发<strong><span style="COLOR: black">游览“川南胜景”之<span style="text-underline: whitetext-decoration:underline">泸山</span></span></strong><span style="text-underline: whitetext-decoration:underline">（游览时间约1小时）<strong>，</strong></span></span><span style="FONT-FAMILY: 宋体; COLOR: #333333">泸山上树木葱茏，绿荫蔽日，黑瓦红墙的寺庙掩映其中，香烟袅袅，钟声徐徐。一棵古汉柏挥舞着虬枝，守候在庙中，见证了太阳城的千</span><span style="FONT-FAMILY: 宋体">――凉山彝族奴</span><span style="FONT-FAMILY: 宋体; COLOR: #333333">年沧桑。透过树枝间的缝隙，看见邛海静静躺在面前，远处是西昌的市区，大片大片的楼房一直延伸到山边。</span><span style="FONT-FAMILY: 宋体">途中可遇见居住子在山中的灵猴，没有专人管理的灵猴，更加贴近自然，参观中国目前唯一的陈列、研究凉山彝族奴隶社会的专题性博物馆隶社会博物馆（现免票）</span><span style="FONT-FAMILY: 宋体; COLOR: black">，后到达邛海自由活动（</span><span style="FONT-FAMILY: " color:="">60</span><span style="FONT-FAMILY: 宋体; COLOR: black">分钟）<strong>参观凉山元素农副产品（</strong></span><strong><span style="FONT-FAMILY: " color:="">40</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: black">分钟），航飞苦荞茶（</span></strong><strong><span style="FONT-FAMILY: " color:="">40</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: black">分钟）</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black">。</span><span style="FONT-FAMILY: 宋体">乘车回成都结束愉快行程！</span></p><p><strong><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体; COLOR: red">温馨提示</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">：自由活动期间注意自身财务及周边环境的安全，遇灵猴时，注意安全，以防被猴子抓伤。</span></strong></p><p><span style="FONT-FAMILY: 宋体"></span>&nbsp;</p><p>&nbsp;<img title="200712262312146323.jpg" src="/ueditor/net/upload/2016-07-15/9586627c-de4f-48ae-8a87-f840d9f24a79.jpg"/></p>', 4, 75)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (264, N'成都-都江堰-茂县-松潘-川主寺-九寨沟', 1, 0, 1, N'藏式特色酒店', N'<p style="text-indent:32px;line-height:24px"><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;">早餐后乘车从成都出发,沿途观赏成都平原风光，经过都江堰市、映秀、汶川、茂县、松潘，午餐后</span><span style="font-size: 16px;font-family: 微软雅黑, sans-serif">乘车抵达</span><span style="font-size: 16px;font-family: 微软雅黑, sans-serif">素有人间瑶池美誉的【<strong>黄龙风景区</strong>】景区黄龙沟的数千个钙化彩池形态各异，流光泛彩</span><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;">，长达2500米的钙化硫是世界之最，沿途主要景点有洗身洞、金沙铺地、盆景池、黄龙洞、黄龙寺、石塔镇海、五彩池、转花玉池等，游览时请量力而行<span style="color:red">（<strong>可自费乘索道上行80/人,下行40/人</strong>）</span>之后驱车前往人间仙境、童话世界九寨沟<strong>，</strong>到达后<span style="color:red">晚餐升级一餐藏族土火锅</span>参加<strong>免费赠送的走进藏家家访+锅庄晚会（不参加不退费）</strong>，让您身临其境的感受藏家生活、藏族文化和藏族风情与少数民族朋友举手狂欢锅庄晚会！</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><img src="/ueditor/net/upload/2016-07-11/41e5b226-07f2-46f2-a7aa-55e2537d33e9.jpg" style="float:none;" title="九黄１.jpg"/></p><p><br/></p>', 1, 62)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (265, N'全天游览九寨沟', 1, 0, 1, N'藏式特色酒店', N'<p style="text-indent:32px;line-height:24px"><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;">早餐后乘车前往【<strong>九寨沟风景区</strong>】，换乘观光车<strong>（含九寨观光车）</strong>游览九寨沟世界级自然遗产保护区（游览时间7小时）,九寨沟是由翠海、叠瀑、彩林、烟云、雪峰以及奇异多彩的藏族风情组成格调自然风韵独具的仙境，景观主要分布在<strong>树正沟</strong>、<strong>日则沟</strong>、<strong>则查洼沟</strong>三条主沟内，景区内有最宽、最高、最雄伟壮观的三大瀑布：<strong>珍珠滩瀑布</strong><strong>、诺日朗瀑布、熊猫海瀑布</strong>，108个高山湖泊及数十处流泉飞瀑等景观，最美最奇特的是九寨沟的水，清冽透底、变幻无穷，细细体味“黄山归来不看山，九寨归来不看水”的意境。酒店晚餐后<strong>参加羌藏风情歌舞晚会（不参加不退费）</strong></span></p><p><img src="/ueditor/net/upload/2016-07-11/5969c9f7-4b36-490c-a982-dfd1052c9fa3.jpg" title="九寨.jpg"/></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src="/ueditor/net/upload/2016-07-11/72529699-84f0-4b44-8d11-2a4d2fd48eb8.jpg" title="九寨１.jpg"/></p><p style="text-align:center;line-height:24px"><strong><span style="font-size:20px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;;color:red">温馨提示</span></strong></p><p style="line-height: 24px"><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;;color:red">1</span><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;;color:red">、九寨沟分为三条沟，单程35公里，沟内每个景点都有一个观光车上下的车站，凭观光车票当日可在任一站点自由换乘，每辆观光车都有讲解导游。游览景区内三条沟的先后顺序，完全根据观光车调度人员临时安排分沟游览，这样也能更好的分散游客，让您在拍照时候舒心一些。观光车一般是到每条沟最高景点下车，然后往下走游览，所以走路也不会觉得累。</span></p><p style="line-height: 24px"><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;;color:red">2</span><span style="font-size:16px;font-family:&#39;微软雅黑&#39;,&#39;sans-serif&#39;;color:red">、九寨沟景区最佳的游览方式还是自由活动，<br/>乘坐的观光车运行方式和城里的公交车是一样的，因此导游也无法全程陪同。<br/>3、九寨景区禁止吸烟，有吸烟习惯的客人请忍耐，或是到餐的一角有唯一的抽烟区吸烟，否则会受到高额罚款。九寨沟是世界自然遗产，爱它您就保护它。</span></p><p><br/></p>', 2, 62)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (898, N'成都-理县-红原 ', 0, 1, 1, N'准四酒店', N'<p style="text-indent:28px"><span style=";font-family:宋体"></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"></span></p><p><span style="font-size: 16px;"><strong><span style="font-family: 宋体;"></span></strong></span></p><p style="line-height:21px"><span style="font-family: 宋体, SimSun; font-size: 16px;">早晨广元宾馆集合统一出发经都江堰市，映秀、汶川神奇的东方古堡――桃坪羌寨新区【自由安排游览桃坪羌寨新区】，后前往理县享用特色午餐，经古尔沟，穿全国最大红叶风景区――米亚罗、刷马路口、查真梁子，―月亮湾风景区，在月亮湾游览景区内美丽的草原风光。</span></p><p><span style="font-size: 16px;"><span style="font-family: 宋体;"></span></span><br/></p><p><span style="font-size: 16px; font-family: 宋体;"><br/></span></p><p><span style="font-size: 16px; font-family: 宋体;"><img src="/ueditor/net/upload/2016-08-05/89a0ec08-377c-43f9-bf1c-ab77b9538463.jpg" title="2010040610163516354.jpg"/></span></p><p><span style="font-size: 16px; font-family: 宋体;"><br/></span></p><p><span style="font-size: 16px;"><span style="font-family: 宋体;">――</span><strong><span style="font-family: 宋体; color: rgb(0, 0, 255);">月亮湾风景区（游览时间不少于30分钟）</span></strong><span style="font-family: 宋体;">，在月亮湾一边游揽景区内美丽的草原风光，一边听着导游讲述月亮湾美丽动人的传说，大约晚上6-7点左右到达，晚宿草原。</span></span></p><p><strong style="text-indent: 28px;"><span style="color: rgb(0, 176, 80);"><br/></span></strong></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong style="text-indent: 28px;"><span style="font-size: 16px; color: rgb(0, 176, 80);">行程小提示</span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong style="text-indent: 28px;"><span style="font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 176, 80);"></span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(255, 0, 0);">（出团前一天晚上18：00-21:00小车师傅会通知贵宾第二天早上上门的接客时间，请务必保持电话畅通）</span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><span style="font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 176, 80);">1.红原及川主寺沿线住宿硬件设施有限，请不要以城市标准来衡量，请尊重当地民族风俗习惯；</span></strong><br/></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><span style="font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 176, 80);">2.出发时间比较早(07:00-07:30),导游会在前一天晚18:00-21:00电话及短信通知第二天集合地点和出发时间，请保持通讯畅通，并且准时到达集合地点。如您是住宿酒店，请自行提前通知酒店为您提供路餐作为早餐，早上到前台处自行领取。当地条件较为艰苦，蔬菜较少，且口味不是很好，建议客人自带些干粮或零食；</span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><span style="font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 176, 80);">3.沿线提倡绿色环保，酒店不提供一次性洗漱用品，请游客自备；</span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><span style="font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 176, 80);">4.成都至草原的路程较远，大部分为山路，如您要晕车，请务必携带晕车药；沿途车辆较多，有可能会堵车，导致用餐时间较晚或回成都的时间较晚，敬请理解；</span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><span style="font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 176, 80);">5.当地属于高原地区。如果您要预防高原反应的发生，请在出发前自行准备抗高原药品。</span></strong></span></p><p><strong><span style="color: rgb(0, 176, 80);"><br/></span></strong></p><p><img src="/ueditor/net/upload/2016-08-05/60db6590-d24d-49a2-87d0-a90311404359.jpg" title="f2bc0515-3ce4-49b7-a84f-58ef9603cadf.jpg"/></p>', 1, 58)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (966, N'成都――紫薇花海――竹海洞天――成都 ', 0, 1, 0, N'', N'<p style="MARGIN: 0px 0px 0px 13px"><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; font-size: 16px;">上午：</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">早7：00于营门口白芙蓉宾馆集合出</span><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">发，游览</span></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">新都首届紫薇花节在军屯镇紫薇华年景区（花溪缘）</span></strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #4f6228; FONT-SIZE: 16px">，“</span><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">探</span></strong></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">薇</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">之源、赏</span></strong></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">薇</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">之美、明</span></strong></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">薇</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">之语、享</span></strong></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">薇</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">之趣、品</span></strong></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">薇</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">之味”紫</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">薇小镇的紫薇花海是我国西部地区最大的紫薇花观赏基地，这里有3000亩紫薇花绚丽成海，盛夏时节，百花已谢，而紫薇花海中缺一片欢腾，从国外引进的</span></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">火红箭、火红球、红叶紫</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0);"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">薇与</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">本国的紫薇争奇斗艳，美不胜收。漫步千米紫薇长廊、千米紫薇隧道之中，赏夏花之绚烂，芬芳的花瓣，轻轻飘落在肩头、指尖和裙边，浪漫牵手、亲友小聚、摄影采风，何其幸福温馨。前往</span></span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">六合/聚源丝绸购物中心(二选一,约50分钟)。</span></strong></p><p style="MARGIN: 0px 0px 0px 13px"><span style="font-family: 宋体, SimSun;">中午： 12:00左右</span><strong><span style="font-family: 微软雅黑, sans-serif; color: red;">享用午餐</span></strong><strong><span style="font-family: 微软雅黑, sans-serif; color: red;">。</span></strong></p><p style="MARGIN: 0px 0px 0px 13px"><span style="font-family: 宋体, SimSun;">下午： 后乘车前往参观</span><strong><span style="font-family: 微软雅黑, sans-serif; color: red;">天王表展示中心/玉石展示中心(二选一,约50分钟)</span></strong></p><p style="MARGIN: 0px 0px 0px 13px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #4f6228; FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">之后乘车前往位于都江堰的</span><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: red; FONT-SIZE: 16px">竹海洞天景区</span></strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">,沿游客步道赏满眼竹海美景，乘凉、休息、品茗、 玩水。漫步竹海,感受超高的负氧离子含量,净化身心。也可沿河滩玩水,景区专门为玩水修筑一层一层台阶积水,当地人喜爱在此玩水洗澡游泳,如果你也想这样玩,请自备泳装哦.下午乘车返回成都,结束游程！</span></p><p style="MARGIN: 0px 0px 0px 13px"><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;"><br/></span></p><p style="LINE-HEIGHT: 20px; MARGIN: 0px 0px 0px 36px"><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; BACKGROUND: yellow; COLOR: black; FONT-SIZE: 16px">备注：导游可能会根据当日情况，为保证客人的正常游览和体验。调整上午和下午的游览顺序，敬请理解。但是行程所涉及的点都会走到。</span></strong></p><p style="LINE-HEIGHT: 20px; MARGIN: 0px 0px 0px 36px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #4f6228; FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: blue; FONT-SIZE: 16px">具体行程顺序，根据情况来定，此行程仅做参考</span></p><p style="LINE-HEIGHT: 20px; MARGIN: 0px 0px 0px 36px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: blue; FONT-SIZE: 16px"><br/></span></p><p style="LINE-HEIGHT: 20px; MARGIN: 0px 0px 0px 25px"><span style="FONT-FAMILY: 新宋体; COLOR: #4f6228; FONT-SIZE: 12px">&nbsp;&nbsp;</span><strong><span style="FONT-FAMILY: 新宋体; COLOR: #4f6228; FONT-SIZE: 12px">&nbsp;</span><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">在不减少景点并保证每个景点游览时间的提前下旅行社有权对行程的游览顺序做调整！</span></strong></p><p style="LINE-HEIGHT: 20px; MARGIN: 0px 0px 0px 26px"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 0, 0); font-size: 16px;">&nbsp;&nbsp;特别安全提示：游客在游玩时，一定注意自身安全，切务进入无工作人员看护的河道游玩！！！</span></strong></p><p><img title="1.png" src="/ueditor/net/upload/2016-08-09/9f81f034-6898-49d6-bdee-cec87b9b1fa6.png"/></p>', 1, 91)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (899, N'红原-花湖-若尔盖-川主寺 ', 1, 0, 1, N'准四酒店', N'<p><span style=";font-family:宋体"></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"></span></p><p><span style="font-size: 16px; font-family: 宋体;"></span></p><p style="line-height:21px"><span style="font-family: 宋体, SimSun; font-size: 16px;"><span style="font-family: 宋体, SimSun;">早餐后经热尔大坝到达美丽的――</span><span style="font-family: 宋体, SimSun; color: rgb(237, 125, 49);">花湖风景区</span><strong><span style="font-family: 宋体, SimSun;">（行程已含门票</span></strong><strong><span style="font-family: 宋体, SimSun;">）</span></strong><span style="font-family: 宋体, SimSun;">，</span><span style="font-family: 宋体, SimSun;">感受明镜般的湖水影照下的蓝天白云。下午乘车前往川主寺入住酒店；抵达川主寺参加</span><span style="font-family: 宋体, SimSun; color: rgb(237, 125, 49);">藏民家访</span><span style="font-family: 宋体, SimSun;">，费用已含不退不换，体会嘉绒藏族小伙儿和姑娘的热情好客。后可欣赏</span><span style="font-family: 宋体, SimSun; color: rgb(237, 125, 49);">藏羌风情晚会</span><span style="font-family: 宋体, SimSun;">，费用已含不退不换，充分展现出松潘县藏羌民族风情景区内均为自行游览（午餐自理），游客请在导游规定的时间内集合以免错过返回酒店的车。</span></span></p><p><span style="font-size: 16px;"><strong><span style="font-family: 宋体; color: rgb(0, 0, 255);"></span></strong></span><br/></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src="/ueditor/net/upload/2016-07-11/7a487495-3582-4d97-9e33-5c251c8a0937.jpg" title="草原１.jpg" style="line-height: 24px; text-indent: 32px;"/><br/><span style="font-family: 宋体;font-size: 12px"></span></p><p style="text-indent:28px"><br/></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; color: rgb(0, 176, 80);">行程小提示</span></strong></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 176, 80);">1.参观花湖景区时，请尽量乘坐景区观光车，单边到达花湖湖边的距离5公里，花湖观光车不会中途停车上客，所以可自行选择坐进去走出来或进去出来都坐观光车。这里也有马可以骑，价格请自行和马夫协商，骑马时一定注意安全，请一定注意，保险公司不保骑马项目； &nbsp;</span></strong></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 176, 80);">2.户外游览时间较长，日照充足，紫外线较强，请客人备好雨伞，防晒霜，太阳镜等物品，请遵守景区内管理制度禁止抽烟，乱丢垃圾，违者最低罚款500元起；</span></strong></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 176, 80);">3.花湖海拔3500米游客切记不要剧烈运动，以免引发严重的高原反应；</span></strong></span></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 176, 80);">4.大草原为游牧部落安多藏族的天堂，大多风俗和汉族不同，草原上听从导游安排，尽量避免和当地人发生口角，尊重他们的风俗习惯，不要和当地人谈政治等敏感问题。如购买东西还了价就必须买，不买就不要还价，切记！</span></strong></span></p><p><strong><span style="color: rgb(0, 176, 80);"></span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 176, 80);">5、此日总行车时间较长（约8小时），易晕车者请提前备晕车药，建议提前自备零食及饮水，花湖风景区海拔3500多米,部分游客可能有轻微高原反应，请自备抗高原反应的药物或氧气。次日游览花湖，不含路餐，午餐客人自理。</span></strong></span></p><p><br/></p>', 2, 58)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (340, N'D4：观音桥镇--马尔康68KM--汶川200KM---成都130KM          共398km              车程约7小时  ', 0, 0, 0, N'', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 28px"><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">早餐后，穿越梭莫峡谷，看着沿途美丽峡谷风光；经马尔康，到达卓克基镇西索村，游览嘉绒藏式民居</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">--</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">西索民居（免费），参观旦达轮寺；还可参观兼具红色文化及藏族土司文化特色</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">--</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">卓克基官寨（自费</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">60</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元）；后经米亚罗，至理县古尔沟。午餐（自费）后，前往理县，途径东方古堡、千年古羌寨</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">--</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">桃坪羌寨（新寨子免费、老寨子自费</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">60</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">/</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">人），再经汶川都江堰返回成都茶店子地铁站，结束震撼的色达朝圣之旅！</span></p><p><img title="mp10578875_1428930579974_21.jpeg" src="/ueditor/net/upload/2016-07-12/6bde68a4-90e7-4b14-b233-2ae62a89c20c.jpeg"/></p>', 4, 66)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (329, N'D1：成都--汶川130KM--马尔康245KM--观音桥镇68KM车程约8小时海拔2560米宿：观音桥镇   ', 0, 0, 0, N'神山脚下酒店', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 28px"><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">早上</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">7:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">成都广元宾馆门口统一集合（具体集合时间地点以导游通知为准）出发，上都汶高速，沿途领略阿坝川西风光，沿岷江支流杂谷脑河谷一路向上，感受藏羌风情</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">,</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">远观羌寨、碉楼、民居，经过理县，到达古尔沟用午餐（自理）后经米亚罗、翻越鹧鸪山，至马尔康，沿途观赏杂谷脑河谷峡谷风光，约</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">16:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">到达观音桥镇，换乘当地交通（往返</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">40</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元自理）经煨桑广场、玛尼墙后到达观音庙，登上海拔</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">3685</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">米之纳勒山（含神山之意）半坡，探访古老的观音庙，登上石阶、转动经筒、细数玛尼墙上的玛尼石来到绰斯甲观音菩萨座前朝拜祈福，了解第二个布达拉宫的由来，欣赏藏区高原日落！，晚餐（自理）后，在神山脚下入眠！</span></p><p>&nbsp;<img title="t01fc46ac0749688408.jpg" src="/ueditor/net/upload/2016-07-12/1f7e1ccc-546f-4abf-bec6-b16caa2f4d98.jpg"/></p>', 1, 67)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (330, N'D2：观音桥镇--翁达123KM--色达五明佛学院66KM --色达20KM     车程约5小时海拔3900住宿：色达', 0, 0, 0, N'年龙宾馆、金域宾馆、普洲客栈（或同级）', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 28px"><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">早上</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">6:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">出发</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">,</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">沿</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">317</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">国道经两河口至翁达镇，沿色曲河北上蓝天白云下，群山绵蜒，山谷秀丽，无垠的草原涌动着肥壮的牛羊，风格各异的庙宇和挂满山崖路口的经幡，渲染着佛音渺渺神奇的藏传佛教文化。约</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">13:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">前往世界最大的色达天顶天葬台（自费中转车往返</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">30</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">/</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">人）了解藏地天葬习俗，上百只秃鹫在上空盘旋，带领逝者往生极乐，场面非常壮观，不由得让你感叹人生，更加珍视自己的生命！看完天葬以后前往喇荣沟――五明佛学院，震撼的佛国画卷渐渐呈现在眼前――漫山遍野的红房子，裹着红袍的僧人在其间穿行，金碧辉煌的佛学院，蓝天白云下气势宏伟的坛城</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">(</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">海拔</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">4000</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">米</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">)</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">；与信徒和僧人交流、转经、朝拜，体验僧人修行方式，在转经筒持续的旋转中，感受信仰的强大、内心的宁静</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">~~ </span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">晚餐（自理）后，可自由选择前往经堂听经</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">/</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">看夜景，在浩瀚星河下的金马草原，安然入梦！</span></p><p>&nbsp;<img title="t01c7c9e18a882a162a.jpg" src="/ueditor/net/upload/2016-07-12/01cb6758-1b62-47be-867c-7044bc5952d1.jpg"/></p>', 2, 67)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (331, N'D3：色达―色尔坝藏寨--观音桥镇 216KM    车程约6 小时     海拔2560米宿：观音桥镇  ', 0, 0, 0, N'年龙宾馆、金域宾馆、普洲客栈（或同级）', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 29px"><strong><span style="FONT-SIZE: 15px; FONT-FAMILY: 宋体">早上乘车再次来到佛学院，自由活动。统一时</span></strong><span style="FONT-SIZE: 15px; FONT-FAMILY: 宋体">间集合后途径翁达，远眺格萨尔</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">藏寨，倾听格萨尔王的大将――上岭八部首领色尔哇・尼奔达雅英雄故事！</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">厚重的格萨尔王文化更为藏寨蒙上一层神秘面纱，是</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">317</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">国道一道亮丽的风景。之后沿着</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">317</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">国道原路返回抵达观音桥，晚餐（自理）后入住酒店。<img title="t011948b9c871b3c0bd.jpg" src="/ueditor/net/upload/2016-07-12/cf484bed-51f0-4c58-8d93-10a375f6fe8c.jpg"/></span></p><p>&nbsp;</p>', 3, 67)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (904, N'溪布街→天门山→德夯苗寨→凤凰古城', 0, 1, 1, N'凤凰古城', N'<table cellspacing="0" cellpadding="0" vspace="0" hspace="0" uetable="null"><tbody><tr><td style="BORDER-BOTTOM: #f0f0f0; BORDER-LEFT: #f0f0f0; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; PADDING-LEFT: 12px; PADDING-RIGHT: 12px; BORDER-TOP: #f0f0f0; BORDER-RIGHT: #f0f0f0; PADDING-TOP: 0px" valign="top"><p style="LINE-HEIGHT: 25px"><span style="LETTER-SPACING: 1px; FONT-FAMILY: 宋体; COLOR: #003366">早餐后游览深山溪畔快活老街---【溪布街】体验湘西大地民族文化和民俗风情，参观张家界大型的【精彩武陵博览园】了解张家界地质地貌的形成过程。后游览土家民族精粹和传统手工艺术展览中心―【印象张家界】该中心以涉及土家传统文化、张家界特色纯天然植物研究等领域</span></p><p><strong><span style="FONT-FAMILY: 宋体; COLOR: #00b0f0">（温馨提示：溪布街享有“酒吧、美食、购物一条街“的美誉。零售当地特色物品，零售店铺不属于旅游行程购物店，客人自愿选择购买，我社不受理任何购物投诉事宜）</span></strong></p></td></tr></tbody></table><p><span style="LETTER-SPACING: 1px; FONT-FAMILY: 宋体; COLOR: #003366">10:00左右游文学大师金庸欣然挥毫的“天门仙山”，也是黄渤、徐峥主演的电影《玩命邂逅》拍摄地之一的――【天门山】(门票已含，游览时间约4小时)，乘坐“世界第一长高山客运索道，索道全长7455米，海拔1279米，体验腾云飞翔的刺激震撼，登张家界之巅，俯瞰张家界全景，观赏奇妙美丽的盆景花园，游览盆景石区，山体孤峰高耸，气势临空独尊，世界最优秀的翼装飞侠杰布・科里斯的惊天挑战也曾在此举行。还有惊险刺激的悬空玻璃栈道，挑战你的高空极限，历史文化积淀深厚的天门山，一直被当地人民奉为神山，圣山，更被誉为“湘西第一神山”和“武陵之魂” 的美誉。（友情提示：如遇大雪或结冰，景区玻璃栈道封路，无法正常游览我社不予承担责任。游客在当地根据个人体质情况可以选择是否乘坐代步工具天门山小索道单程25元/人，天门山扶梯单程32元/人，费用自理）。后BUS前往凤凰（全程高速），途径世界奇观被誉为亚洲第一悬索桥的――矮寨特大悬索桥。赠送游览失落在湘西的一颗璀璨明珠--【德夯苗寨】（门票已含，不去不退，未含德夯风景区内景区环保车120元/人），乘景区观光车游览湘川公路上最险的关卡――【矮寨公路奇观】，在德夯苗家做客赠送一顿丰盛的苗家【苗王特色宴】。抵达凤凰后可自行漫步沱江,燃放许愿灯,或约上三五好友前往沱江边上酒吧畅饮当地胡子酒,品味独特的苗疆风情，一切烦恼都会随风而去… </span></p><p><img style="FLOAT: none" title="QQ图片20160712144944.jpg" src="/ueditor/net/upload/2016-07-12/466cd1fb-49d1-4b2d-8c99-71be5f7558fb.jpg"/>&nbsp;&nbsp;&nbsp; <img style="FLOAT: none" title="QQ图片20160712144927.jpg" src="/ueditor/net/upload/2016-07-12/169857c9-06cc-4fe9-92c7-3e16a51868ca.jpg"/>&nbsp;&nbsp;&nbsp; <img style="FLOAT: none" title="QQ图片20160712144947.jpg" src="/ueditor/net/upload/2016-07-12/bbaafbdc-2890-4271-a703-26ec796a4d0e.jpg"/></p><p><br/></p>', 4, 68)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (318, N'长沙→橘子洲头→韶山→张家界', 0, 1, 1, N'武陵源', N'<p><span style="font-family:新宋体;color:#1E4E79">8</span><span style="font-family:新宋体;color:#1E4E79">：30左右长沙市区统一集合出发(因散拼团客人预约赴长沙大交通及长沙入住酒店标准不一样，导游举黄色“欢乐假期”团旗需按部就班接送，部分旅游者会有短暂等待现象，请客人积极与导游保持联系，务必保持手机通畅，开心旅游）前往长沙橘子洲头。游【橘子洲头】（游览时间约60分钟），吟毛泽东诗词之《橘子洲头 》独立寒江，湘江北去，橘子洲头。。。问苍茫大地，谁主沉浮？感怀毛泽东会当击水三千里的豪情。</span></p><p><span style="font-family:新宋体;color:#1E4E79">&nbsp;&nbsp; </span><span style="font-family:新宋体;color:#1E4E79">中午11：00后乘车赴红太阳升起的地方――韶山。瞻仰一代伟人――【毛主席铜像】聆听主席一生的丰功伟绩；参观【毛泽东同志故居】；“东方红，太阳升，中国出了个毛泽东；毛泽东同志作为一个伟大的历史人物，属于中国，也属于世界”――【韶乐宫】（游览时间约40分钟）。（中餐赠送特色风味餐----毛家宴）</span></p><p><span style="font-family:新宋体;color:#1E4E79">下午14:00乘车赴张家界，途经三湘四水，鱼米之乡，身临“湖广熟、天下足”的佳境。（晚餐赠送特色风味餐----三下锅）</span></p><p><strong><span style="font-size:14px;font-family:宋体;color:#00B0F0">温馨提示：散拼团有一定的特殊性，由于客人交通方式不同，如遇航班、火车晚点，短时间的等待属于正常情况，</span></strong><strong><span style="font-size:14px;font-family:宋体;color:#00B0F0">由于客人原因造成未能赶到正常发班时间的，产生额外费用，客人请自行承担。</span></strong></p><p><strong><span style="font-size:14px;font-family:宋体;color:#00B0F0"><img src="/ueditor/net/upload/2016-07-12/311a3291-f35a-4705-8e08-f38d10ffd00c.jpg" title="QQ图片20160712154918.jpg"/> &nbsp; &nbsp;&nbsp;<img src="/ueditor/net/upload/2016-07-12/884151be-5775-4865-b6f9-612924aff725.jpg" title="QQ图片20160712155012.jpg"/></span></strong></p>', 2, 70)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (901, N'成都→长沙', 0, 0, 0, N'不含', N'<p><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79; FONT-SIZE: 14px">早成都乘火车K504次（11：02-06：06分）前往星城长沙，火车上欣赏沿途风景，自由活动。</span></p>', 1, 68)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (332, N'D4：观音桥镇--马尔康68KM--汶川200KM---成都130KM          共398km              车程约7小时  ', 0, 0, 0, N'', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 28px"><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">早餐后，穿越梭莫峡谷，看着沿途美丽峡谷风光；经马尔康，到达卓克基镇西索村，游览嘉绒藏式民居</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">--</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">西索民居（免费），参观旦达轮寺；还可参观兼具红色文化及藏族土司文化特色</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">--</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">卓克基官寨（自费</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">60</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元）；后经米亚罗，至理县古尔沟。午餐（自费）后，前往理县，途径东方古堡、千年古羌寨</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">--</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">桃坪羌寨（新寨子免费、老寨子自费</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">60</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">/</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">人），再经汶川都江堰返回成都茶店子地铁站，结束震撼的色达朝圣之旅！</span></p><p><img title="mp10578875_1428930579974_21.jpeg" src="/ueditor/net/upload/2016-07-12/6bde68a4-90e7-4b14-b233-2ae62a89c20c.jpeg"/></p>', 4, 67)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (902, N'长沙→橘子洲头→韶山→张家界', 0, 1, 1, N'武陵源', N'<table cellspacing="0" cellpadding="0" vspace="0" hspace="0" uetable="null"><tbody><tr><td style="BORDER-BOTTOM: #f0f0f0; BORDER-LEFT: #f0f0f0; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; PADDING-LEFT: 12px; PADDING-RIGHT: 12px; BORDER-TOP: #f0f0f0; BORDER-RIGHT: #f0f0f0; PADDING-TOP: 0px" valign="top"><p><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">8</span><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">：30左右长沙市区统一集合出发(因散拼团客人预约赴长沙大交通及长沙入住酒店标准不一样，导游举黄色“欢乐假期”团旗需按部就班接送，部分旅游者会有短暂等待现象，请客人积极与导游保持联系，务必保持手机通畅，开心旅游）前往长沙橘子洲头。游【橘子洲头】（游览时间约60分钟），吟毛泽东诗词之《橘子洲头 》独立寒江，湘江北去，橘子洲头。。。问苍茫大地，谁主沉浮？感怀毛泽东会当击水三千里的豪情。</span></p><p><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">&nbsp;&nbsp; </span><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">中午11：00后乘车赴红太阳升起的地方――韶山。瞻仰一代伟人――【毛主席铜像】聆听主席一生的丰功伟绩；参观【毛泽东同志故居】；“东方红，太阳升，中国出了个毛泽东；毛泽东同志作为一个伟大的历史人物，属于中国，也属于世界”――【韶乐宫】（游览时间约40分钟）。（中餐赠送特色风味餐----毛家宴）</span></p><p><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">下午14:00乘车赴张家界，途经三湘四水，鱼米之乡，身临“湖广熟、天下足”的佳境。（晚餐赠送特色风味餐----三下锅）</span></p></td></tr></tbody></table><p><strong><span style="FONT-FAMILY: 宋体; COLOR: #00b0f0; FONT-SIZE: 14px">温馨提示：散拼团有一定的特殊性，由于客人交通方式不同，如遇航班、火车晚点，短时间的等待属于正常情况，由于客人原因造成未能赶到正常发班时间的，产生额外费用，客人请自行承担。</span></strong></p><p><img title="QQ图片20160712144210.gif" src="/ueditor/net/upload/2016-07-12/9072acfe-968f-4caa-acde-865a9c10225c.gif"/></p>', 2, 68)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (905, N'凤凰→长沙→成都', 1, 1, 0, N'火车上', N'<table cellspacing="0" cellpadding="0" vspace="0" hspace="0"><tbody><tr><td style="BORDER-BOTTOM: #f0f0f0; BORDER-LEFT: #f0f0f0; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; PADDING-LEFT: 12px; PADDING-RIGHT: 12px; WORD-BREAK: break-all; BORDER-TOP: #f0f0f0; BORDER-RIGHT: #f0f0f0; PADDING-TOP: 0px" valign="top"><p style="LINE-HEIGHT: 21px"><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">早餐后游览曾被新西兰著名作家路易艾黎称赞为中国最美丽的小城--【凤凰古城】(注：凤凰古城不含九景门票，若因政府政策性变化，产生费用敬请自理，游览时间约3小时），漫步“最湘西”民俗步行街，湘西非物质文化遗风扑面而来，赏传承人作品，品百年老号姜糖，吃苗家血粑鸭，再喝上一口香醇的苗家酒，不亦乐乎！黄永玉的风水巨型雕塑“犟牛座”屹于街心，让您站在这块风水宝地上同时吸纳大师的风范气息。百年历史“凤凰苗族白银锻造工作站”，使您湿身银的海洋，博远的苗银文化之中有您的身影，以“牛角梳”为代表的苗族“文书”文化，更是奇而不俗，意味深长，古老的“湘西茶仓”带给您苗家人的手艺。</span><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79">中餐后凤凰汽车站乘班线大巴（25元自理）前往铜仁火车站乘火车K580次（23：28分发车）返回成都。<img title="QQ图片20160712145228.gif" src="/ueditor/net/upload/2016-07-12/908b2367-7976-495f-b350-426f7da5d356.gif"/></span></p><p style="LINE-HEIGHT: 21px"><span style="FONT-FAMILY: 新宋体; COLOR: #1e4e79"><span style="FONT-FAMILY: 宋体; COLOR: #00b0f0; FONT-SIZE: 14px"><strong>温馨提示：凤凰古城为敞开式民用商业区，特色商品导游义务介绍，旅游者购物行为为自主选择，旅行社不接受、凤凰区域旅游者购物方面的投诉及退换货的要求。建议有购物需求的旅游者提前了解相关信息，尽量选择大型资质齐全的购物商城（或政府授牌非遗传习所）购物。</strong></span></span></p></td></tr></tbody></table><p><br/></p>', 5, 68)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (349, N'北京', 1, 1, 0, N'北京', N'<p style="margin-left: 28px;text-indent: 7px"><strong><span style="font-family:宋体;color:red">☆走进首都，游宏伟故宫，感受中国人的骄傲和自豪</span></strong></p><p><span style="font-size: 15px;font-family: 方正苏新诗柳楷简体">早餐后游览世界上最大的城市中心广场-</span><strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体;color:red">【天安门广场】</span></strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体">，<strong><span style="color:red">【毛主席纪念堂】</span></strong>（政策性关闭除外），<strong><span style="color:red">【人民英雄纪念碑】</span></strong>,观素有“城市中的剧院，剧院中的城市”，漂浮于人造水面上有“湖中明珠”之称的国家大剧院及人民大会堂外景（共2.0小时）。徒步游览<strong><span style="color:red">【老北京胡同】</span></strong>，（若选择人力三轮车胡同游，小费属个人意愿，与我社无关）游什刹海历史文化风景区，后海酒吧一条街，观银锭山”、“柳岸风荷”等享有百年美誉的自然景观。逛北京最古老的街区<strong><span style="color:red">【烟袋斜街】</span></strong>（40分钟）<em>，</em>徘徊于北京独有的青砖古瓦下，漫步于四合院于小桥流水之间，感受八旗子弟和富甲商人的别样生活。300米的斜街承载了600年的历史, 见证了三个朝代的繁华与衰落.......，前往指定地点用中餐(11:00-12:00)，中餐后参观世界文化遗产-<strong><span style="color:red">【故宫博物院】</span></strong>(景区小门票20元自理)（2.0小时），在雄伟威严的的皇家宫殿里，了解我国灿烂的历史和悠久的文化，寻找故事传说中昔日的辉煌与奢</span><span style="font-size: 15px;font-family: 方正苏新诗柳楷简体">华。这里：凝结着历史的烟云,记载着岁月的沧桑。这里：巧夺天工的建筑,灿烂文明的遗迹，足以激起我们每一位华夏儿女的民族自豪感</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">。（参观故宫一是欣赏丰富多彩的建筑艺术；二是观赏陈列于馆内的珍贵文物。珍宝馆更像一座宝库，内存各色宝石，闪闪发光的金银器皿，珍珠翡翠，还有金丝凤冠，象牙玉雕，各类珍宝更是举世无双；其二馆更是故宫参观的精华之所在）<strong>。</strong>逛小吃一条街，王府井步行街，天主大教堂和王府井百货大楼等著名建筑景观（1.0小时）。<strong>（注意：因故宫博物馆管理处相关规定，2016年故宫逢周一全天闭馆(节假日除外)，如我社滚动行程遇周一游览故宫，我社依据情况调整行程，改为其他日期参观故宫，请游客知悉。）</strong></span></p><p><img src="/ueditor/net/upload/2016-07-12/0974b86e-04d3-4180-89e0-1e38ebcd41a3.jpg" title="QQ图片20160712165742.jpg"/></p>', 2, 71)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (352, N' 北京-- 成都 ', 1, 0, 0, N'不含', N'<p style="line-height:27px"><span style="font-size:16px;font-family:方正苏新诗柳楷简体">早餐后，自由活动，后乘机返回成都，结束愉快的旅程！（我社会根据接送站时间调配车辆送站，如</span><span style="font-family: 方正苏新诗柳楷简体;">您不满意送站时间，可自行前往车站。）</span></p><p><img src="/ueditor/net/upload/2016-07-12/8a79eddd-1e51-4e7b-8e59-99c6ee985d1c.jpg" title="QQ图片20160712174612.jpg"/> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src="/ueditor/net/upload/2016-07-12/c31b246d-e0d0-40d5-98ef-02079c9373d3.jpg" title="QQ图片20160712174625.jpg"/></p>', 5, 71)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (319, N'张家界森林公园→黄龙洞→袁家界→天子山 ', 1, 1, 1, N'武陵源', N'<p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; white-space: normal; line-height: 25px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);">早餐后赠送游览享有“世界溶洞全能冠军”之称的【黄龙洞景区】（门票已含，不去不退，未含黄龙洞VIP通道95元/人）洞中灯火阑珊、石笋林立，犹如一株株古木错节盘根、洞中有洞、洞中有河、无奇不有，真乃名副其实的“地下魔宫”，洞中还有投保一亿元的石笋定海神针著名景点。</span></p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; white-space: normal; line-height: 25px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);">下午赴中国第一个国家森林公园―【张家界国家森林公园】（首道大门票已含），从标志门进山游览武陵源自然遗产的最后一块处女地乘亚洲第一梯――百龙天梯上山（自理电梯72元/人）。观【袁家界景区】：游天下第一桥、迷魂台、五女出征、情人谷等景点.观电影《阿凡达》悬浮山取景景观―乾坤一柱。后游览【天子山景区】索道下山（自理天子山索道72元/人）。天子山有“峰林之王”的美称。著名景点：御笔峰、仙女散花、贺龙公园、梦笔生花、天子阁、贺龙铜像、西海峰林、天子点将台、神堂湾等。</span></p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);"><img title="QQ图片20160712144407.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-07-12/db563fce-64d8-4689-9f69-e5f3aea51c30.jpg" style="border: 0px; outline: none; margin: 0px; padding: 0px; float: none;"/>&nbsp;&nbsp;&nbsp;&nbsp;<img title="QQ图片20160712144433.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-07-12/77450354-159c-4600-8540-6458545f928f.jpg" style="border: 0px; outline: none; margin: 0px; padding: 0px; float: none;"/>&nbsp;&nbsp; &nbsp;<img title="QQ图片20160712144436.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-07-12/78b18534-bf3b-460f-9477-b762551e76a8.jpg" style="border: 0px; outline: none; margin: 0px; padding: 0px; float: none;"/></p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);"><strong><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);">晚间活动推荐：武陵源大型民俗风情歌舞晚会-（门票普座228元/人、贵宾座268元/人 超级VIP座398元/人无优免</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: blue;">）</span></strong></p><p><br/></p>', 3, 70)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (320, N'溪布街→天门山→德夯苗寨→凤凰古城', 0, 1, 1, N'凤凰古城', N'<p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; white-space: normal; line-height: 25px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; letter-spacing: 1px; font-family: 宋体; color: rgb(0, 51, 102);">早餐后游览深山溪畔快活老街---【溪布街】体验湘西大地民族文化和民俗风情，参观张家界大型的【精彩武陵博览园】了解张家界地质地貌的形成过程。后游览土家民族精粹和传统手工艺术展览中心―【印象张家界】该中心以涉及土家传统文化、张家界特色纯天然植物研究等领域</span></p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; background-color: rgb(255, 255, 255);"><strong><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体; color: rgb(0, 176, 240);">（温馨提示：溪布街享有“酒吧、美食、购物一条街“的美誉。零售当地特色物品，零售店铺不属于旅游行程购物店，客人自愿选择购买，我社不受理任何购物投诉事宜）</span></strong></p><p><span style="color: rgb(0, 51, 102); font-family: 宋体; font-size: 14px; letter-spacing: 1px; line-height: 28px;  background-color: rgb(255, 255, 255);">10:00左右游文学大师金庸欣然挥毫的“天门仙山”，也是黄渤、徐峥主演的电影《玩命邂逅》拍摄地之一的――【天门山】(门票已含，游览时间约4小时)，乘坐“世界第一长高山客运索道，索道全长7455米，海拔1279米，体验腾云飞翔的刺激震撼，登张家界之巅，俯瞰张家界全景，观赏奇妙美丽的盆景花园，游览盆景石区，山体孤峰高耸，气势临空独尊，世界最优秀的翼装飞侠杰布・科里斯的惊天挑战也曾在此举行。还有惊险刺激的悬空玻璃栈道，挑战你的高空极限，历史文化积淀深厚的天门山，一直被当地人民奉为神山，圣山，更被誉为“湘西第一神山”和“武陵之魂” 的美誉。（友情提示：如遇大雪或结冰，景区玻璃栈道封路，无法正常游览我社不予承担责任。游客在当地根据个人体质情况可以选择是否乘坐代步工具天门山小索道单程25元/人，天门山扶梯单程32元/人，费用自理）。后BUS前往凤凰（全程高速），途径世界奇观被誉为亚洲第一悬索桥的――矮寨特大悬索桥。赠送游览失落在湘西的一颗璀璨明珠--【德夯苗寨】（门票已含，不去不退，未含德夯风景区内景区环保车120元/人），乘景区观光车游览湘川公路上最险的关卡――【矮寨公路奇观】，在德夯苗家做客赠送一顿丰盛的苗家【苗王特色宴】。抵达凤凰后可自行漫步沱江,燃放许愿灯,或约上三五好友前往沱江边上酒吧畅饮当地胡子酒,品味独特的苗疆风情，一切烦恼都会随风而去…</span></p><p><span style="color: rgb(0, 51, 102); font-family: 宋体; font-size: 14px; letter-spacing: 1px; line-height: 28px;  background-color: rgb(255, 255, 255);"><img title="QQ图片20160712144944.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-07-12/466cd1fb-49d1-4b2d-8c99-71be5f7558fb.jpg" style="border: 0px; outline: none; margin: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; float: none; background-color: rgb(255, 255, 255);"/><span style="color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px;  background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;</span><img title="QQ图片20160712144927.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-07-12/169857c9-06cc-4fe9-92c7-3e16a51868ca.jpg" style="border: 0px; outline: none; margin: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; float: none; background-color: rgb(255, 255, 255);"/><span style="color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px;  background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;</span><img title="QQ图片20160712144947.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-07-12/bbaafbdc-2890-4271-a703-26ec796a4d0e.jpg" style="border: 0px; outline: none; margin: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; line-height: 28px; white-space: normal; float: none; background-color: rgb(255, 255, 255);"/></span></p>', 4, 70)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (321, N'凤凰→长沙→成都', 1, 1, 0, N'温馨的家', N'<p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; white-space: normal; line-height: 21px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);">早餐后游览曾被新西兰著名作家路易艾黎称赞为中国最美丽的小城--【凤凰古城】(注：凤凰古城不含九景门票，若因政府政策性变化，产生费用敬请自理，游览时间约3小时），漫步“最湘西”民俗步行街，湘西非物质文化遗风扑面而来，赏传承人作品，品百年老号姜糖，吃苗家血粑鸭，再喝上一口香醇的苗家酒，不亦乐乎！黄永玉的风水巨型雕塑“犟牛座”屹于街心，让您站在这块风水宝地上同时吸纳大师的风范气息。百年历史“凤凰苗族白银锻造工作站”，使您湿身银的海洋，博远的苗银文化之中有您的身影，以“牛角梳”为代表的苗族“文书”文化，更是奇而不俗，意味深长，古老的“湘西茶仓”带给您苗家人的手艺。</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);"><span style="font-size:14px;font-family:新宋体;color:#1E4E79">中餐后车赴长沙送机EU2206次航班（23：05分起飞），</span>返回成都。</span></p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; white-space: normal; line-height: 21px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);"><img src="/ueditor/net/upload/2016-07-12/2d0a74cf-a788-4704-bdfb-a19d75ef8923.jpg" title="QQ图片20160712155545.jpg"/> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src="/ueditor/net/upload/2016-07-12/9b333401-9a38-491d-9e3f-9063a342c2d7.jpg" title="QQ图片20160712155635.jpg"/></span></p><p style="border: 0px; outline: none; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 微软雅黑, tahoma, 宋体, Arial; font-size: 14px; white-space: normal; line-height: 21px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 新宋体; color: rgb(30, 78, 121);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体; color: rgb(0, 176, 240);"><strong>温馨提示：凤凰古城为敞开式民用商业区，特色商品导游义务介绍，旅游者购物行为为自主选择，旅行社不接受、凤凰区域旅游者购物方面的投诉及退换货的要求。建议有购物需求的旅游者提前了解相关信息，尽量选择大型资质齐全的购物商城（或政府授牌非遗传习所）购物。</strong></span></span></p><p><br/></p>', 5, 70)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (351, N'北京', 1, 1, 0, N'北京', N'<p><span style="font-size:15px;font-family:方正苏新诗柳楷简体">早餐后前往我国现存规最大，保存最完整的皇家园林</span><strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体;color:red">【颐和园】</span></strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体">（约2小时），游昆明湖，观万寿山，在山水画卷中感受身心康泰、益寿延年的真谛。</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">免费参观世界非物质文化遗产--风水易经文化展览馆。前往指定餐厅用餐(11:00-12:00)（品尝北京烤鸭餐），中央电视塔景区自由活动照（2.0小时左右）。</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">参观</span><strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体;color:red">【清华大学或北京大学】</span></strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体">（门口拍照留念）</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">（15分钟），赠送亚洲最大的军械展示基地―<strong><span style="color:red">【军事博物馆】</span></strong>（30分钟，政策性维修中，改为车览），了解国防建设新成就，一睹军事强国的风采,观中华世纪坛外景。</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">游览</span><strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体;color:red">【天坛公园】</span></strong><span style="font-size:15px;font-family:方正苏新诗柳楷简体">（首道门票，约1小时）。天坛是明、清两朝皇帝祭天、求雨和祈祷丰年的专用祭坛，是世界上现存规模最大、最完美的古代祭天建筑群</span><span style="font-size:15px;font-family:方正苏新诗柳楷简体">。</span></p><p><span style="font-size:15px;font-family:方正苏新诗柳楷简体"><img src="/ueditor/net/upload/2016-07-12/2090dd22-4cef-4a56-81d0-0578fcae2792.jpg" title="QQ图片20160712174413.jpg"/></span></p><p><br/></p>', 4, 71)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (362, N'成都市――石棉――冷碛镇', 0, 0, 0, N'旅馆（双人标间，带独立卫生间及24小时热水）', N'<p style="BACKGROUND: white; LINE-HEIGHT: 27px"><span style="FONT-SIZE: 14px; FONT-FAMILY: " color:="" background:="">18.00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">集合</span><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">(</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: #333333">具体以领队通知为准</span><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">)</span></p><p style="BACKGROUND: white; LINE-HEIGHT: 27px"><span style="FONT-SIZE: 14px; FONT-FAMILY: " color:="" background:="">18.30</span><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">乘坐中巴或大巴出发</span></p><p style="BACKGROUND: white; LINE-HEIGHT: 20px"><span style="FONT-SIZE: 14px; FONT-FAMILY: " color:="" background:="">23.30</span><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">到达冷碛</span></p><p style="BACKGROUND: white; LINE-HEIGHT: 27px"><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">食：宵夜自行安排（晚上去看看冷碛有没有什么好吃的）</span></p><p style="BACKGROUND: white; LINE-HEIGHT: 27px"><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">宿：旅馆（双人标间，带独立卫生间及</span><span style="FONT-SIZE: 14px; FONT-FAMILY: " color:="" background:="">24</span><span style="FONT-SIZE: 14px; FONT-FAMILY: sans-serif; BACKGROUND: white; COLOR: #333333">小时热水）<img title="带我飞.jpg" src="/ueditor/net/upload/2016-07-12/2712d90d-9d06-4495-be9a-2eea3c589df4.jpg"/></span></p><p></p>', 1, 54)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (703, N'全天游览泸沽湖（含泸沽湖环湖游览）', 1, 1, 0, N'泸沽湖特色客栈', N'<p><span style="FONT-FAMILY: 新宋体">清晨，凉爽的风吹过脸颊，神清气爽！乘坐摩梭人独特的交通工具――猪槽船游湖（配备专业工作人员，划船时间100分钟）费用已含（登船起点景区管理局规范管理的正规码头：四川博树/五支洛码头划船至王妃岛，登王妃岛领略湖中三色（湖上之舟、舟上之人、人之歌声，听泸沽湖动人传说）。游览四川段30分钟（费用已含）观万亩草海，芦苇如墙，水路错综，漫步摩梭情人桥（桥是情的前奏，婚是情的归宿。白鹤见证着泪眼婆娑，野花窥视着秋水意浓。摩梭人在这小桥上留下诗篇，谱写新曲），后游览云南段（费用已含）《内容：摩梭风情园，自然景观 手捧金山银山，云南情人滩，里格观景台等合计时间2-3小时》，</span><strong><span style="font-family: 新宋体;font-size: 14px">了解</span></strong><strong><span style="font-family: 宋体;font-size: 14px">摩梭文化</span></strong><strong><span style="font-family: 宋体;font-size: 14px">(45分钟）</span></strong><strong><span style="font-family: 宋体;font-size: 14px">，</span></strong><strong><span style="font-family: 宋体;font-size: 14px">后</span></strong><strong><span style="font-family: 宋体;font-size: 14px">参观摩梭原始部落银器店</span></strong><strong><span style="font-family: 宋体;font-size: 14px">（</span></strong><strong><span style="font-family: 宋体;font-size: 14px">40分钟</span></strong><strong><span style="font-family: 宋体;font-size: 14px">）</span></strong><strong><span style="font-family: 宋体;font-size: 14px">,</span></strong></p><p><span style="FONT-FAMILY: 新宋体">晚餐后，后参加集体锅庄晚会（30分钟）（注：如遇村上晚会因人数少/或下雨等天气原因不能正常营业，由于是赠送项目，不去费用不退）。</span><br/></p><p></p><p><strong><span style="font-family: 新宋体;color: rgb(255, 0, 0);font-size: 14px">温馨提示</span></strong><strong><span style="font-family: 新宋体;font-size: 14px">： </span></strong><span style=";font-family:新宋体;font-size:14px">下车拍照和乘船时请注意自身及财务以及周边环境安全，乘船时请不要在游船上嬉戏打闹，以免跌入水中。</span><strong><span style="font-family: 新宋体;font-size: 14px">严禁湖边戏水，游泳。</span></strong></p><p><span style="FONT-FAMILY: 新宋体"></span><br/></p><p><strong><span style="FONT-FAMILY: 新宋体">&nbsp;<img title="猪槽船.jpg" src="/ueditor/net/upload/2016-07-15/351942ae-8d19-48e2-8f92-e49479ec08de.jpg"/></span></strong><span style="FONT-FAMILY: 新宋体"></span></p>', 2, 75)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (878, N'全天游览九寨沟风景区', 1, 0, 1, N'藏式特色酒店 ', N'<p style="margin-right:2px"><span style="font-family: 宋体, SimSun; font-size: 16px; background: rgb(204, 255, 204);"></span></p><p style="margin-right:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style="FONT-FAMILY: 宋体">早餐后乘车前往</span><span style=";font-family:微软雅黑;font-size:16px">【</span><strong><span style="font-family: 微软雅黑;font-size: 16px">九寨沟风景区</span></strong><span style=";font-family:微软雅黑;font-size:16px">】</span><span style="FONT-FAMILY: 宋体">，换乘观光车</span><strong><span style="font-family: 微软雅黑;font-size: 16px">（含九寨观光车）</span></strong><span style=";font-family:微软雅黑;font-size:16px">、</span><span style="font-family: 宋体;">游览九寨沟世界级自然遗产保护区（游览时间7小时）,九寨沟是由翠海、叠瀑、彩林、烟云、雪峰以及奇异多彩的藏族风情组成格调自然风韵独具的仙境，景观主要分布在</span><strong><span style="font-family: 微软雅黑;">树正沟</span></strong><span style="font-family: 微软雅黑;">、</span><strong><span style="font-family: 微软雅黑;">日则沟</span></strong><span style="font-family: 微软雅黑;">、</span><strong><span style="font-family: 微软雅黑;">则查洼沟</span></strong><span style="font-family: 宋体;">三条主沟内，景区内有最宽、最高、最雄伟壮观的三大瀑布：</span><strong><span style="font-family: 微软雅黑;">珍珠滩瀑布</span></strong><strong><span style="font-family: 微软雅黑;">、</span></strong><strong><span style="font-family: 微软雅黑;">诺日朗瀑布</span></strong><strong><span style="font-family: 微软雅黑;">、熊猫海瀑布</span></strong><span style="font-family: 宋体;">，108个高山湖泊及数十处流泉飞瀑等景观，最美最奇特的是九寨沟的水，清冽透底、变幻无穷，细细体味“黄山归来不看山，九寨归来不看水”的意境。酒店晚餐后</span><strong><span style="font-family: 微软雅黑;">参加羌藏风情歌舞晚会（不参加不退费）</span></strong></p><p style="margin-right:2px"><span style="FONT-FAMILY: &#39;Arial&#39;,&#39;sans-serif&#39;"></span><br/></p><p style="LINE-HEIGHT: 115%; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #ff6600">九寨沟介绍：</span><span style="FONT-FAMILY: 宋体">九寨集翠海、叠溪、彩林、雪峰和藏族风情于一体，以其自成天成、美丽绝伦的自然风光而成为中国著名的风景名胜区。九寨沟的蓝天、白云、雪山、森林、尽融于瀑、河、滩、缀成一串串宛若从天而降的珍珠；九寨沟分为则查洼沟、日则沟、树正沟）三条沟，呈“丫”字分布；整个构成了一个五彩斑谰、绚丽奇绝的瑶池玉盆，一个原始古朴、神奇梦幻的人间仙境，一个不见纤尘、自然纯净的“童话世界”。</span></p><p style="LINE-HEIGHT: 115%; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: &#39;Arial&#39;,&#39;sans-serif&#39;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="font-family: 宋体, SimSun;"></span><span style="font-family: 宋体, SimSun; font-size: 18px;"><strong><span style="font-family: 宋体, SimSun; font-size: 24px; color: rgb(255, 0, 0);">温馨提示</span></strong></span></span></p><p style="margin-right: 0;margin-left: 0;text-indent: 0;line-height: 21px"><span style="font-family: 宋体, SimSun; color: rgb(255, 0, 0); font-size: 16px;">1、九寨沟分为三条沟，单程35公里，沟内每个景点都有一个观光车上下的车站，凭观光车票当日可在任一站点自由换乘，每辆观光车都有讲解导游。游览景区内三条沟的先后顺序，完全根据观光车调度人员临时安排分沟游览，这样也能更好的分散游客，让您在拍照时候舒心一些。观光车一般是到每条沟最高景点下车，然后往下走游览，所以走路也不会觉得累。</span></p><p style="margin-right: 0;margin-left: 0;text-indent: 0;line-height: 21px"><span style="font-family: 宋体, SimSun; color: rgb(255, 0, 0); font-size: 16px;">2、九寨沟景区最佳的游览方式还是自由活动，<br/>乘坐的观光车运行方式和城里的公交车是一样的，因此导游也无法全程陪同。<br/>3、九寨景区禁止吸烟，有吸烟习惯的客人请忍耐，或是到餐的一角有唯一的抽烟区吸烟，否则会受到高额罚款。九寨沟是世界自然遗产，爱它您就保护它。</span></p><p style="LINE-HEIGHT: 115%; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: &#39;Arial&#39;,&#39;sans-serif&#39;"></span><br/></p><p style="LINE-HEIGHT: 115%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: Tahoma">&nbsp;</span></p><p style="LINE-HEIGHT: 16px; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"></span></p><p><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"><img title="20140521114806_hkXQN.jpeg" src="/ueditor/net/upload/2016-08-04/1d42db13-993b-4eba-b8c5-bc05460692cd.jpeg"/></span></p><p><br/></p><p style="LINE-HEIGHT: 16px; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"><br/></span></p><p style="LINE-HEIGHT: 16px; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: 微软雅黑; COLOR: rgb(255,102,0); FONT-SIZE: 16px">藏羌风情歌舞晚会介绍：</span><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px">九寨沟的藏羌民俗表演由来已久，表演内容包括，藏族歌舞、藏族传统活动表演、藏羌传统服饰展示等，演员还会与游客有非常热烈、欢快的互动，可以说去九寨沟旅游不去此地领略一翻藏、羌文化神秘魅力，那你的九寨沟之行是绝对不完整的。</span></p><p style="LINE-HEIGHT: 16px; MARGIN-RIGHT: 10px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"><br/></span></p><p><img title="16881802.jpg" src="/ueditor/net/upload/2016-08-04/975b5013-564f-4960-a689-1480cf5ec956.jpg"/></p>', 2, 90)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (879, N'九寨沟沟口-特色藏寨-川主寺-松潘-茂县-成都 ', 1, 1, 0, N'不含', N'<p style="LINE-HEIGHT: 16px"><span style="FONT-FAMILY: 腾祥嘉丽中黑简; BACKGROUND: rgb(204,255,204); FONT-SIZE: 16px"></span></p><p style="margin-right:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px">早餐后前往川主寺途中游览</span><strong><span style="font-family: 微软雅黑;font-size: 16px">特色藏寨</span></strong><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px">，观藏族建筑，体验藏羌文化，随后参观土特产销售中心，中心主要销售（当地药材、牛肉、水晶工艺品等）游览藏羌文化馆参观</span><strong><span style="font-family: 微软雅黑;font-size: 16px">丝绸工坊</span></strong><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px">，了解古丝绸之路的丝绸文化和历史文化，午餐后乘车前往松潘参观</span><strong><span style="font-family: 微软雅黑;font-size: 16px">印象南红博物馆</span></strong><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px">，该馆有商品出售，如需购买请认真甄选。而后乘车返回成都结束愉快的旅程！</span></p><p style="LINE-HEIGHT: 16px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"></span><br/></p><p style="MARGIN-RIGHT: 2px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"><br/></span></p><p style="MARGIN-RIGHT: 2px"><span style="FONT-FAMILY: 微软雅黑; COLOR: rgb(255,102,0); FONT-SIZE: 16px">藏族村寨：</span><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px">整个村寨民风淳朴，风景宜人，建筑规划无不体现藏族人精湛的技艺，进村入户路、户间路、环寨路全部硬化，特有的锅庄、民歌、服饰、习俗以及屯兵文化、宗教文化、农耕文化、石头文化正以独特的魅力吸引着游客前来观赏游玩、休闲度假。走进藏寨，走进藏民的家中做客，在当地居民的介绍中感受他们的生活方式和风土人情。此景点有当地寨子中的特产售卖，请根据自身需要合理选择。</span></p><p style="MARGIN-RIGHT: 2px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"><br/></span></p><p style="MARGIN-RIGHT: 2px"><span style="FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px"><img title="t01c8bae45c02941102.jpg" src="/ueditor/net/upload/2016-08-04/8279c414-3c57-473c-a679-9ea2a64b0f8f.jpg"/></span></p><p style="MARGIN-RIGHT: 2px"><br/></p>', 3, 90)
