INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (542, N'成都―毕棚沟218KM―14KM古尔沟/朴头/丘地/理县', 0, 1, 1, N'2-3人间', N'<p style="TEXT-ALIGN: left"><span style="FONT-FAMILY: 宋体">早上</span><span style="FONT-FAMILY: Times New Roman">7:30-8</span><span style="FONT-FAMILY: 宋体">点</span><span style="FONT-FAMILY: Times New Roman">[</span><span style="FONT-FAMILY: 宋体">导游通知为准</span><span style="FONT-FAMILY: Times New Roman">]</span><span style="FONT-FAMILY: 宋体">在指定地点统一集合从成都出发，沿岷江风光而上，沿途车览</span><span style="FONT-FAMILY: 宋体">都江堰、紫坪埔</span><span style="FONT-FAMILY: 宋体">水利工程，到达被誉为“四季山水、山水世界”的旅游胜地―</span><strong><span style="FONT-FAMILY: 宋体; COLOR: red">毕棚沟风景区</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: fuchsia">（游览时间不低于3个小时，观光车必须乘坐，50元/人，费用已含；另游客可根据自身情况自愿自理乘坐电瓶车游览,20元/人/单程）</span></strong><span style="FONT-FAMILY: 宋体">，毕棚沟以其优美的自然风光、完美的自然生态景观、优良的生态环境著称。景区内红叶、杜鹃花种类繁多，森林原始、瀑布飞挂。毕棚沟被是个五彩斑斓的世界，浓绿的树，深红的叶，青青的湖，湛蓝的天，纯白的雪；这是个多姿多彩世界，险峻奇峰，一道冰川，人间寺庙。奇峰、异石：女皇峰、才女十二峰、<a href="http://baike.baidu.com/subview/52324/10414071.htm" target="http://baike.baidu.com/_blank"><span style="COLOR: black; text-underline: none">杜鹃山</span></a>、燕子岩窝、熊猫岭、神鹿峰、雄鹰岩、狮子守皇峰、野牛岭、磐羊峰、通天门、天狗望月、玉兔问天、镇山将军、下凡&nbsp;仙龟、仰面长空、仰天长啸、鲲鹏待月、包公岩（脸普岩），沿途风景让你流连忘返。</span></p><p>&nbsp;<img title="bipg.jpg" src="/ueditor/net/upload/2016-07-14/a4605a64-ae31-4a85-af1f-5af944531e91.jpg"/></p>', 1, 79)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (338, N'D2：观音桥镇--翁达123KM--色达五明佛学院66KM --色达20KM     车程约5小时海拔3900住宿：色达', 0, 0, 0, N'年龙宾馆、金域宾馆、普洲客栈（或同级）', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 28px"><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">早上</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">6:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">出发</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">,</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">沿</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">317</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">国道经两河口至翁达镇，沿色曲河北上蓝天白云下，群山绵蜒，山谷秀丽，无垠的草原涌动着肥壮的牛羊，风格各异的庙宇和挂满山崖路口的经幡，渲染着佛音渺渺神奇的藏传佛教文化。约</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">13:00</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">前往世界最大的色达天顶天葬台（自费中转车往返</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">30</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">元</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">/</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">人）了解藏地天葬习俗，上百只秃鹫在上空盘旋，带领逝者往生极乐，场面非常壮观，不由得让你感叹人生，更加珍视自己的生命！看完天葬以后前往喇荣沟――五明佛学院，震撼的佛国画卷渐渐呈现在眼前――漫山遍野的红房子，裹着红袍的僧人在其间穿行，金碧辉煌的佛学院，蓝天白云下气势宏伟的坛城</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">(</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">海拔</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">4000</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">米</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">)</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">；与信徒和僧人交流、转经、朝拜，体验僧人修行方式，在转经筒持续的旋转中，感受信仰的强大、内心的宁静</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">~~ </span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">晚餐（自理）后，可自由选择前往经堂听经</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">/</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">看夜景，在浩瀚星河下的金马草原，安然入梦！</span></p><p>&nbsp;<img title="t01c7c9e18a882a162a.jpg" src="/ueditor/net/upload/2016-07-12/ac3f0832-f75f-4da8-ab0b-ccff3aff76a0.jpg"/></p>', 2, 66)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (339, N'D3：色达―色尔坝藏寨--观音桥镇 216KM    车程约6 小时     海拔2560米宿：观音桥镇  ', 0, 0, 0, N'年龙宾馆、金域宾馆、普洲客栈（或同级）', N'<p style="VERTICAL-ALIGN: auto; TEXT-ALIGN: justify; TEXT-JUSTIFY: inter-ideograph; LINE-HEIGHT: normal; TEXT-INDENT: 29px"><strong><span style="FONT-SIZE: 15px; FONT-FAMILY: 宋体">早上乘车再次来到佛学院，自由活动。统一时</span></strong><span style="FONT-SIZE: 15px; FONT-FAMILY: 宋体">间集合后途径翁达，远眺格萨尔</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">藏寨，倾听格萨尔王的大将――上岭八部首领色尔哇・尼奔达雅英雄故事！</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">厚重的格萨尔王文化更为藏寨蒙上一层神秘面纱，是</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">317</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">国道一道亮丽的风景。之后沿着</span><span style="FONT-SIZE: 14px; FONT-FAMILY: ">317</span><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体">国道原路返回抵达观音桥，晚餐（自理）后入住酒店。</span></p><p>&nbsp;<img title="t01fc46ac0749688408.jpg" src="/ueditor/net/upload/2016-07-12/4870781c-e3a0-428d-9d59-88c76ce48c46.jpg"/></p>', 3, 66)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (644, N'参考航班：成都AIR香港KA825      08:15-11:00', 0, 0, 0, N'', N'<p><span style="FONT-SIZE: 10pt; FONT-FAMILY: 宋体; mso-bidi-font-family: " times="" new="" mso-font-kerning:="" mso-ascii-font-family:="" mso-hansi-font-family:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="">于成都双流机场集合，由专业领队带领换登机牌后，乘坐豪华客机前往香港，后乘坐豪华客机前往日本大阪关西机场，</span><span style="FONT-SIZE: 9pt; FONT-FAMILY: 宋体; mso-bidi-font-family: " times="" new="" mso-font-kerning:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="" mso-bidi-font-size:="">导游</span><span style="FONT-SIZE: 10pt; FONT-FAMILY: 宋体; mso-bidi-font-family: " times="" new="" mso-font-kerning:="" mso-ascii-font-family:="" mso-hansi-font-family:="" mso-ansi-language:="" mso-fareast-language:="" mso-bidi-language:="" mso-bidi-font-size:="">接机后送往酒店休息，为明日的行程储备充足的体力。<img title="f7eff2cc-7e63-47bd-a291-26f5e7d5bf2d.jpg" src="/ueditor/net/upload/2016-07-16/745827b2-bad3-420d-af63-eaae14b7a011.jpg"/></span></p>', 1, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (647, N'古都镰仓的标志：鹤冈八幡宫（约40分钟）～有百年历史的电车：江之岛电车（镰仓站――镰仓高校前站）～镰仓高校前站+镰仓高等学校+湘南海滩（约60分钟）～秋叶原TOKIS（约45分钟）～与美国一模一样的自由女神像：台场自由女神像～日本五大民营电视台之一：东京富士电视台～有高达做保镖的商场：最新落成DIVER CITY 商场(近观1：1极富真实感元祖「高达」)(约60分钟)～可以试乘试驾体验的：丰田汽车展示馆～酒店', 1, 1, 1, N'东京或千叶或横滨或静冈或周边酒店', N'<p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">东京周边著名的度假胜地――镰仓，镰仓位于神奈川县，旅游胜地。</span><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:="">12</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">世纪末源赖朝创建幕府并开始武士政权的地方。以后成为中世纪初期的政治中心，除了幕府的建筑和武士们的宅邸外，还建有不少神社和寺院，曾繁荣一时。</span><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:="">14</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">世纪，随着幕府灭亡，镰仓便衰落了。江户时代作为游览地又得到了复兴，它是仅次于京都、奈良的一座古都。</span></p><p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">特别安排：全日本最美的铁路线――</span><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【江之岛电铁】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">：江ノu的线路贴近湘南海，由藤泽市经过江之岛再到镰仓，途径</span><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:="">15</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">各站。沿途有</span><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:="">4</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">站与海岸线平行，跟相模湾仅仅一马路之隔。之后江ノu会驶入民居小巷中。坐在车厢中，欣赏着窗外各色的房子，骑自行车的妇女，砰砰跳跳的学生，其乐无穷。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【镰仓高校前站+镰仓高等学校+湘南海滩】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">：江之电沿途停靠的“镰仓高校前”车站，出了车站不远处靠近海边的路口，就是《灌篮高手》里樱木花道与晴子打招呼这一经典场景的取景地。樱木花道与晴子第一次打招呼“命运的路口”，陵南高中的原型“镰仓高等学校“，以及流川枫常常飞驰而过的”湘南海岸“，生动勾勒出青春回忆中那些熟悉的场景。面对蔚蓝的湘南海岸，关于灌篮高手的热血青春记忆扑面而来，当江之电从波光粼粼的大海前缓缓驶过，这画面足以令人回味感动。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【镰仓鹤冈八幡宫】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">位于镰仓由比乡的鹤冈八幡宫是日本古都镰仓的标志之一，最早是镰仓幕府的武将在</span><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:="">900</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">多年前建造的神社，后成为包括</span><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:=""><a href="http://blog.sina.com.cn/lm/z/sakura/" target="http://blog.sina.com.cn/s/_blank"><span style="FONT-FAMILY: 宋体; COLOR: black; text-underline: none">东京</span></a></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">在内的关东地区的总守护神，也是保护国家的守护神，至今仍受到众人的敬奉。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【台场自由女神像】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">位于东京台场海滨公园内的自由女神像是世界上第三座“自由女神像”，总高</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">15</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">米。东京的自由女神不仅形象和美国的那座一模一样（尺寸要小得多），连周围的景致也很相像――如果不是因为有彩虹桥这座标志性建筑作背景，神像身后的港湾简直跟哈德逊河没什么两样；东边不远处的两幢高楼，更是犹如双子塔再生，一时真有“错把东京当纽约”之感。如今，以彩虹大桥为背景，与女神一起合影留念是来到东京不可缺少的活动项目了。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【东京富士电视台】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">富士电视台是日本五大民营电视台之一，以常年提供高质量的电视剧、长篇动画和娱乐节目而闻名。</span></p><p style="LINE-HEIGHT: 16px"><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【台场1:1元祖「高达」】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">万众期待，</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">09</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">年曾在台场轰动一时，</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">18</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">米高的</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">1:1</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">元祖高达</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">RX78</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">终于再度现身！这次高达重新回归台场，并且每日定时五次昂首转头及喷射烟雾助兴。最令人兴奋的是，元祖高达今次不是孤军作战，更带来全球首个高达主题馆「东京高达前线」，引领粉丝进入高达世界！</span></p><p style="LINE-HEIGHT: 16px"><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【台场最新开幕Diver City Tokyo Plaza商场】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">看过</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">1:1</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">高达后，别忘了还要到刚刚在</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">13</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">年</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">4</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">月日开业的台场最新商场</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">Diver City Tokyo Plaza</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">逛逛。商城楼高</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">7</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">层，店铺达</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">150</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">多家，</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">由国际名牌</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">UGG</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">、</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">BURBERRY</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">，到大众品牌</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">UNIQLO</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">，</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">FOREVER21</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">一应俱全，是东京最新的品牌总汇，绝对能满足您的购物欲！</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【丰田汽车大型展示馆】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">（</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">MEGA WEB</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">）是世界上最大的汽车陈列馆之一，它的口号是：“观赏、驾驶、感受”。这个</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">2.4</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">万平方米的陈列室里陈列着大约</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">150</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">部汽车，包括</span><span style="FONT-SIZE: 12px; FONT-FAMILY: ">70</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">种不同的型号。人们还可以看到最新的混合动力车，对于孩子们来说，这里有“儿童混合车试驾室”，可以让孩子们很容易地就理解到什么是混合汽车。同样受欢迎的还有“电动通勤车”。参观者可以享受驾驶这种下一代汽车的乐趣。<img title="QQ图片20160712161824.png" src="/ueditor/net/upload/2016-07-12/dd6fb381-ef33-478c-b00b-4f8ce9a164f5.png"/></span></p><p>&nbsp;</p>', 4, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (649, N'自由活动一天（不含车、餐、导游、领队）---可自行前往迪士尼乐园哦！！', 0, 0, 0, N'成田机场或千叶或周边', N'<p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">建议您搭乘东京都内最便利的交通工具～JR山手线，来尝试自由行的乐趣。您可来一次时尚流行文化之旅，走访东京的潮流发源地，购物美食一网打尽！或者来一次主题乐园之旅，前往东京迪斯尼乐园享受童趣浪漫！建议行程：</span></strong></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【表参道】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: #0000cc">：</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">由原宿至青山大道间的『表参道』是一条林荫大道、以日本的「巴黎」而着称，沿街的林荫道、开放式的时髦咖啡店和餐厅、高级时装店，优雅而富有风情。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【涉谷】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: #0000cc">：</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">这里的特色是每条街、每个地段各有自己的特点。面向年轻人的时尚服饰店、快餐食品店和游艺中心等鳞次栉比的中心大道，作为年轻人时尚的发源地颇引人瞩目。无论白天还是夜晚，这里总是聚集着大多为十几岁的年轻人，热闹的场面能让人深切地体会到现代日本的气息。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【东京迪尼斯乐园】（门票自理）</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: #0000cc">：</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">被誉为亚洲第一游乐园。主要分为世界市集、<a href="http://baike.baidu.com/view/716101.htm" target="_blank"><span style="COLOR: windowtext; text-underline: none">探险</span></a>乐园、西部乐园、新<a href="http://baike.baidu.com/view/4579.htm" target="_blank"><span style="COLOR: windowtext; text-underline: none">生物</span></a>区、梦幻乐园、卡通城及未来乐园等7个区，园内的舞台以及广场上定时会有丰富多彩的化装表演和富趣味性的游行活动。</span></p><p>&nbsp;<img title="QQ图片20160712161722.png" src="/ueditor/net/upload/2016-07-12/e68d9d0a-dcbb-4fda-97f3-2d9993d99d05.png"/></p>', 6, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (645, N'日本历史上的三大名城之一：大阪城公园 (不含天守阁 约45分钟)～大阪最热闹的购物中心：心斋桥（约45分钟）～动漫迷的天堂：日本桥动漫一条街（约60分钟）～京都～《艺妓回忆录》拍摄地：祗园艺伎街 (约40分钟)～日本全国约三千座八坂神社之总本社：京都八坂神社（约20分钟）～世界遗产：金阁寺（约45分钟）~赠送：茶道体验(约30分) ～酒店', 1, 1, 0, N'名古屋中部或滨松或滨名湖或丰桥或周边', N'<p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【大阪城公园】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">(</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">不登天守阁)：</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">由丰臣秀吉于公元</span><span style="FONT-SIZE: 12px"><span style="FONT-FAMILY: Times New Roman">1583</span></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">年在石山本愿寺遗址上初建，至今已有</span><span style="FONT-SIZE: 12px"><span style="FONT-FAMILY: Times New Roman">400</span></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">多年历史，为当时日本第一名城，也是日本前所未有的最大城堡。如今大阪城已被定为特别史迹。</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">在大阪要说赏樱名所,首当其冲要数大阪城公园了。这里遍植樱树，其中许多树径已逾50公分，树龄至少在百年以上。据说大约有四千棵樱树围绕着大阪城，一齐开放的情景肯定是一幅美丽的风景画。这里是樱花的海洋，一片片、一排排的樱花树，所有的枝头绽放着粉白色的樱花，繁花竞放，灿若卿云。&nbsp;在清澈的护城河水、灰白色的古老城墙衬托下，有说不清的清丽脱俗。</span></p><p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">而后游览<strong><span style="COLOR: red">【心斋桥】</span></strong>，心斋桥作大阪最大的购物区,集中了许多精品屋和专卖店,从早到晚熙熙攘攘, 到处是市民和游客的人流。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【日本桥动漫一条街】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">东京有秋叶原，大阪有日本桥，是日本动漫文化圣地。在这里动漫、游戏、女仆、</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">COSPLAY</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">、时尚美食无处不在；还有各种化妆品店，了解日式化妆的精华所在，和各种美容知识；深受当地人喜爱。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【祗园艺伎街】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">：</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">电影艺伎回忆录中出现的场景─【祗园花见小路】，是祗园街正中南北走向的一条保持了京都历史古老风貌的著名街道。有机会的时候，间或可见外出献艺的艺伎的挑帘进出、小步急行的窈窕身影，增添古都风情。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【八坂神社】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">位于祗园町北侧的八坂神社，从东大路通上阶梯拾级而上的香火鼎盛，气势非凡的八坂神社是关西地区最知名且历史最悠久的神社之一，京都人昵称它为“祗园桑”。八坂神社与圆山公园相同，是京都商人们的信仰。925年建造的神社大殿被称为“祗园造”，是日本独特的神社建筑，而舞殿上无数的提灯，使夜晚很是美丽。这里春天的樱花，秋天的红叶更是美不胜收。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【金阁寺】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">（日语：きんかくじ）（英语：Golden Pavilion），正式名称其实是<a href="http://baike.baidu.com/view/931375.htm"><span style="COLOR: windowtext; text-underline: none">鹿苑寺</span></a>（日语：ろくおんじ），因为建筑物外面包有金箔，故又名金阁寺。是一座位于<a href="http://baike.baidu.com/view/25791.htm"><span style="COLOR: windowtext; text-underline: none">京都</span></a>、最早完成于1397年（<a href="http://baike.baidu.com/view/1851017.htm"><span style="COLOR: windowtext; text-underline: none">应永</span></a>四年）的<a href="http://baike.baidu.com/view/1554.htm"><span style="COLOR: windowtext; text-underline: none">日本</span></a><a href="http://baike.baidu.com/view/361620.htm"><span style="COLOR: windowtext; text-underline: none">古刹</span></a>，除了是知名的观光旅游景点之外，也是被日本政府指定为国宝，并于1994年被联合国教科文组织指定为<a href="http://baike.baidu.com/view/14389.htm"><span style="COLOR: windowtext; text-underline: none">世界文化遗产</span></a>的重要历史建筑。金阁寺是世界文化遗产，正式名为鹿苑寺。</span></p><p style="LINE-HEIGHT: 16px"><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">京都茶道体验（品尝绿茶+小点心）：</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; BACKGROUND: white">日本茶道起源于中国，兴盛于唐宋，衰于清朝。茶道程序复杂、礼法繁多，讲究精心禅境，增进友谊。日本人非常重视传统文化，其中茶道与书道、花道和香道为日本社会文化的瑰宝。到日本旅游，特别是到京都观光的话，欣赏一回日本的茶道表演，是感受日本传统文化的绝佳机会，也是一次高雅的超值体验。</span></p><p><span style="FONT-SIZE: 12px; FONT-FAMILY: " color:="" background:="">PS</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: blue">：茶道体验为赠送项目，如不能预约，则放弃此项，费用不退</span></p><p>&nbsp;<img title="395e58c2-a5eb-41bd-b760-719ccf7b204c.jpg" src="/ueditor/net/upload/2016-07-16/6b0b9157-e166-4a49-934a-7461b534edae.jpg"/></p>', 2, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (646, N'一年四季欣赏不同的鲜花：山中湖花之都（6月1日至6月20日和7月16日至8月31日限定）或紫色的浪漫河口湖薰衣草庆典（6月22日至7月16日限定）（约60分钟）～给人最大刺激和快感的乐园：富士急乐园（约2小时，园内午餐，赠入园券）～日本新世界文化遗产：富士山五合目（约30分钟）～富士山地震博物馆（约40分钟）', 1, 1, 1, N'箱根或伊豆或周边温泉酒店', N'<p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【河口湖-八木崎公园-熏衣草祭】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">（6月1日至6月20日和6月22日到7月16日限定）在初夏的富士河口湖，每年都会举行“河口湖香草节”，这是富士河口湖的惯例活动。被染成紫色的湖畔与雄伟的富士山相映成趣，是只有在这里才能观赏得到的美景。10万多株的紫色熏衣草在这里盛开，会场里除了举办各种活动之外，还可以找到各式各样的香草、熏衣草产品，熏衣草冰淇淋更是值得一尝。这里一年四季盛开着五彩缤纷的各样鲜花，形成一片彩色的海洋，欣赏10万株薰衣草同时盛开的美景，被染成紫色的湖畔，搭配后面壮观的富士山随手一拍，每一个画面都像是一张明信片。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【山中湖花之都】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">（7月17日到8月31日限定）山中湖是富士山一侧的淡水湖，为富士五湖中最大的湖泊，也是海拔最高的一湖。这里以避暑胜地而闻名，小木屋及别墅鳞次栉比。山中湖花之都公园就位于海拔1000米的距富士山最近的山中湖畔，是一处群芳争艳的大花园，有花圃、有温室，还有浑然天成的人工瀑布。在面积广达30万平方米的花海田里，有春天的郁金香、夏天的波斯</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">菊、向日葵和百日草。美丽的花海，日式和欧风的各类庭园搭配的巧思的设计，在富士山美景的衬托之下更显得绚烂缤纷。</span></p><p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: blue">备注：6月1日到6月20日和7月16日到8月31日的团队前往山中湖花之都参观，6月20日至7月16日的团队前往河口湖八木崎公园参观）</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【富士急乐园】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">富士急游乐园拥有世界最大落差（79m）总长度2,045米的过山车FUJIYAMA ，1997年富士急游乐园的过山车以4项世界最高记录而加载吉尼斯世界大全。其它还有海盗船，透明观览车，处刑馆等数十项游乐项目，内容极为丰富。富士急游乐园给人以最大的刺激和快感，可在富士山美丽壮观的大背景下感受非日常的体验，留下难忘的记忆。</span></p><p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: blue">PS</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: blue">：富士急乐园为园内用午餐，赠送门票。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【富士山五合目】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">：富士山由山脚到山顶分为十合，由山脚下出发到半山腰称为五合目。很多人都以登上富士山为荣。为此我们特意安排您乘坐巴士上到2305米的五合目，亲自目睹富士山的壮观景象，富士山一年四季随季节变化而景观不同，日出日落，气候变化，都会令富士山瞬间呈现不同的身影，千姿百态！</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black">能否登上富士山五合目受到天气因素的影响，若由于天候因素，可能无法完全登上富士山五合目（可能只能登上一合目），亦无退费，敬请谅解。</span></strong></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">富士山区【地震博物馆】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: black">该体验馆主要包含地震体验、避难体验及科普角三个板块。除了通过图片视频模型等形象地向您介绍地震、火山喷发等天灾发生的原因及避难方法外，还可以让您身临其境般亲身体验</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: #333333">。</span></p><p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: black"></span></p><p>&nbsp;<img title="3565896b-1d3e-4c73-952e-c9636ff5411d.jpg" src="/ueditor/net/upload/2016-07-16/4d696472-74d7-4c42-9bba-934080e43894.jpg"/></p>', 3, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (955, N'[汽车]成都-45KM都江堰-65KM汶川-285KM红原（约400KM，行车约7-8小时）', 0, 1, 1, N'红原/唐克', N'<p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 24px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><span style="FONT-FAMILY: 宋体,SimSun">早上成都三环内接，途经千年古堰――<strong>都江堰</strong>、<strong>紫坪埔水利工程</strong>，走都汶高速，车览5.12地震中“映秀”，</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">可以看见512大地震给当地人民带来的巨大灾难景象，震撼的公路、倒塌的桥梁、一栋栋被破坏的楼房、还有各地政府为灾区人民地震后重建的一栋栋崭新的新居，</span><span style="FONT-FAMILY: 宋体,SimSun">经汶川县城，尔后领略米亚罗峡谷风光，穿越雄伟的鹧鸪山隧道（4420米）， 经红军会师地――刷马路口，翻越黄河与长江的分水领――<strong>查真梁子山</strong>景观，领略“才饮长江水，又食黄河鱼”的美妙情怀<span style="FONT-FAMILY: 宋体,SimSun; COLOR: red">（查真梁子海拔4345米，游客切勿剧烈运动，以免引发严重的高原反应）</span>，抵达龙日坝草原，眼前呈现出的是一望无垠的草原风光：蔚蓝的天空、洁白的云彩、青青的草原、星星点点的格桑花、成群的牛羊、纯朴悠闲的牧民．．．．．．进入红原大草原，游览红原第一景――<strong>月亮湾</strong>，因草原上的河流弯曲像弯月，故名“红原月亮湾”。大自然就把景色布置在这片草滩上，这一景色恰似朝鲜族演员舞蹈时帽子上晃动的长长飘带，画出一个又一个弯曲的弧线，当柔和的阳光洒向水面的时候，沉静的草滩顿时亮丽起来，水光泛金，蜿蜒如金龙起物，</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">听着导游讲述月亮湾美丽动人的传说</span><span style="FONT-FAMILY: 宋体,SimSun">，抵</span></span><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 16px">达高原不夜城红原入住酒店</span><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red">(晚餐后参加走进藏家，体验藏家生活，此项目为赠送，不参加不退费，也不等价交换，如当天未安排则第二天晚上在川主寺安排)。</span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 24px"><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red"></span>&nbsp;</p><p style="TEXT-ALIGN: center"><img title="19128042.jpg" src="/ueditor/net/upload/2016-08-10/128fe7e9-93fd-4e8d-8b29-a4d3a5cbf21d.jpg"/></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 24px"><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red"></span>&nbsp;</p><p><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #ff6600">温馨提示：</span></strong></span></p><p style="LINE-HEIGHT: 1.75em; MARGIN: 0px 3px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">1、本行程沿线属藏羌地区，用餐及住宿硬软件条件有限，限电限水，限时供应热水，请不要以城市的标准来衡量；沿线提倡绿色环保，请游客自带毛巾及洗漱用品等，若对住宿条件有更高要求的游客建议住宿升级。</span></p><p style="LINE-HEIGHT: 1.75em; MARGIN: 0px 3px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">2、该线路景区景色优美世人向往，但沿途地处少数民族聚居地，用餐简单，建议您自备干粮或可口的榨菜等自己家乡佐料。</span></p><p style="LINE-HEIGHT: 1.75em; MARGIN: 0px 3px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">4、整个三天行程沿途会有旅游车加水或供游客上厕所方便的路边站点，类似站点下车后属于自由活动时间，上厕所会有当地人收费（自费：一般为1元/人/次），自由活动期间请随身携带或寄存贵重物品，自行负责人身及财产安全。</span></p><p style="LINE-HEIGHT: 1.75em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">5、进入藏区，请尊重少数民族风俗习惯，如若你自由活动期间在附近逛街商店或小卖点，请不要讨价还价后而不购买，请不要与当地人争吵及发生冲突，夜间请不要自行外出，需要帮助请及时与随团导游或旅行社联系。</span></p><p></p>', 1, 92)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (463, N'成都（137km)→雅安(140km)→泸定(49km)→康定(78km)/新都桥(海拔3400m)(404公里 大约9个小时）', 0, 1, 1, N'新都桥（海拔3400m）', N'<p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; 成都集合出发，从成雅高速经雨城【雅安】,天全午餐后，经二O山欣赏“一山有阴阳”的两种截然不同的奇观，到达大渡河谷（川西最大最深峡谷），远眺大渡河风光;游览【泸定桥】(自费10元)，经情歌故乡【康定】翻越有“康巴第一关”的塞外屏障【折多山】，最高峰海拔4900米，垭口海拔4298米，也是川藏线上第一个需要翻越的高山垭口。后抵“摄影家的天堂”之称的【新都桥】，它处在川藏线南北分叉路口，是一片如诗如画的世外桃源，神奇的光线、无垠的草原、弯弯的小溪、金黄的柏杨、山峦连绵起伏、藏寨散落其间……川西的平原风光美丽地绽放，晚餐后入住指定宾馆。</span></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><strong><span style="POSITION: relative; FONT-FAMILY: 黑体; COLOR: #010f21; FONT-SIZE: 16px; TOP: 1px">【泸定桥】</span></strong></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 0px"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">泸定桥又被称为铁索桥，是中国古代桥梁建筑的杰作。1935年5月，北上抗日</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">的红军向天险大渡河挺近。国民党派了两个团防守泸定， 大渡河水流湍急，</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">两岸都是高山峻岭，只有一座铁索桥可以通过。这座铁索桥，桥面离水面有十</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">多米高，由十三根铁链组成的。它成为阻拦红军北上天堑，二十二位英雄担</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">任突击连飞夺泸定桥。</span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 39px"><strong><span style="POSITION: relative; FONT-FAMILY: 黑体; COLOR: #010f21; FONT-SIZE: 16px; TOP: 1px">【康定】</span></strong></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; 康定，一座享誉世界的历史文化名城；一座<a href="http://baike.baidu.com/view/68373.htm" target="http://baike.baidu.com/_blank"><span style="COLOR: windowtext; text-underline: none">跑马山</span></a>，名扬五州四海；一曲《<a href="http://baike.baidu.com/subview/131171/8181693.htm" target="http://baike.baidu.com/_blank"><span style="COLOR: windowtext; text-underline: none">康定情歌</span></a>》千古绝唱，醉了天下人。具有悠久灿烂的历史文化，是川藏咽喉、茶马古道重镇、藏汉交汇中心。</span></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【雅安】【二O山】【泸定桥（自理）】【康定】【折多山】【新都桥】</span></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><span style="FONT-SIZE: 15px"><span style="FONT-FAMILY: Calibri">&nbsp;<img src="/ueditor/net/upload/2016-07-14/36777b83-c555-4ac9-bca4-005b9c7066ec.jpg" title="wKgBpVUeupaAMcS7ABGAk4tOlHs64.jpg"/></span></span></p>', 1, 72)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (952, N'[汽车]成都-110KM汶川-110KM米亚罗-108KM-花海-80KM红原 ', 0, 1, 1, N'红原/唐克/瓦切', N'<p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 1.75em; BACKGROUND-COLOR: rgb(255,255,255); MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; MARGIN-BOTTOM: 0px; COLOR: rgb(77,77,77); FONT-SIZE: 14px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">早上成都出发，经千年古堰――都江堰，经汶川县城,</span><span style="FONT-FAMILY: 宋体,SimSun">经过东方古堡-桃坪羌寨、嘉绒藏族第一寨--甘堡藏寨、</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">理县，穿越鹧鸪山遂道，进入红原县境内的刷马路口，过刷经寺镇</span><span style="FONT-FAMILY: 宋体,SimSun; BACKGROUND: white; COLOR: #333333">后</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">到达长江与黄河的分水岭――</span><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: red">查真梁子</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">（海拔4345米）</span></strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">（自由活动30分钟），进入红原县境内的――龙日坝大草原 ，沿着大草原一直到达红原――</span><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: red">月亮湾风景区（游览时间约30分钟）</span></strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">，在月亮湾一边游揽景区内美丽的草原风光，一边听着导游讲述月亮湾美丽动人的传说，晚宿红原</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: red">(晚餐后自由活动，稍微休息后参加走进藏家，体验藏家生活，此项目为赠送，不参加不退费，也不等价交换，如当天未安排则第二天晚上在川主寺安排)。</span></span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 28px; BACKGROUND-COLOR: rgb(255,255,255); MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; MARGIN-BOTTOM: 0px; COLOR: rgb(77,77,77); FONT-SIZE: 14px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><img title="DSC_2497-2.jpg" src="/ueditor/net/upload/2016-08-10/d8b5a5e6-a8b3-4a06-97d7-48fa06c00ba5.jpg"/></p><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 28px; BACKGROUND-COLOR: rgb(255,255,255); MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; MARGIN-BOTTOM: 0px; COLOR: rgb(77,77,77); FONT-SIZE: 14px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">&nbsp;</p><table cellspacing="0" cellpadding="0" vspace="0" hspace="0"><tbody><tr><td style="BORDER-BOTTOM: #f0f0f0; BORDER-LEFT: #f0f0f0; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; PADDING-LEFT: 12px; PADDING-RIGHT: 12px; BORDER-TOP: #f0f0f0; BORDER-RIGHT: #f0f0f0; PADDING-TOP: 0px" valign="top"><p style="LINE-HEIGHT: 24px; MARGIN: 0px 5px 0px 0px"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: blue">温馨提示</span></strong><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: blue">：</span></strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099"><br/>1.</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099">大草原的出发时间都比较早，如果您是入住酒店，请自行提前通知酒店为您提供路早餐，早上到前台自行领取。当地条件较为艰苦，蔬菜较少，且口味不是很好，建议客人自带些干粮或零食（400多公里：水泥和沥青盘山公路<span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099">）。</span></span></span></p><p style="LINE-HEIGHT: 24px"><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099; FONT-SIZE: 16px">2.大草原沿线属藏区条件较差，限时供应热水，一般为晚9-11点间供应，沿线提倡绿色环保，酒店不提供一次性洗漱用品，请游客自备。</span></p></td></tr></tbody></table><p><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099; FONT-SIZE: 14px">&nbsp;</span> <span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099; FONT-SIZE: 16px">3.当地属于高原地区。如果您要预防高原反应的发生，请在出发前自行准备抗高原药品。<span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"></span></span></p><p><br/></p>', 1, 61)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (457, N'成都→127km雅安→174km磨西镇', 0, 1, 1, N'磨西镇', N'<p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: #010f21; FONT-SIZE: 15px">【泸定桥】</span></strong><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">又称铁索桥，桥长103米，宽3米，13根铁链固定在两岸桥台落井里，9根作底链，4根分两侧作扶手，共有12164个铁环相扣，飞夺泸定桥--这是中国工农红军长征中的一场战役，发生于1935年5月25日。中央红军部队在贵州省安顺强渡大渡河成功，沿大渡河左岸北上，主力由安顺场沿大渡河右岸北上，红四团官兵在天下大雨的情况下，在崎岖陡峭的山路上跑步前进，一昼夜奔袭竟达120公里，终于在5月29日凌晨6时许按时到达泸定桥西岸。创造了人类行军史的奇迹！第2连连长廖大珠等22名突击队员沿着枪林弹雨和火墙密布的铁索踩着铁链夺下桥头，并与左岸部队合围占领了泸定桥。中央红军主力随后从泸定桥上越过天险，粉碎了蒋介石歼灭大渡河以南红军的企图。</span></span></p><p style="LINE-HEIGHT: 150%"><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; COLOR: #00b050; FONT-SIZE: 15px">【行】</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 15px">早上：</span></strong><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">7:00</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">成都市体育中心东大门集合，经成雅高速前往以“雅雨、雅女、雅鱼”三绝而闻名的雅安，是川藏、青藏的咽喉，是古南方丝绸之路和茶马古道的门户必经之路。下高速后沿秀美清衣江而上，观青衣江河谷风光.</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 15px">中午：</span></strong><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">天全午餐后，经【二O山】欣赏“一山有阴阳”的两种截然不同的奇观。到达大渡河谷（川西最大最深峡谷），远眺大渡河风光，游览【泸定桥】</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">(</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">自费</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">10</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">元</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">)1935</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">年中国工农红军在长征途中“飞夺泸定桥”，使之成为中国共产党重要的历史纪念地。感受当年红军</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">22</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">勇士飞夺泸定桥的气势。沿大渡河逆流而上，沿大渡河逆流而上，</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">沿大渡河逆流而上。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 15px">晚上：</span></strong><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">晚餐后入住低<span style="BACKGROUND: yellow">海拔</span></span><span style="LINE-HEIGHT: 150%; BACKGROUND: yellow; FONT-SIZE: 15px">1600</span><span style="LINE-HEIGHT: 150%; BACKGROUND: yellow; FONT-SIZE: 15px">米磨西镇酒店。</span></span></p><p style="LINE-HEIGHT: 150%"><span style="LINE-HEIGHT: 150%; FONT-FAMILY: &#39;MS Mincho&#39;; COLOR: #00b050; FONT-SIZE: 15px"></span><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; COLOR: #00b050; FONT-SIZE: 15px">【游】</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">【二O山】【泸定桥】</span></span></p><p style="LINE-HEIGHT: 150%"><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; COLOR: red; FONT-SIZE: 15px">温馨提示：</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">如果因为道路维修施工不能走二郎山，可以改道石棉 。</span></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">&nbsp;<img title="wKgBpVUeupaAMcS7ABGAk4tOlHs64.jpg" src="/ueditor/net/upload/2016-07-14/8c55ea7b-d19b-4506-ace7-1556c5346828.jpg"/></span></p>', 1, 74)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (458, N'磨西→180km新都桥', 1, 0, 1, N'新都桥', N'<p style="LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: black">【折多山】</span></strong></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: black">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: black">川藏线上第一个需要翻越的高山垭口，为大雪山一脉，是重要的地理分界线。最高峰海&nbsp; </span><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: black">拔4900米，垭口海拔4298米。</span></p><p style="LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: black">【新都桥】</span></strong></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 方正兰亭超细黑简体">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="FONT-FAMILY: 方正兰亭超细黑简体">新都桥是令人神往的“摄影天堂”，地处在公路318国道南、北线分叉路口，是一片</span><span style="FONT-FAMILY: 方正兰亭超细黑简体"><a href="http://baike.baidu.com/view/1366315.htm" target="_blank"><span style="COLOR: windowtext; text-underline: none">如</span></a></span><span style="FONT-FAMILY: 方正兰亭超细黑简体"><a href="http://baike.baidu.com/view/1366315.htm" target="_blank"><span style="COLOR: windowtext; text-underline: none">诗如画</span></a></span><span style="FONT-FAMILY: 方正兰亭超细黑简体">的世外桃源。神奇的光线，无垠的草原，弯弯的小溪，金黄的</span><span style="FONT-FAMILY: 方正兰亭超细黑简体"><a href="http://baike.baidu.com/view/25090.htm" target="_blank"><span style="COLOR: windowtext; text-underline: none">柏杨</span></a></span><span style="FONT-FAMILY: 方正兰亭超细黑简体">，山峦连绵起</span><span style="FONT-FAMILY: 方正兰亭超细黑简体">伏，藏寨散落其间，牛羊安详地吃草……</span><span style="FONT-FAMILY: 方正兰亭超细黑简体"><a href="http://baike.baidu.com/view/297370.htm" target="_blank"><span style="COLOR: windowtext; text-underline: none">川西</span></a></span><span style="FONT-FAMILY: 方正兰亭超细黑简体">的平原风光美丽地绽放。这，就是新都桥，</span><span style="FONT-FAMILY: 方正兰亭超细黑简体">令人神往的“光与影的世界”、“摄影家的天堂”。</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【行】</span></p><p style="LINE-HEIGHT: 1.5em"><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp;&nbsp; <strong>&nbsp;</strong></span></span><strong><span style="FONT-FAMILY: 宋体">早上：</span></strong><span style="FONT-FAMILY: 宋体">早餐后自由活动，可以自费前往红石滩参观游览，红石滩位于四川省</span><a href="http://baike.baidu.com/subview/6372/6372.htm" target="_blank"><span style="FONT-FAMILY: 宋体; COLOR: windowtext; text-underline: none">海螺沟</span></a><span style="FONT-FAMILY: 宋体">景</span><span style="FONT-FAMILY: 宋体">区</span><a href="http://baike.baidu.com/subview/3338767/3338767.htm" target="_blank"><span style="FONT-FAMILY: 宋体; COLOR: windowtext; text-underline: none">雅家埂</span></a><a href="http://baike.baidu.com/subview/1801092/1801092.htm" target="_blank"><span style="FONT-FAMILY: 宋体; COLOR: windowtext; text-underline: none">两河口</span></a><span style="FONT-FAMILY: 宋体">地带，是世界上规模较大的</span><a title="红石滩自助游" href="http://www.yododo.com/area/1-01-25-20-24299" target="_blank"><span style="FONT-FAMILY: 宋体; COLOR: windowtext; text-underline: none">红石滩</span></a><span style="FONT-FAMILY: 宋体">之一，石头上生长的呈铁锈红的物质</span><span style="FONT-FAMILY: 宋体">是一种极稀有的藻类</span><span style="FONT-FAMILY: Calibri">(</span><span style="FONT-FAMILY: 宋体">地衣的复合体</span><span style="FONT-FAMILY: Calibri">)</span><span style="FONT-FAMILY: 宋体">，在湿度、温度、气候适宜的情况下，这种植物就</span><span style="FONT-FAMILY: 宋体">会在岩石的表面自然生长，大概四五年后就会死亡，然后再生。据中科院的研究，这种</span><span style="FONT-FAMILY: 宋体">藻类的生长规律至今没有圆满的解释。</span><span style="FONT-FAMILY: Calibri">&nbsp;</span><a title="雅家梗自助游" href="http://www.yododo.com/area/1-01-25-56-24040" target="_blank"><span style="FONT-FAMILY: 宋体; COLOR: windowtext; text-underline: none">雅家梗</span></a><span style="FONT-FAMILY: 宋体">的红石面积广，颜色鲜红，顺着山谷延</span><span style="FONT-FAMILY: 宋体">绵不绝，气势宏伟。</span><a title="冰川自助游" href="http://www.yododo.com/area/2-28-02-22608" target="_blank"><span style="FONT-FAMILY: 宋体; COLOR: windowtext; text-underline: none">冰川</span></a><span style="FONT-FAMILY: 宋体">河岸上的每一块石头都被披上了一件件红色外衣，红石在河流</span><span style="FONT-FAMILY: 宋体">两旁堆积，成群的红石变成一种流动的红色，与清洌的流水相伴顺流而下，让人感叹大</span><span style="FONT-FAMILY: 宋体">自然的造化神奇红石不仅神秘，而且在全世界也是独一无二的奇观。</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style="FONT-FAMILY: 宋体">中午：</span></strong><span style="FONT-FAMILY: 宋体">午餐自理，下午离开磨西镇前往新都桥</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><strong><span style="FONT-FAMILY: 宋体">晚上：</span></strong><span style="FONT-FAMILY: 宋体">乘车至摄影天堂新都桥，拍摄夕照中的光影世界，之后酒店内用晚餐</span></p><p style="LINE-HEIGHT: 1.5em"><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp; </span></span></p><p style="LINE-HEIGHT: 1.5em"><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;</span></span><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【游】</span><span style="FONT-FAMILY: 宋体">【折多山】【新都桥】</span></p><p style="LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;">&nbsp;</span></strong></p><p style="LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: red">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: red">温馨提示：</span></strong></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: red">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: red">雅家梗红石滩景区公路还在建设中，部分路段路况较差，自费前往的客人费用和安全自&nbsp; </span></strong></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: red">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><strong><span style="FONT-FAMILY: 方正兰亭超细黑简体; COLOR: red">理，与旅行社无关。</span></strong></p><p style="LINE-HEIGHT: 1.5em"><strong><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;"><img title="新都桥2.jpeg" src="/ueditor/net/upload/2016-07-14/dc155941-6db3-44c3-afc9-f11463d5fdee.jpeg"/></span></strong></p>', 2, 74)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (459, N'新都桥→68km 雅江→137km理塘→148km稻城→77km香格里拉镇', 1, 1, 1, N'香格里拉', N'<p style="LINE-HEIGHT: 150%; TEXT-INDENT: 24px; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: Tahoma"></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【高尔寺山】高尔寺山是318国道上的景点之一，海拔4659米(注：高尔寺山垭口海拔4412米)。高尔寺山山顶为康定与雅江两县的天然屏障，而且它是纵览大雪山(雅拉神山,折多山, 和贡嘎神山)的最佳位置之一。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【剪子弯山】著名的剪子弯山，它的藏语名字叫“惹玛那扎”，意为羊子山口。山口海拔4659米，是318国道经康巴地区的最高山口之一。千山万壑尽收眼底的意境，领略一番茶马古道的古风遗韵。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【雅江】雅江，藏语“捻曲卡”，“河口”的意思，雅江县位于康巴地区腹地，立于茶马古道上，积</span></span></p><p style="LINE-HEIGHT: 150%"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">淀下了丰富的康巴人文景观。因而，享有“中国香格里拉文化旅游大环线第一县”和“茶马古道第一渡”之称。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【卡子拉山】卡子拉山海拔4718米。因海拔较高，树木就更加稀少，主要是高山草甸。在这里看山，层峦叠嶂，一层比一层更远，一层比一层颜色更浅，直至天边，真像行走在‘天路’之上。卡子拉山说起来是山，但却感觉不出它是山，好象是到了美丽的牧场，让人心旷神怡。虽然美景让人流连忘返。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【理塘】理塘，系藏语，藏语拼音：Litang Zong。“理”意为“铜”，“塘”为“坝子”，即广阔坝子有如铜镜。故为理塘土司地。海拔4014米，是一个风景秀丽，人杰地灵的地方，被人们誉为“中华高城、雪域圣地、草原明珠”。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【毛垭大草原】平均海拔4500米左右</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【海子山自然保护区】海子山自然保护区位于稻城县北部高原区，是青藏高原最大的古冰体遗迹，以“稻城古冰帽”著称于世。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">【稻城】古名“稻坝”。藏语意为山谷沟口开阔之地。东汉为白狼羌地，唐属吐蕃，元属吐蕃等路宣慰司，明属朵甘都司，清属理塘土司。光绪三十三年（公元1911年），正式核准置“稻成县”，隶康定府.</span></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; COLOR: #00b050; FONT-SIZE: 15px">【行】</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px; MARGIN: 0px 0px 13px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 15px">早上：</span></strong><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">6:00</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">早餐后，乘车欣赏“光与影的世界”新都桥的美丽风光。翻越【高尔寺山】抵雅砻江畔―康巴小县城【雅江】继续翻越两座大山海拔4659米【剪子弯山】。海拔4718米【卡子拉山】。</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px; MARGIN: 0px 0px 13px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 15px">中午：</span></strong><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">抵高原明珠、世界高城【理塘】海拔4014米，远观【毛垭大草原】风光，在理塘用完中餐后，继续翻越形似兔子的【兔子山】、经青藏高原最大的古冰体遗迹，</span></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">即“稻城古冰帽”平均海拔4500米左右【海子山自然保护区】。至【稻城】（名</span></span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px; MARGIN: 0px 0px 13px"><strong><span style="LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 15px">晚上：</span></strong><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">抵高原明珠、世界高城【理塘】海拔4014米，远观【毛垭大草原】风光，在理塘用完中餐后，继续翻越形似兔子的【兔子山】、经青藏高原最大的古冰体遗迹，</span></span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px"><span style="FONT-FAMILY: 宋体">即“稻城古冰帽”平均海拔4500米左右【海子山自然保护区】。至【稻城】（名稻坝，意为山谷沟口开阔之地）</span></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体"><span style="LINE-HEIGHT: 150%; COLOR: #00b050; FONT-SIZE: 15px">【游】</span><span style="LINE-HEIGHT: 150%; COLOR: black; FONT-SIZE: 15px">【高尔寺山】【雅江】</span><span style="LINE-HEIGHT: 150%; FONT-SIZE: 15px">【剪子弯山】<span style="COLOR: black">【卡子拉山】</span>【理塘】<span style="COLOR: black">【兔子山】【香格里拉镇】</span></span></span></p><p style="LINE-HEIGHT: 150%"><span style="LINE-HEIGHT: 150%; FONT-FAMILY: &#39;Arial&#39;,&#39;sans-serif&#39;; FONT-SIZE: 15px"></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">&nbsp;<img title="高尔寺山2.jpg" src="/ueditor/net/upload/2016-07-14/1982bdcd-8107-42dc-bd02-95a12102a9e8.jpg"/></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">&nbsp;<img title="wKgB3FCcwf2Admb1AAGxQQDSypU40.jpg" src="/ueditor/net/upload/2016-07-14/87e7d4ac-9dc7-47c9-b353-685c6610554e.jpg"/></span></p></p>', 3, 74)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (460, N'日瓦→亚丁→36km日瓦→74km稻城', 1, 0, 1, N'稻城', N'<p style="LINE-HEIGHT: 150%; TEXT-INDENT: 29px; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体">【亚丁景区】</span></strong><span style="FONT-FAMILY: 宋体">亚丁，藏语意为“向阳之地”。保护区内的三座雪山：仙乃日、央迈勇、夏诺多吉，南&nbsp; 北向分布，呈品字形排列，统称“念青贡嘎日松贡布”，意为：终年积雪不化的三座护法神山圣地。藏传佛教中称其为“三怙主雪山”，是藏民心中的神圣之地。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; COLOR: #333333">&nbsp;&nbsp;&nbsp;</span><strong><span style="FONT-FAMILY: 宋体; COLOR: #333333">【仙乃日】</span></strong><span style="FONT-FAMILY: 宋体; COLOR: #333333">（意为“观音菩萨”，藏语意为“观世音菩萨”）海拔6032米，是稻城三座神山的北峰，是三座神山中海拔最高的一座。</span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 24px; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: #333333">【央迈勇】</span></strong><span style="FONT-FAMILY: 宋体; COLOR: #333333">（藏语意为“文殊菩萨”）是“三怙主”雪山的南峰，海拔高度为5958米，在佛教中排在“三怙主”雪山之首。文殊菩萨在佛教中是有智慧的化身，雪峰像文殊师利用手中的智慧之俞直指苍穹，冰晶玉洁的央迈勇傲然于天地之间。对于从稻城方向过来的人们来说，央迈勇是三座雪山里最难以见到的一座。</span></p><p style="LINE-HEIGHT: 150%; TEXT-INDENT: 24px; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: #333333">【夏诺多吉】</span></strong><span style="FONT-FAMILY: 宋体; COLOR: #333333">（意为“金刚手菩萨”）是“三怙主”雪山的东峰，海拔高度为5958米，在“三怙主雪山”佛位第三，洛克先生把它形容为展开巨翅蓄抛待飞的蝙蝠，将它比喻成西腊神话中的雷神，神山脚下洛绒牛场是当年洛克先生宿营处。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050">【行】</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">&nbsp;&nbsp;&nbsp; </span><strong><span style="FONT-FAMILY: 宋体">早上：</span></strong><span style="FONT-FAMILY: 宋体">7:00</span><span style="FONT-FAMILY: 宋体; COLOR: black">早餐后到达【亚丁景区】后在游客接待中心换乘景区观光车（观光车120/人自理）抵达亚丁景区（30多公里，车程1.5小时）【扎灌崩】，下车</span><span style="FONT-FAMILY: 宋体">游客徒步500米直接抵达冲古寺，观赏冲古寺风光及神山【仙乃日】（意为观世音菩萨，海拔6023米），之后朝拜【卓玛拉措神湖】（珍珠海单程1.5公里），早上为拍摄仙乃日倒影最佳时间，后原路返回冲古寺。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体">&nbsp;&nbsp;&nbsp; </span><strong><span style="FONT-FAMILY: 宋体">中午：</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black">中餐（自理）后游客可自费乘坐电瓶车（往返80，单程50）前往【洛绒牛场】（徒步1-2小时左右到达洛绒牛场）。这里是观【夏诺多吉神山】（意为金刚手菩萨，海拔5958米）【央迈勇神山】（意为金文殊菩萨，海拔5958米）的最佳地点，观赏野生动物、高山杜鹃、雪山森林、冰川、湖泊、河流，体验人和自然和谐相处的天然妙趣。让神山圣水与心共鸣，尽情沉醉在梦幻之地。后原路返回【扎灌崩】，</span><span style="FONT-FAMILY: 宋体">乘观光车出景区。后翻越海拔4300多米的【波瓦山】，欣赏赤土河谷迷人风光、世外桃源般藏式村落，万亩白杨林等，定会令你流连其中。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><strong><span style="FONT-FAMILY: 宋体; COLOR: black">&nbsp;&nbsp;&nbsp; </span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: black">晚上：</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black">回到稻城县，入住当地唯一挂星酒店亚丁温泉大酒店并用晚餐，</span><strong><span style="FONT-FAMILY: 宋体; COLOR: red">赠送酒店内的温泉</span></strong><span style="FONT-FAMILY: 宋体; COLOR: red">。<strong>（赠送项目，不用不退费）</strong></span><span style="FONT-FAMILY: 宋体; COLOR: black">连日的奔波操劳，随着那天然的优质温泉水，彻底洗去一身的疲劳。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体; COLOR: black">&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; COLOR: #00b050">【游】</span><span style="FONT-FAMILY: 宋体; COLOR: black">【亚丁景区】【扎灌崩】【</span><span style="FONT-FAMILY: 宋体">卓玛拉措神湖<span style="COLOR: black">】【夏诺多吉神山】</span></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体; COLOR: red">温馨提示：</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px 29px"><span style="FONT-FAMILY: 宋体">1. </span><span style="FONT-FAMILY: 宋体">洛绒牛场至五色海牛奶海，路途遥远且海拔非常高，有些路段只有马道基本没有可以徒步道路，对身体素质要求很高，不建议前往。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">2.&nbsp; </span><span style="FONT-FAMILY: 宋体">马帮由当地人经营，如因费用和服务水平有争议和投诉，旅行社无责。请在消费前明确清楚费用服务细节。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">3.&nbsp; </span><span style="FONT-FAMILY: 宋体">骑马时请戴手套，需注意安全，必须由马夫牵方能骑。景区气侯变化不定，请准备好雨具。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">4.&nbsp; </span><span style="FONT-FAMILY: 宋体">因亚丁景区内无法提供用餐，景区内中餐请自理，客人可提前自备干粮。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px 29px"><span style="FONT-FAMILY: 宋体; BACKGROUND: yellow">5</span><span style="FONT-FAMILY: 宋体; BACKGROUND: yellow">、温泉，若选择参加请带好洗浴用品，一般泡温泉时间为40分钟为宜，请按照温泉区所告知注意事项进行活动；温泉池边路滑，请小心慢行，注意安全；出水口温度高，小心烫伤；未成年人及老人须成人陪同；心脏病、高血压、脑溢血、冠心病等疾病患者和孕妇及酒后严禁泡温泉。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px 29px"><span style="FONT-FAMILY: 宋体; BACKGROUND: yellow"><img title="wKgB4lMRjcCAOy4TAAIgdYxvXRE27.jpg" src="/ueditor/net/upload/2016-07-14/3eccc4ce-c06a-4167-b374-a95483188013.jpg"/></span></p>', 4, 74)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (956, N'红原-130KM若尔盖-44KM花湖-180KM川主寺（约360KM，行车约5-6小时）', 1, 0, 1, N'川主寺/松潘', N'<p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">早餐后乘车到高原明珠――若尔盖县，到达国家重点保护湿地、也是世界上保护最好的高原湿地、黑颈鹤栖息地――<strong>花湖</strong>（<strong>门票已含，电瓶车30元/人自理</strong>，游览时间约2小时），花湖是一片未被污染的处女地，鱼儿游弋，湖畔五彩缤纷，感受明镜般的湖水影照下的蓝天白云，领悟大自然的无私给予。花湖因水中开满花朵而得名，这里是中国最大最平坦的湿地草原，四周数百亩水草地就是“高原湿地生物多样性自然保护区”――被喻为“地球的肺”，保护区以黑颈鹤为主，可以走上浮桥，陶醉在湖水和鲜花的美景<span style="FONT-FAMILY: 宋体,SimSun">中，与水鸟嬉戏，寻找黑颈鹤的足迹。后抵<strong>镰刀坝草原</strong>，在这里有游玩方式齐备的牧家乐――品烤全羊、喝酥油茶、尝藏式土火锅、体验牧家生活，骑中国三大名马之一的河曲马，纵马驰骋，感受游牧族策马扬鞭的马背生活......<span style="FONT-FAMILY: 宋体,SimSun; COLOR: red">（费用150-200元/人自理，视参加项目多少而定）</span>，后<span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">沐浴夕阳乘车到达川主寺入住酒店。</span><span style="FONT-FAMILY: 宋体,SimSun; COLOR: red">（晚上参加藏羌歌舞晚会，此项目为赠送，不参加不退费，也不等价交换）。</span></span></span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red"></span>&nbsp;</p><p style="TEXT-ALIGN: center"><img title="20116171322347989.jpg" src="/ueditor/net/upload/2016-08-10/419afe12-8307-4a94-917e-37f8f5b4aef4.jpg"/></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red"></span>&nbsp;</p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red"></span>&nbsp;</p><p><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #ff6600">温馨提示：</span></strong></span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">1、户外游览时间较长，日照充足，紫外线较强，请客人备好雨伞，防晒霜，太阳镜等物品，景区内禁止抽烟，严禁乱丢垃圾，违者最低罚款500元起。</span><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">2、骑马是一项危险的运动，保险公司不承保请一定注意安全，如发生意外由客人全权承担，切记！&nbsp; </span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">3、沿线海拔3500米左右，游客切记不要剧烈运动，以免引发严重的高原反应。</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">4、沿途均有野生动物，请不要往窗外扔任何物品。</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px">5、大草原为游牧部落安多藏族的天堂，大多风俗和汉族不同，草原上听从导游安排，避免和当地人发生口角，尊重他们的风俗习惯，不要和当地人谈政治等敏感问题。</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体,SimSunFONT-FAMILY: 宋体,SimSun; COLOR: red"></span></p>', 2, 92)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (461, N'稻城→148km理塘→137km新都桥/磨西', 1, 1, 1, N'新都桥/磨西', N'<p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体">【雅江】雅江，藏语“捻曲卡”，“河口”的意思，雅江县位于康巴地区腹地，立于茶马古道上，积淀下了丰富的康巴人文景观。因而，享有“中国香格里拉文化旅游大环线第一县”和“茶马古道第一渡”之称。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体">【理塘】理塘，系藏语，藏语拼音：Litang Zong。“理”意为“铜”，“塘”为“坝子”，即广阔坝子有如铜镜。故为理塘土司地。海拔4014米，是一个风景秀丽，人杰地灵的地方，被人们誉为“中华高城、雪域圣地、草原明珠”。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体">【毛垭大草原】平均海拔4500米左右</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; COLOR: #00b050">【行】</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体">早上：</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black">早餐后参观康巴特色【龙古民俗文化村】，至桑堆镇观【红草地】（注：红草地为季节性景观，只有在每年的9月底到10月初才能看到此处的草原呈红色），在秋天的时候，配合上后面泛黄的桦树，倒映在水塘中，是许多书上所介绍的美景。之后翻越古冰川遗迹海子山到达理塘。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: black">中午：</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black">经过雅江县、用中餐后至新都桥/磨西</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: black">晚上：</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black">抵达新都桥或磨西，入住酒店休息</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; COLOR: black"></span><span style="FONT-FAMILY: 宋体; COLOR: #00b050">【游】</span><span style="FONT-FAMILY: 宋体; COLOR: black">【红草地】【雅江】【</span><span style="FONT-FAMILY: 宋体">理塘<span style="COLOR: black">】【毛垭大草原】</span></span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><strong><span style="FONT-FAMILY: 宋体; COLOR: red">温馨提示：</span></strong></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">当天若遇新都桥用房紧张或天气或路况原因，当晚入住的地方可能会做一定的调整（比如安排入住康定、泸定、海螺沟等，住宿标准不变。），敬请谅解！</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">&nbsp;<img title="高尔寺山3.jpg" src="/ueditor/net/upload/2016-07-14/5d508445-08ba-4f5f-9f38-d75d647f317d.jpg"/></span></p><p>&nbsp;</p>', 5, 74)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (620, N'下午自由活动（自由活动期间 无导游 领队 用车服务）', 1, 0, 0, N'普吉国五星酒店（Wyndham Sea pearl villas phuket或同级）', N'<p style="line-height:24px"><span id="_baidu_bookmark_start_268" style="display: none; line-height: 0px;">?</span><span style="font-family: 宋体, SimSun;"><span style="font-size: 16px;">8:30出发珠宝店。</span>后参观：</span><strong><span style="font-family: 宋体, SimSun; font-size: 16px;">【<span id="_baidu_bookmark_end_269" style="display: none; line-height: 0px;">?</span>人蛇大战】</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">『60分钟』并听取蛇毒知识讲解。毒蛇研究中心只在泰国、巴西、印度三地才有，只有这些地方才会有号称蛇王的“金刚王眼镜蛇”，因其以眼镜蛇为食，故而练就了无与伦比的阴阳剧毒。泰国皇家毒蛇中心的“空手捉蛇表演”将使您惊呼连连、印象深刻。随后出发，前往乳胶店。下午返回酒店，自由安排。</span></p><p><br/></p>', 5, 80)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (621, N'普吉-成都 （9c 8664  14：55-19：25）', 1, 0, 0, N'不含', N'<p><span style="font-size: 16px; font-family: 宋体, SimSun;">睡到自然醒</span><span style="font-size: 16px; font-family: 宋体, SimSun;">，中午11点退房，下午可在导游通知的指定地方集合并送普吉国际机场。，结束愉快旅程！</span></p><p><br/></p>', 6, 80)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (650, N'参考航班：东京成田AIR香港CX501   10:50-14:40', 1, 0, 0, N'', N'<p><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">在领队的带领下</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">专车</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">前往</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">成田</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">机</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">场</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">搭乘</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">国际</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">班机飞往香港，后转机</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">飞</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">往</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">温</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">暖的家，</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">结</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">束</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">这</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">次</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">温</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">馨</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">之旅。</span></p>', 7, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (543, N'古尔沟/朴头/丘地/理县―40KM甘堡――成都193KM', 1, 1, 0, N'', N'<p><span style="FONT-FAMILY: 宋体">早</span><span style="FONT-FAMILY: 宋体">餐后出发呼吸着芳香的空气，抵达</span><strong><span style="FONT-FAMILY: 宋体; COLOR: red">甘堡藏寨</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: fuchsia">（游览时间不低于1小时）</span></strong><span style="FONT-FAMILY: 宋体">，<span style="LETTER-SPACING: 1px">倚山傍河而建，故得名甘堡，藏语意为“坡上的村落”。在整个藏区自古就将甘堡为甘堡甲穹，意为百户大寨，甘堡藏寨石屋壮观的艺术群雕，带着给人一种的艺术享受。然后乘车抵达</span>神奇的东方古堡―</span><strong><span style="FONT-FAMILY: 宋体; COLOR: red">桃坪羌寨新寨子</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: fuchsia">（自由活动），</span></strong><strong><span style="FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: fuchsia">桃坪羌寨</span></strong><span style="FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: #333333">是世界上保存最完整的尚有人居住的碉楼与民居融为一体建筑群，享有“天然空调”美名。其完善的地下水网、四通八达的通道和碉楼合一的迷宫式建筑艺术，被中外学者誉为“羌族建筑艺术活化石”、“神迷的东方古堡”。</span></p><p><span style="FONT-SIZE: 14px; FONT-FAMILY: 宋体; BACKGROUND: white; COLOR: #333333">午餐后返回成都结束愉快旅程！</span></p><p><img title="5719b7cb2fec1.jpg" src="/ueditor/net/upload/2016-07-14/d80313b0-b037-4b69-910d-c5fd95e493be.jpg"/></p>', 2, 79)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (435, N'石林风景区+九乡风景区', 1, 1, 0, N'火车卧铺', N'<p style="line-height:28px"><span style="font-size:14px;font-family:宋体">早从酒店接团，至统一集合地点-泰丽酒店，之后乘车前往国家AAAAA景区-<strong>【石林风景区】</strong>（赠送景区电瓶车），体会鬼斧神工的天下第一奇观，畅游2亿7千万年前浩瀚海洋之海底，一边漫步在世界最大的喀斯特地质公园：大小石林、大叠水、长湖、月湖、芝云洞、奇风洞``等等；一边聆听阿诗玛的故事，午餐后乘车前往国家AAAA景区-<strong>【九乡风景区】</strong>（赠送景区索道），依次游览幽静迷人的情人谷荫翠峡，雄狮大厅，气势恢弘的雌雄双瀑，奇美壮阔的喀斯特溶洞，九乡是以溶洞景观为主、溶洞外自然风光、人文景观、民族风情为一体的综合性风景名胜区。之后乘车返回昆明，送至昆明火车站。晚乘火车硬卧前往艳遇之都-丽江。</span></p><p><strong><span style="font-size:16px;font-family:楷体;color:red">温馨提示：昆明到丽江坐汽车需要将近9个小时，而我们安排舒服的火车卧铺，您睡一觉就到了世界旅游胜地-丽江。</span></strong></p><p><img src="/ueditor/net/upload/2016-07-14/a5c74e92-a1ca-43ec-9a00-bcb165fbd453.jpg" title="3171988fcdaa021024cfa98989df7266b2dec.jpg"/>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src="/ueditor/net/upload/2016-07-14/96eaa103-2838-4229-b96d-29ab40a88a0f.jpg" title="9965758_171540569000_2.jpg"/></p>', 3, 73)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (739, N'景洪―勐仑植物园―景洪 ', 1, 1, 1, N'景洪', N'<p><span style="FONT-FAMILY: 黑体">早餐后乘车沿澜沧江赴</span><strong><span style="FONT-FAMILY: 宋体; COLOR: #c44100">【勐仑植物园】</span></strong><strong><span style="FONT-FAMILY: 黑体; COLOR: black">，</span></strong><span style="FONT-FAMILY: 黑体">全称”中国科学院西双版纳热带植物园”<strong><span style="COLOR: red">（自理观光车40元/人）</span></strong>，<span style="COLOR: black">游览中国唯一的热带雨林文化博物馆后观赏跳舞草、白竹园、神秘果、见血封喉树、绞杀树、百竹园等数千种珍稀热带雨林植物，观梭风光。午餐后</span>游览<span style="COLOR: black">参观</span><span style="COLOR: red">【<strong>傣族园</strong>】<strong>（费用已含门票：100/人，此费用不含观光车40元/人）</strong></span>，还可<span style="COLOR: black">参加大型泼水活动。晚可自费观看</span><strong><span style="COLOR: red">【勐巴拉娜西歌舞秀】（费用自理：甲票260元/人 贵宾票：380元/人）</span></strong><span style="COLOR: black">。</span></span></p><p><span style="FONT-SIZE: 14px; FONT-FAMILY: 黑体; COLOR: black">温馨小提示：当天车程:往返约170公里，植物园游览时间4小时</span></p><p><img title="C_20130531-177946_1280_.jpg" src="http://www.yueyouyuebei.com/ueditor/net/upload/2016-08-03/e41d93bf-9702-41c0-a88a-623b2ea8a99d.jpg"/></p><p>&nbsp;</p>', 2, 89)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (740, N'景洪―野象谷风景区―景洪', 1, 1, 1, N'景洪', N'<p><span style="FONT-FAMILY: 黑体">早餐后，酒店大堂集合出发，前往</span><strong><span style="FONT-FAMILY: 宋体; COLOR: #c44100">【新老傣族村寨】</span></strong><span style="FONT-FAMILY: 黑体">，了解傣族村寨的发展变化，参观西双版纳新农村建设的标准典范，自费品尝傣族特色风味佳肴，后乘车前往具有热带雨林特点的</span><strong><span style="FONT-FAMILY: 宋体; COLOR: #c44100">【野象谷风景区】</span></strong><strong><span style="FONT-FAMILY: 黑体; COLOR: black">，</span></strong><span style="FONT-FAMILY: 黑体">领略边疆热带地区独特的气候风貌，游蝴蝶园、蟒蛇园、百鸟园(观看各种鸟艺表演），可自费乘观光索道<strong><span style="COLOR: red">（费用自理：50元/人/单程,70元/人/往返，索道约35分钟,全长2063M）</span></strong>或步行进野象谷,最后欣赏中国第一所大象学校大象多才多艺的搞笑表演。【车程约36公里.约40-50分钟，途中可由导游带领自费游览<span style="COLOR: red">【<strong>基诺山寨景区</strong>】<strong>（费用自理： </strong></span></span></p><p style="LINE-HEIGHT: 21px"><strong><span style="FONT-FAMILY: 黑体; COLOR: red">160</span></strong><strong><span style="FONT-FAMILY: 黑体; COLOR: red">元/人）</span></strong><span style="FONT-FAMILY: 黑体">。<span style="COLOR: black">晚可自费观看</span><span style="COLOR: red">【<strong>湄公河之夜篝火晚会</strong>】<strong>（费用自理：甲票200元/人 ，贵宾票： 380元/人）。</strong></span></span></p><p><span style="FONT-SIZE: 14px; FONT-FAMILY: 黑体">温馨小提示：当天车程:往返约80公里，景点游览时间3小时</span></p><p><img title="ff23036400df9ddfc8d65eac.jpg" src="/ueditor/net/upload/2016-08-03/520687aa-36fc-4af4-8124-12886df4b44d.jpg"/></p><p>&nbsp;</p>', 3, 89)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (742, N'景洪-成都', 0, 0, 0, N'', N'<p><span style="FONT-SIZE: 10.5pt; FONT-FAMILY: 宋体; COLOR: #c44100; mso-bidi-font-size: 10.0pt; mso-font-kerning: 1.0pt; mso-ascii-font-family: " mso-bidi-language:="" mso-fareast-language:="" mso-ansi-language:="" mso-bidi-font-family:="" mso-hansi-font-family:="" new="" times=""><strong>早餐后，自由活动，根据航班时间送机，乘机返回成都。</strong></span></p>', 5, 89)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (436, N'拉市海+束河古镇（九鼎龙潭）+千亩花海+丽江古城一日游', 1, 1, 0, N'丽江', N'<p style=";line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">早接火车，抵达丽江后前往游览丽江高原湖泊</span><span style="font-size:16px;line-height:150%;font-family:&#39;PMingLiU&#39;,&#39;serif&#39;">-</span><span style="font-size:16px;line-height:150%;font-family:宋体">【</span><strong><span style="font-size:16px;line-height:150%;font-family:宋体">拉市海</span></strong><span style="font-size:16px;line-height:150%;font-family:宋体">】</span><span style="font-size:16px;line-height:150%;font-family:宋体">（骑马划船自理），午餐在拉市海品尝具丽江特色的土鸡火锅，拉市海玩法推荐：</span></p><p style=";margin-left:18px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">1、第一是骑着马重走茶马古道，领略沿途风光。</span></p><p style=";margin-left:18px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">2、第二是划着船感受拉市海的美，我们提供铁皮船与皮划艇两种方式，可以感受高原湖泊的美丽。</span></p><p style=";margin-left:18px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体"><img src="/ueditor/net/upload/2016-07-14/e008d202-3526-4aac-9542-011ce23b7aaf.jpg" title="拉海.jpg"/></span></p><p style=";margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">玩完拉市海后，我们前往下一站【<strong>束河古镇</strong>】<strong>（被评为中国最美的古镇）</strong>，束河古镇的玩法推荐：</span></p><p style=";margin-left:18px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;">1、</span><span style="font-size:16px;line-height:150%;font-family:宋体">到束河古镇一定要玩九鼎龙潭，它是整个束河的水源头，我相信你从来没有见过这么清辙的水，你会爱上它的。</span></p><p style=";margin-left:18px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;">1、</span><span style="font-size:16px;line-height:150%;font-family:宋体">到束河古镇一定要玩四方街，它是整个束河古镇的灵魂，看懂了它就看懂了束河。</span></p><p style=";margin-left:18px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">2、到了束河古镇一定要去大石桥拍照，在哪拍出来的照片是最美的照片。</span></p><p style=";line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">我们前往观看【<strong>丽江千亩花海</strong>】（丽江的普罗旺斯，等你来拍照），在花海下，拍出你人生中最美的照片，我们的领队全心全意帮你拍照，带你领略丽江的美丽</span><span style="font-size:16px;line-height:150%;font-family:宋体">:</span><span style="font-size:16px;line-height:150%;font-family:宋体">玩转花海的攻略：</span></p><p style=";line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体"><img src="/ueditor/net/upload/2016-07-14/8d604b02-f678-44ca-ad07-176bdc0a915e.jpg" title="花海.jpg"/></span></p><p style=";margin-left:48px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;">1、</span><span style="font-size:16px;line-height:150%;font-family:宋体">请你一定要把手机充满电，如果可以一定要带一个充电宝。</span></p><p style=";margin-left:48px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;">2、</span><span style="font-size:16px;line-height:150%;font-family:宋体">请你一定要穿最漂亮的衣服，拍出最美的自己。</span></p><p style=";margin-left:48px;margin-bottom:0;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;">3、</span><span style="font-size:16px;line-height:150%;font-family:宋体">请你一定要保持心情开心，因为开心的人最美丽。</span></p><p><span style="font-size:16px;font-family:宋体">玩完束河古镇后，如果您有需要可以前往观看代表丽江最高水平的演出-【<strong>丽江千古情</strong>】（费用请自理，100%满意，不满意可以退款），晚餐自理，建议你在新城找个美味的丽江特色小吃品尝（可以咨询导游或司机），丽江特色美味包括腊排骨、沙坝鱼、黑山羊等等，建议晚上21:00左右自行前往丽江古城，21:00开始丽江古城的夜生开始了，你可以尽情享受艳遇丽江，请自身的人身与财产安全，自行返回酒店休息；</span></p><p><img src="/ueditor/net/upload/2016-07-14/d48c7a42-82fd-423d-a1e4-5b1a3fa744a9.jpg" title="丽江.jpg"/></p>', 4, 73)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (437, N'蝴蝶泉公园+洱海大游船+南诏风情岛+洱海音乐party一日游', 1, 1, 1, N'大理', N'<p style="line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">早餐后，沿着大理到丽江的高速公路前往风花雪月大理</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">，抵达后前往大理最唯美的自然景观</span><span style="font-size: 16px;line-height: 150%;background: white">-</span><span style="font-size:16px;line-height:150%;font-family:宋体">【</span><strong><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">蝴蝶泉公园</span></strong><span style="font-size:16px;line-height:150%;font-family:宋体">】</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">（每年蝴蝶会时，成千上万的蝴蝶从四面八方飞来蝴蝶泉来约会），形成天下罕见的奇观</span><span style="font-size: 16px;line-height: 150%;background: white">-</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">蝴蝶会</span><span style="font-size: 16px;line-height: 150%;background: white">,</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">它也是电影五朵金花的拍摄基地，同时它也是大理白族文化的代表</span><span style="font-size:16px;line-height:150%;font-family:宋体">；</span></p><p style="line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体"><img src="/ueditor/net/upload/2016-07-14/0270dde8-265a-4c0f-8cca-a16bed021f35.jpg" title="蝴蝶.jpg"/></span></p><p style="line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">午餐（午餐安排最具大理特色的白族土八碗）过后乘车前往大理最高大上的景点-【<strong>洱海豪华大游船</strong>】</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体">：大游船玩法推荐：</span></p><p style="margin-left:24px;line-height:150%"><span style="font-size: 16px;line-height: 150%">1、</span><span style="font-size:16px;line-height:150%;font-family:宋体">从洱海桃园码头登游船，有“金花”、“阿鹏”热烈欢乐的迎、送宾仪式，游程中举行三道茶歌舞表演，让广大游客饱览苍洱风光的同时，享受到“礼仪之邦”的待客礼仪，领略白族独特的民俗风情和歌舞艺术。</span></p><p style="margin-left:24px;line-height:150%"><span style="font-size: 16px;line-height: 150%">2、</span><span style="font-size:16px;line-height:150%;font-family:宋体">游船在南诏风情岛停靠，您可以尽情欣赏洱海最美岛屿-南诏风情岛，同时还可以欣赏双廊古镇。“大理风光在苍洱 ，苍洱风光在双廊”指的就是这里了。</span></p><p style="margin-left:24px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体"><img src="/ueditor/net/upload/2016-07-14/d08deb47-82c2-4615-8ce2-6903b7ed2b7d.jpg" title="D3AB059B4C372C709069E6B8C40D1297.jpg"/></span></p><p style="margin-left:24px;line-height:150%"><span style="font-family: 宋体; line-height: 150%;">3</span><span style="font-family: 宋体; line-height: 150%;">、我们乘坐游船抵达龙龛玛头下游船，之后前往吃晚餐，晚餐结束后入住酒店休息。</span><span style="font-family: 宋体; line-height: 150%;">我们乘车前往用餐，之后入住酒店休息，晚上前往参加洱海音乐会，20:00</span><span lang="EN-US" style="font-family: 宋体; font-size: 12pt; line-height: 150%; background: white;">Party</span><span style="font-family: 宋体; font-size: 12pt; line-height: 150%; background: white;">正式开始，我们精心为您准备了当地的民谣歌手，也为每一位赠送了当地风花雪月啤酒一瓶，为了相互之间<span lang="EN-US">20</span>几年、<span lang="EN-US">30</span>几年或者<span lang="EN-US">40</span>、<span lang="EN-US">50</span>几年才修来的相识与相聚，没有理由不忘情的嗨皮，这里一定没有地域与年龄，这就是旅游的真谛：无忧、忘我、广交天下友。这一夜，也留给那些因为“时光?慢”而结下缘分的菇凉与帅锅们<span lang="EN-US">.....</span></span></p><p><br/></p>', 5, 73)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (616, N'成都－甲米 ', 0, 0, 1, N'普吉国际四星酒店', N'<p><span style="font-size: 16px; font-family: 宋体, SimSun;">请各位贵宾于早上9点机场集合，由专人办理登机手续，搭乘豪华客机前往泰国南部有东方夏威夷美喻著名渡假胜地</span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;">普吉，下午乘车前往搭乘专车前往</span><strong><span style="font-family: 微软雅黑;color: rgb(36, 64, 97);font-size: 16px">【沙发里】</span></strong><span style="font-size: 16px; font-family: 宋体, SimSun;">『约30分钟』</span><span style=";font-family:微软雅黑;letter-spacing:-0;font-size:16px">，</span><strong><span style="font-family: 微软雅黑;color: rgb(36, 64, 97);font-size: 16px">【乘坐大象】</span></strong><span style=";font-family:微软雅黑;color:rgb(33,88,104);letter-spacing:-0;font-size:16px">（温馨提示：请提前准备20铢/人的小费，交于训大象的工作人员）</span><span style="font-size: 16px; font-family: 宋体, SimSun;">跋山涉水悠游在丛林之中,晚上参观国际人妖秀后回到酒店。</span></p><p><img src="/ueditor/net/upload/2016-07-15/0ea404ea-a823-4ff2-8c0a-84c538f78417.jpg" title="eee08d3bb2ba98daca9c9e3b88fbaa02.water.jpg_710x360_817915b1.jpg"/></p>', 1, 80)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (618, N'珊瑚岛――安达曼公主号', 1, 1, 1, N'普吉国际四星酒店', N'<p><span style="font-size: 16px; font-family: 宋体, SimSun;">酒店用早餐之后，前往搭乘【快艇】前往</span><span style="font-size: 16px;"><strong><span style="font-family: 微软雅黑; color: rgb(192, 0, 0);">【珊瑚岛KOH HEA】</span></strong></span><span style="font-size: 16px; font-family: 宋体, SimSun;">（停留约3小时），作为泰国国家一级保护珊瑚区，其四周的海域是浮潜和潜水爱好者必去之地，优质的珊瑚可以和马尔代夫媲美。一直以来都是欧美人士度假休闲的天堂，四周金发碧眼的游客仿佛让你置身地中海的海滩。面积约1平方公里，在印度洋翡翠般的海水的环绕之中，慵懒的白色沙滩反射着阳光，散发迷人的光晕。从透明渐变到碧蓝的海水，蕴藏着无尽的传说和故事，随后乘船前往</span><span style="font-size: 16px;"><strong><span style="font-family: 微软雅黑; color: rgb(192, 0, 0);">【安达曼公主号】</span></strong></span><span style="font-family: 微软雅黑;">，</span><span style="font-size: 16px; font-family: 宋体, SimSun;">安达曼公主号有来自菲律宾的著名歌手，伴随着动人旋律，享受着种种美食，环望着一望无际的安达曼海，更有中文歌曲，泰文歌曲，英文歌曲，你喜欢的风格，只要我们的歌手能够做到的，都可以随时点唱……还有各式各样的冷饮（游玩约90分钟）后送往酒店休息。&nbsp;</span></p><p><img src="/ueditor/net/upload/2016-07-15/f9d2b85b-75c8-42fe-8d4a-0dfc5098ed4e.jpg" title="2014120-20379.jpg"/></p><p><span style=";font-family:微软雅黑;font-size:16px"></span></p><p><span style="font-size: 16px;"><strong><span style="font-family: 微软雅黑; color: rgb(255, 0, 0);">（岛上有各种水上项目可自行参加报名，水上项目不算自费项目）</span></strong></span></p><p><span style="font-size: 16px;"><strong><span style="font-size: 16px; font-family: 微软雅黑; color: rgb(255, 0, 0);">1、特别提醒：每位游客乘坐长尾船或乘坐快艇时，必须穿救生衣，不要乘坐船头，听从导游安排，若不按规定乘坐，发生意外身体伤害，旅行社只负责协助办理意外保险，旅行社将不承担任何责任。</span></strong></span></p><p><span style="font-size: 16px;"><strong><span style="font-size: 16px; font-family: 微软雅黑; color: rgb(255, 0, 0);">2、旅客于离岛或酒店泳池或海边参与水上活动时，请注意自身安全，患有高血压、心脏衰弱、癫痫、刚动完手术、酒醉、孕妇等恕不适合参加出海行程</span></strong></span></p><p><br/></p>', 3, 80)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (617, N'快艇大PP岛――快艇小PP岛环游――帝王岛-情人沙滩――大堡礁浮潜――翠竹岛', 1, 1, 1, N'普吉国际四星酒店', N'<p><span style="font-size: 16px; font-family: 宋体, SimSun;">早餐后，乘车快艇前</span><span style="font-family: 宋体, SimSun;">前往码头，</span><span style="font-family: 宋体, SimSun;">后乘往</span><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【帝王岛】</span></strong><span style="font-family: 宋体, SimSun;">（约45分钟）,后乘</span><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【快艇前往PP岛】</span></strong><span style="font-family: 宋体, SimSun;">，此岛被泰国政府规划为国家公园，环境受到重视，途经</span><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【燕子洞】</span></strong><span style="font-family: 宋体, SimSun;">（约10分钟），根据泰国法律，停船于洞外，欣赏奇观。之后搭乘快艇前往一片三面环山拥有着细腻沙滩的幽静海域―【情人沙滩】（约20分钟），在此您可以游泳、日光浴；随后前往</span><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【天堂湾】</span></strong><span style="font-family: 宋体, SimSun;">（约30分钟），天堂湾三面环山，中间是一汪平静的海水。在大家尽兴后我们在此搭乘快艇，来到一片使人激动人心的海域―</span></p><p><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);"><img src="/ueditor/net/upload/2016-07-15/4dae29d2-8464-4ad4-a2fa-7446f3ba25b5.jpg" title="普吉.jpg"/></span></strong></p><p><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【小PP岛环游】</span></strong><span style="font-family: 宋体, SimSun;">（约10分钟），当你将手中的面包投入这片清澈透底的海中，这里会突然出现上各种类的热带鱼来享用您的面包美餐，您更可以马上穿上救生衣、带上我们为您准备的专业蛙镜跳入海中</span><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【大堡礁 浮潜】</span></strong><span style="font-family: 宋体, SimSun;">（约60分钟），与热带鱼共舞，之后我们搭乘前往快艇前往泰国小马代</span><strong><span style="font-family: 微软雅黑; color: rgb(227, 108, 10);">【翠竹岛】</span></strong><span style="font-family: 宋体, SimSun;">，下午返回普吉岛。</span></p><p><span style="font-family: 宋体, SimSun;"><img src="/ueditor/net/upload/2016-07-15/4c6f384b-80c5-4055-8237-a2b2838db9ba.jpg" title="c2dd5a2bd576482e9d3bef571d471590_C_1600_1200_Mtg_7.jpg"/></span></p><p><span style="font-size: 16px;"><strong><span style="font-family: 微软雅黑; color: rgb(255, 0, 0);">注意：泰国国家环境保护部门协同泰国国家旅游局于2015年6月22日发布正式通知：为保护PP岛国家自然景区生态环境现开始不建议游客登上“情人沙滩”【严禁偷拾贝壳；严禁乱丢垃圾；严禁捉弄海鱼】为配合工作将此景点换作其他沙滩。</span></strong></span></p><p><span style="font-size: 16px;"><strong><span style="font-size: 16px; font-family: 微软雅黑; color: rgb(255, 0, 0);">1、安全特别提醒：每位游客乘坐快艇时必须穿救生衣，不能坐船头，若不按规定乘坐，发生意外身体伤害，旅行社只负责协助办理意外保险，旅行社将不承担任何责任。当船停稳，在导游或者船家的搀扶下，小心上岸。重要物品请自行小心保管，以免发生意外。</span></strong></span></p><p><span style="font-size: 16px;"><strong><span style="font-size: 16px; font-family: 微软雅黑; color: rgb(255, 0, 0);">2、出海请注意自身安全，患有高血压、心脏衰弱、癫痫、刚动完手术、孕妇等恕不适合参加出海行程。</span></strong></span></p><p><br/></p>', 2, 80)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (699, N'成都-宜宾-蜀南竹海（约4.5小时）', 0, 1, 1, N'竹海景区', N'<p><span style="font-family: 宋体;font-size: 16px">早上07:00</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">成都</span></strong><span style="font-family: 宋体;font-size: 16px">出发，经甜城―内江，恐龙故居―自贡，到达万里长江第一城―</span><span style=";font-family:宋体;color:rgb(0,128,128);font-size:16px">宜</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">宾午餐</span></strong><span style="font-family: 宋体;font-size: 16px">，赠送</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">宜宾燃面</span></strong><span style="font-family: 宋体;font-size: 16px">，餐后乘车沿途远眺岷江、金沙江、长江三江汇合口，后乘车欣赏有“不是漓江而胜漓江”之称的U江，</span><span style="font-family: 宋体;letter-spacing: -0;font-size: 16px">到达国家AAAA级风景名胜区――</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);letter-spacing: -0;font-size: 16px">蜀南竹海！</span></strong><span style="font-family: 宋体;letter-spacing: -0;font-size: 16px">游览有超凡脱俗、荡涤烦忧的</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);letter-spacing: -0;font-size: 16px">忘忧谷</span></strong><span style="font-family: 宋体;font-size: 16px">，让你体会“千年茂林曲径幽”的意境;我国第一家“竹”专业博物馆--</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">竹海博物馆</span></strong><span style="font-family: 宋体;font-size: 16px">，这里即有竹海美丽的风光，又有丰富的竹类资源、灿烂的竹文化和悠久的竹利用史；至墨溪</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">乘索道观竹海全貌（含）</span></strong><span style="font-family: 宋体;font-size: 16px">，感受空中观竹，领略“万倾竹海波涛涌”的气势，漫步</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">翡翠长廊</span></strong><span style="font-family: 宋体;font-size: 16px">（翡翠长廊：路面是由“色如渥丹、灿若明霞”的天然红砂石铺成。两旁密集的老竹新篁拱列，遮天蔽日）与</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">观海楼</span></strong><span style="font-family: 宋体;font-size: 16px">。后至酒店</span><strong><span style="font-family: 宋体;color: rgb(0, 128, 128);font-size: 16px">用餐</span></strong><span style="font-family: 宋体;font-size: 16px">，安排住宿，晚上自由活动。</span></p><p><img src="/ueditor/net/upload/2016-07-20/c6ba5a7d-809d-42a7-be05-ea8fd6449215.jpeg" title="20150411H5556_urQdx.jpeg"/></p><p><br/></p><p><img src="/ueditor/net/upload/2016-07-20/6ff94fa8-9c46-4bb2-a109-0208ecb93038.jpg" title="pic_004_01.jpg"/></p><p><br/></p><p><img src="/ueditor/net/upload/2016-07-20/7404cd77-c810-4361-ac9d-1941389b3ea3.jpg" title="6597447998656487650.jpg"/></p>', 1, 84)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (700, N'蜀南竹海-成都（5小时左右）', 1, 1, 0, N'不含', N'<p style="margin-right:1px;text-indent:32px;line-height:125%"><span style="font-family: 宋体;line-height: 125%;font-size: 16px">早餐后，参观有“竹海明珠”之称的佛教、道教共存的宗教活动文化圣地---</span><strong><span style="font-family: 宋体;line-height: 125%;color: rgb(0, 128, 128);font-size: 16px">仙寓洞</span></strong><span style="font-family: 宋体;line-height: 125%;font-size: 16px">；再到</span><strong><span style="font-family: 宋体;line-height: 125%;color: rgb(0, 128, 128);font-size: 16px">天宝寨</span></strong><span style="font-family: 宋体;line-height: 125%;font-size: 16px">揽胜，体会“一夫当关万夫莫开”的气势，观“三十六计”石刻浮雕，忆古通经。会“千年茂林曲径幽”的意境;悠久的历史文物古迹掩映在青山绿水之间，</span><span style="font-family: 宋体;">令蜀南竹海之旅更富神秘与远离尘世之情趣。泛舟卧虎藏龙拍摄点--</span><strong><span style="font-family: 宋体; color: rgb(0, 128, 128);">照映潭（含）</span></strong><span style="font-family: 宋体;">，体会“舟在水中行人在画中游”的感觉，再到</span><strong><span style="font-family: 宋体; color: rgb(0, 128, 128);">七彩飞瀑（过渡含）</span></strong><span style="font-family: 宋体;">景区体会“飞流直下三千尺”的意境，观瀑布落下，泛起水花，在太阳照耀下映出的七色彩虹，</span><strong><span style="font-family: 宋体; color: rgb(0, 128, 128);">午餐品尝竹海熊猫大餐</span></strong><strong><span style="font-family: 宋体; color: rgb(0, 128, 128);">―</span></strong><strong><span style="font-family: 宋体; color: rgb(0, 128, 128);">全竹宴</span></strong><span style="font-family: 宋体; color: rgb(0, 128, 0);">（含山珍六宝</span><span style="font-family: 宋体; color: rgb(0, 128, 0);">―</span><span style="font-family: 宋体; color: rgb(0, 128, 0);">五彩竹胎儿、泡椒竹荪蛋、黄金竹燕窝、白灼竹</span><span style="font-family: 宋体; color: rgb(0, 128, 0);">胎盘、富贵竹毛肚、土鸡竹荪汤；另赠两个竹海野味</span><span style="font-family: 宋体; color: rgb(0, 128, 0);">―</span><span style="font-family: 宋体; color: rgb(0, 128, 0);">高蛋白竹虫、竹林野鸡，以及竹叶老腊肉、竹筒豆花、多种竹笋、野菌及竹海特色小吃---</span><strong><span style="font-family: 宋体; color: rgb(255, 0, 0);">价值600元/800元桌以上</span></strong><span style="font-family: 宋体; color: rgb(0, 128, 0);">）</span><span style="font-family: 宋体;">，餐后乘车返回成都（4小时左右）结束愉快行程。</span></p><p><img src="/ueditor/net/upload/2016-07-20/1966599a-cbb7-454a-9aea-b5b633aa0a02.jpg" title="404903099378.jpg"/><span style=";font-family:宋体;color:rgb(0,128,0);font-size:16px"></span></p><p><br/></p><p><img src="/ueditor/net/upload/2016-07-20/3c0d7ddc-1e4a-438e-8d5c-177c5c8c9e58.jpg" title="641d8a0bx92020e550586&amp;690.jpg"/></p><p><br/></p><p><img src="/ueditor/net/upload/2016-07-20/e52563ce-4904-4a58-98dd-dbec2070869f.jpg" title="1403585444125252.jpg"/></p>', 2, 84)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (704, N'泸沽湖―盐源-西昌 270KM 车程6小时', 1, 1, 0, N'泸沽湖特色客栈', N'<p><span style="FONT-SIZE: 18px; FONT-FAMILY: 新宋体"></span>&nbsp;</p><p><span style="FONT-FAMILY: 宋体,SimSun">早餐后，后参观<strong><span style="COLOR: red">摩梭民居家纺（40分钟）</span></strong>，进一步了解摩梭人的生活习俗，<strong><span style="COLOR: red">家纺时参观摩梭银器店（40分钟），</span></strong>告别美丽的泸沽湖，后返回西昌，盐源午餐，返回西昌入住酒店。晚上可自行在城区吃特色火盆烧烤。</span></p><p><span style="FONT-FAMILY: 宋体,SimSun"><strong><span style="FONT-SIZE: 14px; FONT-FAMILY: 新宋体">温馨提示：</span></strong><span style="FONT-SIZE: 14px; FONT-FAMILY: 新宋体">严禁下湖游泳。自由活动期间注意安全！<span style="FONT-FAMILY: 宋体,SimSun"></span></span></span></p><p><span style="FONT-SIZE: 18px; FONT-FAMILY: 新宋体"><img title="鲁谷路.jpg" src="/ueditor/net/upload/2016-07-14/18f04887-42e8-42dd-a125-b192388159eb.jpg"/></span></p>', 3, 75)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (730, N'成都-大熊猫乐园-茂县-松潘-川主寺-薰衣草庄园基地-九寨沟', 0, 1, 1, N'沟口', N'<p><span style="font-family: 宋体, SimSun; font-size: 16px;">早餐后乘车从成都出发</span><span style="font-family: 宋体, SimSun; font-size: 16px;">【三环路内包接】前往都江堰参观大熊猫乐园景区（门票58元/人，费用已含，游览时间1h）</span><span style=";font-family:微软雅黑;font-size:16px">，</span><a href="https://www.baidu.com/s?wd=%E9%83%BD%E6%B1%9F%E5%A0%B0%E7%86%8A%E7%8C%AB&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1dbPA7WPjcYmvc3PjDvPWIh0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnWDYnjcsP103PHnYPHb3rjDYr0"><strong><span style=";font-family:微软雅黑;font-size:16px">都江堰熊猫</span></strong></a><span style="font-family: 宋体, SimSun; font-size: 16px;">乐园有30多只大熊猫，其中很多海龟派熊猫</span><span style=";font-family:微软雅黑;font-size:16px">，</span><a href="https://www.baidu.com/s?wd=%E6%B3%B0%E5%B1%B1&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1dbPA7WPjcYmvc3PjDvPWIh0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnWDYnjcsP103PHnYPHb3rjDYr0"><strong><span style=";font-family:微软雅黑;font-size:16px">泰山</span></strong></a><span style=";font-family:微软雅黑;font-size:16px">，</span><span style="font-family: 宋体, SimSun; font-size: 16px;">华</span><span style="font-family: 宋体, SimSun; font-size: 16px;">星，华美等还有</span><a href="https://www.baidu.com/s?wd=%E7%86%8A%E7%8C%AB%E7%9B%BC%E7%9B%BC&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1dbPA7WPjcYmvc3PjDvPWIh0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnWDYnjcsP103PHnYPHb3rjDYr0"><strong><span style=";font-family:微软雅黑;font-size:16px">熊猫盼盼</span></strong></a><span style=";font-family:微软雅黑;font-size:16px">。</span><span style="font-family: 宋体, SimSun; font-size: 16px;">另外一部分开自卧龙，极具&quot;攻击性&quot;上树，打滚随时都是表演起的，而且这里比成都基地的熊猫数量要多，环境自然也是都江堰的好，而且配有观光车。熊猫可以近距离的观看国宝“大熊猫。茂县午餐后前往川主寺镇</span><span style=";font-family:微软雅黑;font-size:16px">。</span></p><p><span style=";font-family:微软雅黑;font-size:16px"><img src="/ueditor/net/upload/2016-08-03/b9ac716a-ff7f-4142-8692-5c704996450e.jpg" title="196c832c-48a5-40b6-9c03-76b02663581b.jpg"/></span></p><p style="margin-right:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style="font-family: 宋体, SimSun; font-size: 16px;">后前往</span><span style="color: rgb(0, 176, 80);"><strong><span style="font-family: 微软雅黑; font-size: 16px;">薰衣草庄园基地</span></strong></span><span style="font-family: 宋体, SimSun; font-size: 16px;">游览（门票40元/人，费用已含，游览时间2h），薰衣草庄园介绍：又到草长莺飞的季节，在美丽的松潘，占地3000余亩的薰衣草正相继绽放，汇成一片紫色花海，被誉为高原的“普罗旺斯”。“今年紫色的花海会比去年更加壮观，更有普罗旺斯薰衣草花海的感觉。”据介绍，从今年5月下旬开始，在海拔3000米的松潘县川主寺镇，薰衣草已陆续绽放，相对去年400余亩的规模，今年的薰衣草已扩种至3000余亩，7-8月将进入全面盛开期，花期长达4个月。堪比普罗旺斯的成片紫色花海，无疑是九环线的一道独特的风景，吸引游客纷纷驻足拍照留影。</span></p><p style="margin-right:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style="font-family: 宋体, SimSun; font-size: 16px;"><img src="/ueditor/net/upload/2016-08-03/eb9c4c66-a603-43e9-bc29-eb31989b30be.jpg" title="24229291.jpg"/></span></p><p style="margin-right:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style="font-family: 宋体, SimSun; font-size: 16px;">后前往酒店休息</span></p><p><br/></p>', 1, 88)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (731, N'全天游览九寨沟', 1, 0, 1, N'沟口', N'<p><span style="font-family: 宋体, SimSun; font-size: 16px;">早餐后乘车前往</span><strong><span style=";font-family:微软雅黑;color:rgb(255,192,0);font-size:16px">【</span><span style="font-family: 微软雅黑;color: rgb(255, 192, 0);font-size: 16px">九寨沟风景区</span><span style=";font-family:微软雅黑;color:rgb(255,192,0);font-size:16px">】</span></strong><span style=";font-family:微软雅黑;font-size:16px">，</span><span style="font-family: 宋体, SimSun; font-size: 16px;">换乘观光车</span><strong><span style="font-family: 微软雅黑;font-size: 16px">（含九寨观光车）</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">游览九寨沟世界级自然遗产保护区（游览时间7小时）,九寨沟是由翠海、叠瀑、彩林、烟云、雪峰以及奇异多彩的藏族风情组成格调自然风韵独具的仙境，景观主要分布在</span><strong><span style="font-family: 微软雅黑;font-size: 16px">树正沟</span></strong><span style=";font-family:微软雅黑;font-size:16px">、</span><strong><span style="font-family: 微软雅黑;font-size: 16px">日则沟</span></strong><span style=";font-family:微软雅黑;font-size:16px">、</span><strong><span style="font-family: 微软雅黑;font-size: 16px">则查洼沟</span></strong><span style="font-family: 宋体, SimSun; font-size: 16px;">三条主沟内，景区内有最宽、最高、最雄伟壮观的三大瀑布：</span><strong><span style="font-family: 微软雅黑;font-size: 16px">珍珠滩瀑布</span></strong><strong><span style="font-family: 微软雅黑;font-size: 16px">、</span></strong><strong><span style="font-family: 微软雅黑;font-size: 16px">诺日朗瀑布</span></strong><strong><span style="font-family: 微软雅黑;font-size: 16px">、熊猫海瀑布</span></strong><span style=";font-family:微软雅黑;font-size:16px">，108</span><span style="font-family: 宋体, SimSun; font-size: 16px;">个高山湖泊及数十处流泉飞瀑等景观，最美最奇特的是九寨沟的水，清冽透底、变幻无穷，细细体味“黄山归来不看山，九寨归来不看水”的意境。参加走进藏家家访，让您身临其境的感受藏家生活、藏族文化和藏族风情与少数民族朋友举手狂欢锅庄晚会！</span><strong><span style="font-family: 微软雅黑;font-size: 16px">后参加羌藏风情歌舞晚会（不参加不退费）</span></strong></p><p><img src="/ueditor/net/upload/2016-08-03/f4cd6650-e810-4042-b0d7-c4cced4ae062.jpg" title="pc329-03.jpg"/></p><p><br/></p><p><img src="/ueditor/net/upload/2016-08-03/1d6a567e-cecc-4747-b2aa-473159b76258.jpg" title="1342232471294.jpg"/></p><p style="text-align:center;line-height:24px"><strong><span style="font-family: 宋体;color: rgb(255, 0, 0);font-size: 24px">温馨提示</span></strong></p><p style="margin-right: 0;margin-left: 0;text-indent: 0"><span style="font-family: 宋体, SimSun; font-size: 14px;">1、九寨沟分为三条沟，</span><span style="font-family: 宋体, SimSun; font-size: 14px;">单程35公里，沟内每个景点都有一个观光车上下的车站，凭观光车票当日可在任一站点自由换乘，每辆观光车都有讲解导游。游览景区内三条沟的先后顺序，完全根据观光车调度人员临时安排分沟游览，这样也能更好的分散游客，让您在拍照时候舒心一些。观光车一般是到每条沟最高景点下车，然后往下走游览，所以走路也不会觉得累。</span></p><p style="margin-right: 0;margin-left: 0;text-indent: 0"><span style="font-family: 宋体, SimSun; font-size: 14px;">2、九寨沟景区最佳的游览方式还是自由活动，<br/>乘坐的观光车运行方式和城里的公交车是一样的，因此导游也无法全程陪同。<br/>3、九寨景区禁止吸烟，有吸烟习惯的客人请忍耐，或是到餐的一角有唯一的抽烟区吸烟，否则会受到高额罚款。九寨沟是世界自然遗产，爱它您就保护它。</span></p>', 2, 88)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (949, N'成都-110KM汶川-110KM米亚罗-108KM-花海-80KM红原', 0, 1, 1, N'红原/唐克/瓦切', N'<p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">早上成都出发，经千年古堰――都江堰，经汶川县</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">城,经过东方古堡-桃坪羌寨、嘉绒藏族第一寨--甘堡藏寨、</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">理县，穿越鹧鸪山遂道，进入红原县境内的刷马路口，过刷经寺镇</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: rgb(51, 51, 51); background: white;">后</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">到达长江与黄河的分水岭――</span><strong style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 24.5px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: red;">查真梁子</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: black;">（海拔4345米）</span></strong><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">（自由活动30分钟），进入红原县境内的――龙日坝大草原 ，沿着大草原一直到达红原――</span><strong style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 24.5px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: red;">月亮湾风景区（游览时间约30分钟）</span></strong><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">，在月亮湾一边游揽景区内美丽的草原风光，一边听着导游讲述月亮湾美丽动人的传说，晚宿红原</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: red; background-color: rgb(255, 255, 255);">(晚餐后自由活动，稍微休息后参加走进藏家，体验藏家生活，此项目为赠送，不参加不退费，也不等价交换，如当天未安排则第二天晚上在川主寺安排)。</span></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: red; background-color: rgb(255, 255, 255);"><br/></span></p><p>&nbsp; &nbsp;&nbsp;<img src="/ueditor/net/upload/2016-08-10/e9ce4a1b-2295-48fa-a16e-c51652f6c9eb.jpg" title="QQ图片20160810145422.jpg" style="color: red; font-family: 宋体, SimSun; line-height: 24.5px;"/><span style="color: red; font-family: 宋体, SimSun; line-height: 24.5px; background-color: rgb(255, 255, 255);"></span><br/></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: red; background-color: rgb(255, 255, 255);"><br/></span></p><p><strong style="font-family: 宋体, SimSun; color: rgb(77, 77, 77); line-height: 24px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: blue;">温馨提示</span></strong><strong style="font-family: 宋体, SimSun; color: rgb(77, 77, 77); line-height: 24px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: blue;">：</span></strong><br/></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px; background-color: rgb(255, 255, 255);"><span style="border: 0px; border-image-source: initial; border-image-slice: initial; border-image-width: initial; border-image-outset: initial; border-image-repeat: initial; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24px; background-color: rgb(255, 255, 255);"><font color="#000099">1.</font></span><span style="color: rgb(0, 0, 153); border: 0px; border-image-source: initial; border-image-slice: initial; border-image-width: initial; border-image-outset: initial; border-image-repeat: initial; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24px; background-color: rgb(255, 255, 255);">大草原的出发时间都比较早，如果您是入住酒店，请自行提前通知酒店为您提供路早餐，早上到前台自行领取。当地条件较为艰苦，蔬菜较少，且口味不是很好，建议客人自带些干粮或零食（400多公里：水泥和沥青盘山公路<span style="border: 0px; outline: none; margin: 0px; padding: 0px;">）。</span></span></span></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: red; background-color: rgb(255, 255, 255);"><span style="color: rgb(0, 0, 153); font-family: 宋体, SimSun; line-height: 24px;  background-color: rgb(255, 255, 255);">2.大草原沿线属藏区条件较差，限时供应热水，一般为晚9-11点间供应，沿线提倡绿色环保，酒店不提供一次性洗漱用品，请游客自备。</span></span></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: red; background-color: rgb(255, 255, 255);"><span style="color: rgb(0, 0, 153); font-family: 宋体, SimSun; line-height: 24px;  background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; line-height: 28px;  font-family: 宋体, SimSun; color: rgb(0, 0, 153); background-color: rgb(255, 255, 255);">3.当地属于高原地区。如果您要预防高原反应的发生，请在出发前自行准备抗高原药品。</span></span></span></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  color: red; background-color: rgb(255, 255, 255);"><br/></span></p>', 1, 57)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (954, N'川主寺-17km松潘-149km茂县-190km成都', 1, 1, 0, N'不含', N'<p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 21px; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 微软雅黑, sans-serif; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"></span></p><table cellspacing="0" cellpadding="0" vspace="0" hspace="0"><tbody><tr><td style="BORDER-BOTTOM: #f0f0f0; BORDER-LEFT: #f0f0f0; PADDING-BOTTOM: 0px; BACKGROUND-COLOR: transparent; PADDING-LEFT: 12px; PADDING-RIGHT: 12px; WORD-BREAK: break-all; BORDER-TOP: #f0f0f0; BORDER-RIGHT: #f0f0f0; PADDING-TOP: 0px" valign="top"><p style="TEXT-ALIGN: left; LINE-HEIGHT: 1.75em"><span style="FONT-FAMILY: 宋体,SimSun; FONT-SIZE: 16px"><span style="FONT-FAMILY: 宋体,SimSun">早餐后<span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">参观具有藏族文化的<strong>特色</strong></span></span><strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">藏寨</span></strong><span style="FONT-FAMILY: 宋体,SimSun; COLOR: black">，近距离了解藏族民居特色、建筑艺术及生活方式，体验民族地区文化特色及经济建设成就；参观当地<strong>土特产展销中心，丝绸博物馆</strong>，乘车经历史名城――松潘古城，后经老虎嘴（堰塞湖），沿“震中”大峡谷、茂县、汶川返回成都，结束愉快美妙的之旅。</span></span></p></td></tr></tbody></table><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 21px; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 微软雅黑, sans-serif; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"></span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: center; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><br/><img title="201504271448094104.jpg" src="/ueditor/net/upload/2016-08-10/df87f243-0eb8-42d8-9f34-2083256c9eac.jpg"/></p><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">&nbsp;</p><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 1.75em; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099; FONT-SIZE: 16px">1. 小提示：行车时可能会遇上堵车等情况，行车时间会比平时要长，客人不要购买返程当天的机票或火车票。<strong><br/></strong>2 .今天是本次大草原之旅游程的最后一站，如若你对此次行程有任何异议，请告诉我们，我们会在第一时间根据你反馈的情况落实调查并及时处理；</span></p><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 1.75em; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><span style="FONT-FAMILY: 宋体,SimSun; COLOR: #000099; FONT-SIZE: 16px"></span></p><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN-TOP: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; MARGIN-BOTTOM: 0px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #000099; FONT-SIZE: 12px"></span>&nbsp;</p><p style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; BACKGROUND-COLOR: rgb(255,255,255); MARGIN-TOP: 0px; TEXT-INDENT: 96px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: tahoma, Arial, 微软雅黑, SimSun, Helvetica, sans-serif; WHITE-SPACE: normal; MARGIN-BOTTOM: 0px; HEIGHT: 30px; COLOR: rgb(119,119,119); FONT-SIZE: 14px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 15px"><span style="COLOR: rgb(255,102,0)">*</span>&nbsp;以上行程仅供参考，最终行程可能会根据实际情况进行微调，敬请以出团通知为准。</p><p><br/></p>', 3, 61)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (438, N'大理古城+崇圣寺三塔一日游', 1, 1, 0, N'昆明', N'<p style="line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">早餐后，约9:00乘车</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">前往</span><span style="font-size:16px;line-height:150%;font-family:宋体">游览“文献名邦”―【<strong>大理古城</strong>】(游览时间约120分钟)，大理古城的玩法推荐：</span></p><p style="line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">1</span><span style="font-size:16px;line-height:150%;font-family:仿宋">、</span><span style="font-size:16px;line-height:150%;font-family:宋体">到大理古城必须去洋人街看看，现在洋人街还有来自世界各地的驴友，与他们打招呼可以得到热情的回应。</span></p><p style="line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">2、到大理古城一定要到人民路上寻宝，去那里慢慢逛，看到了喜欢的东西还可以与摊主聊天，你会了解很多流浪故事。</span></p><p style="margin-left:8px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体">3、到大理古城一定要看看手工作坊打造的木雕、银雕、石雕（如果你需要在大理古城购物，请一定要认准质量，大理古城假货较多，请慎重）&nbsp;&nbsp;&nbsp; &nbsp;<img src="/ueditor/net/upload/2016-07-14/723bc024-158f-4daf-a4fe-22fe351299ff.jpg" title="大理.jpg"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style="line-height:150%"><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">午餐安排最具大理洱海特色的洱海砂锅鱼，午餐后前往游览大理最牛逼的景点，也是大理唯一的</span><span style="font-size: 16px;line-height: 150%;background: white">AAAAA</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">景区</span><span style="font-size: 16px;line-height: 150%;background: white">-</span><span style="font-size:16px;line-height:150%;font-family:宋体">【</span><strong><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">崇圣寺三塔</span></strong><span style="font-size:16px;line-height:150%;font-family:宋体">】（电瓶车自理）</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">，崇圣寺三塔主要是三大看点：</span></p><p style="margin-left:24px;line-height:150%"><span style="font-size: 16px;line-height: 150%">1、</span><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">是南诏国和大理国时期建筑的皇家寺院，至今已有1000多年的历史。</span></p><p style="margin-left:24px;line-height:150%"><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">2、这里东临洱海，西靠苍山应乐峰，风景十分优美，这湖中的三塔倒影，是大理标志的景点。</span></p><p style="margin-left:8px;line-height:150%"><span style="font-size: 16px;line-height: 150%;font-family: 宋体;background: white">3、三塔是了解大理国文化的最佳地，1000位游客眼中有1000个三塔，你眼中的三塔是什么样子呢。</span></p><p><span style="font-size:16px;font-family:宋体">行程结束后，乘坐汽车返回昆明，抵达昆明后，入住酒店休息。</span></p><p><img src="/ueditor/net/upload/2016-07-14/3b157e15-c7e6-47b0-885e-cd079f2d156f.jpg" title="b123e692-4029-46b5-b32b-08933ca34519.jpg"/></p>', 6, 73)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (462, N'住宿地→泸定→273km→成都', 1, 1, 0, N'', N'<p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: #002060">【康定】</span></strong><span style="FONT-FAMILY: 宋体">一座享誉世界的历史文化名城；一座跑马山，名扬五洲四海；一曲《康定情歌》千古绝唱，醉了天下人。康定市具有悠久灿烂的历史文化，是川藏咽喉、茶马古道重镇、藏汉交汇中心。自古以来就是康巴藏区政治、经济、文化、商贸、信息中心和交通枢纽。</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; COLOR: #00b050">【行】</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体">早上：</span></strong><span style="FONT-FAMILY: 宋体">6:30</span><span style="FONT-FAMILY: 宋体">出发告别世外桃源般的小镇新都桥，翻越康巴第一关―</span><strong><span style="FONT-FAMILY: 宋体">【</span></strong><span style="FONT-FAMILY: 宋体">折多山</span><strong><span style="FONT-FAMILY: 宋体">】</span></strong><span style="FONT-FAMILY: 宋体">（海拔4298米），经跑马溜溜的情歌故里【康定】，前往革命前辈战斗过的地方【泸定】，行走在（自愿前往，费用自理10元/人）【<strong>泸定铁索桥】</strong>拍照留影，感受红军飞夺泸定桥；后午餐。穿行于美丽的大渡河峡谷经【雅安】返回成都，结束此次愉快的旅行！</span><span style="FONT-FAMILY: 宋体; COLOR: #00b050">&nbsp;&nbsp;&nbsp; </span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; COLOR: #00b050">【游】</span><span style="FONT-FAMILY: 宋体; COLOR: black">【康定】【雅安】</span></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: red">温馨提示：</span></strong></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 7px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: black">1</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: black">因道路维修，部分路段实行交通管制，容易堵车造成延时回成都，建议预定次日回程大交通，以免造成不必要的损失。</span></strong></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 1px 13px 0px; TEXT-AUTOSPACE: "><strong><span style="FONT-FAMILY: 宋体; COLOR: black">2.</span></strong><strong><span style="FONT-FAMILY: 宋体; COLOR: black">散客到达成都后统一地点散团。</span></strong></p><p style="LINE-HEIGHT: 150%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: 宋体">&nbsp;<img title="高尔寺山1.jpg" src="/ueditor/net/upload/2016-07-14/5769a771-aefc-4701-8ed9-3c47c10c43a7.jpg"/></span></p>', 6, 74)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (465, N'稻城（77km)→香格里拉 (大约2.5个小时）', 1, 0, 1, N'香格里拉镇（日瓦镇）（海拔2700m）或亚丁村（海拔4100m）', N'<p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">稻城亚丁有三座神山--仙乃日、央迈勇、</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/6191700-6404952.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">夏诺多吉</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">是守护亚丁藏民的守护神山，北</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">峰仙乃日意为&quot;观</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/1484533-1569817.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">世音菩萨</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&quot;(6032</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">米)、南峰央迈勇&quot;文殊菩萨&quot;(5958米)、东峰夏诺</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">多吉&quot;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/5649577-5862219.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">金刚手菩萨</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&quot;(5958</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">米)，据说若藏民能够朝拜三次神山，便能实现今生之所愿。</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">登山沿途所见景色堪称人间仙境，雪山、冰湖、奇石、高原草场，脚下的每一寸都</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">令人惊叹造物主的神意</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 7px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: &#39;Arial&#39;,&#39;sans-serif&#39;; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><strong><span style="POSITION: relative; FONT-FAMILY: 黑体; COLOR: #010f21; FONT-SIZE: 16px; TOP: 1px">【央迈勇】</span></strong></p><p><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">南峰央迈勇&quot;文殊菩萨&quot;(5958米)、</span></p><p><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><strong><span style="POSITION: relative; FONT-FAMILY: 宋体; FONT-SIZE: 15px; TOP: 1px">【夏诺多吉神山】</span></strong></p><p><strong><span style="POSITION: relative; FONT-FAMILY: 宋体; FONT-SIZE: 15px; TOP: 1px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span style="POSITION: relative; FONT-FAMILY: 宋体; FONT-SIZE: 15px; TOP: 1px">意为“金刚手菩萨”是“三怙主”雪山的东峰（海拔5900m）。</span></p><p><span style="POSITION: relative; FONT-FAMILY: 宋体; FONT-SIZE: 15px; TOP: 1px"></span>&nbsp;</p><p style="LINE-HEIGHT: 20px; TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">早餐后乘车前往令人神往的亚丁景区，观万亩白杨林，翻越海拔4300多</span><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">米的【波瓦山】，到达【亚丁景区】后在游客接待中心换乘景区<span style="TEXT-DECORATION: underline">观光车(120元/人自理)</span>抵达亚丁景区龙龙坝（30多公里，车程1.5小时），到达【扎罐崩】后游客徒步抵达冲古寺，观赏冲古寺风光及神山仙乃日风光，然后可自费乘坐电瓶车（往返80，单程50）前往【洛绒牛场】（徒步1-2小时左右达到洛绒牛场）。这里是观【夏诺多吉神山】【央迈勇神山】的最佳地点，后原路返回，到达【扎罐崩】乘观光车，返回住宿地（亚丁村或香格里拉镇）（晚餐后客人可自愿选择参加走进藏家180元/人，费用自理）（如住景区 内无法安排此活动）</span></p><p style="LINE-HEIGHT: 20px; TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">&nbsp;</span></p><p style="TEXT-INDENT: 43px"><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;</span></span><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【游】</span><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">【白杨林】【波瓦山】【亚丁景区】</span></p><p style="TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">&nbsp; </span><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;</span></span><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【自费】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">参考价：<span style="COLOR: black">走进藏家180元/人</span></span></p><p style="TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><span style="COLOR: black"></span></span>&nbsp;</p><p style="TEXT-INDENT: 53px; MARGIN: 0px 35px 0px 0px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【温馨提示】</span></p><p style="TEXT-INDENT: 53px; MARGIN: 0px 35px 0px 0px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px"></span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">1.</span></span><span style="FONT-FAMILY: 宋体; COLOR: red; FONT-SIZE: 15px">景区一次观光车(120元/人自理)</span></p><p style="TEXT-INDENT: 70px; MARGIN: 0px 35px 0px 0px"><span style="FONT-FAMILY: 宋体; COLOR: red">冲古寺</span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">―</span></span><span style="FONT-FAMILY: 宋体; COLOR: red">络绒牛场：电瓶车往返</span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">80</span></span><span style="FONT-FAMILY: 宋体; COLOR: red">元，单程</span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">50</span></span><span style="FONT-FAMILY: 宋体; COLOR: red">元（自理）</span></p><p style="MARGIN: 0px 35px 0px 42px"><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="FONT-FAMILY: 宋体; COLOR: red">络绒牛场―五色海、牛奶海骑马参考价：</span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">300-500</span></span><span style="FONT-FAMILY: 宋体; COLOR: red">元</span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">/</span></span><span style="FONT-FAMILY: 宋体; COLOR: red">匹</span><span style="COLOR: red"><span style="FONT-FAMILY: Calibri">/</span></span><span style="FONT-FAMILY: 宋体; COLOR: red">人（自理）</span></p><p style="MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; COLOR: red"></span><span style="COLOR: #c00000"><span style="FONT-FAMILY: Calibri">2. </span></span><span style="FONT-FAMILY: 宋体; BACKGROUND: yellow; COLOR: #c00000">五色海</span><span style="BACKGROUND: yellow; COLOR: #c00000"></span><span style="FONT-FAMILY: 宋体; BACKGROUND: yellow; COLOR: #c00000">牛奶海：海拔很高对身体素质要求非常高，五色海、牛奶海不包含在旅行社景区行程内安排，请游客根据自身身体素质选择是否前往，我社不建议游客前往这两个景点。以上告之旅客如充分清楚后仍决定前往，请到导游处签好离团说明。一切后果自负。</span></p><p style="TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><span style="COLOR: black"></span></span>&nbsp;<img src="/ueditor/net/upload/2016-07-14/7f1469c8-ac1a-4e56-8f9e-9708a2e0ba11.jpg" title="wKgB4lMRjcCAOy4TAAIgdYxvXRE27.jpg"/></p><p>&nbsp;</p>', 3, 72)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (466, N'亚丁（36km)→香格里拉镇（74km)→稻城(110公里）', 1, 1, 1, N'稻城县（海拔3700m）', N'<p style="TEXT-ALIGN: left; LINE-HEIGHT: 7px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><strong><span style="POSITION: relative; FONT-FAMILY: 黑体; COLOR: #010f21; FONT-SIZE: 16px; TOP: 1px">【仙乃日】</span></strong></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">三座神山--仙乃日、央迈勇、</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/6191700-6404952.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">夏诺多吉</span></a>,</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">北峰仙乃日意为&quot;观</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/1484533-1569817.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">世音菩萨</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&quot;(6032</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">米)、南&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">峰央迈勇&quot;文殊菩萨&quot;(5958米)、东峰夏诺多吉&quot;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/5649577-5862219.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">金刚手菩萨</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&quot;(5958</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">米)，据说若藏民</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">能够朝拜三次神山，便能实现今生之所愿。登山沿途所见景色堪称人间仙境，雪山、</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">冰湖、奇石、高原草场，脚下的每一寸都令人惊叹造物主的神意</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【行】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">早餐后，如住景区外乘观光车（二次观光车60自理）抵达【扎罐崩】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">下车后游客徒步上山直接抵达冲古寺观赏冲古寺风光及神山【仙乃日】，朝拜【卓</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">玛拉措神湖】（珍珠海）。朝观仙乃日神山（意为观世音菩萨，海拔6023米），</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">后原路返回，乘观光车出景区，在景区外用中餐后，一步一步远离最后的香格里</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">拉--亚丁，返回稻城入住，途中参观康巴特色【民俗文化村色拉藏寨或者龙古藏</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">寨】，免费品尝藏地原生态牦牛肉，详尽了解康巴藏族文化。可自费参加康巴藏</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">族特色民俗表演和特色藏餐180元/人，后入住稻城宾馆。</span></p><p style="LINE-HEIGHT: 20px; TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px"></span></p><p style="TEXT-INDENT: 43px"><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;</span></span><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【游】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【亚丁景区】<span style="COLOR: black"> 【稻城县】 </span></span></p><p style="MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;</span><br clear="all"/></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="COLOR: #00b050; FONT-SIZE: 21px"><span style="FONT-FAMILY: Calibri">&nbsp;&nbsp;</span></span><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【自费】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">参考价：康巴藏族特色民俗表演和藏餐（180元）</span></p><p><span style="FONT-FAMILY: Calibri">&nbsp;</span></p><p style="MARGIN: 0px 0px 0px 63px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【温馨提示】</span></p><p style="MARGIN: 0px 0px 0px 70px"><span style="FONT-SIZE: 15px"><span style="FONT-FAMILY: Calibri">1.</span></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">二次观光车</span><span style="FONT-SIZE: 15px"><span style="FONT-FAMILY: Calibri">60</span></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">（自理）</span></p><p style="MARGIN: 0px 0px 0px 71px"><span style="FONT-SIZE: 15px"><span style="FONT-FAMILY: Calibri">2.</span></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">可在第三天游完所有景点，第四天早上不用起个大早，在宾馆内休息至中餐前与团队汇合。</span></p><p style="MARGIN: 0px 0px 0px 63px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【赠送】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">免费参观稻城色拉坝藏寨或龙古民俗村体验康巴风情</span></p><p>&nbsp;<img src="/ueditor/net/upload/2016-07-14/34ba41ee-2efc-4219-bf6e-a1cd781489e8.jpeg" title="新都桥2.jpeg"/></p>', 4, 72)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (464, N'新都桥（68km)→雅江(137km)→理塘(148km)→稻城（海拔3700M）(351公里 大约10个小时）', 1, 0, 1, N'稻城（海拔3700m）', N'<p style="TEXT-ALIGN: left; LINE-HEIGHT: 1.5em; TEXT-INDENT: 41px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><strong><span style="POSITION: relative; FONT-FAMILY: 黑体; FONT-SIZE: 16px; TOP: 1px">【高尔寺山】</span></strong><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">高尔寺山海拔4659米(注:高尔寺山垭口海拔4412米)。位于</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/2964533-3127396.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">新都桥</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">与雅江之间，</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">如果说折多山是康巴第一关的话, 那么高尔寺山则是康巴第二关, 而且它是纵</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">览大雪山(</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/7640760-7914855.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">雅拉神山</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">、</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">折多山</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">和</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/7536860-7810953.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">贡嘎神山</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">)</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">的最佳位置之一。</span></p><p style="LINE-HEIGHT: 1.5em"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; </span><strong><span style="POSITION: relative; FONT-FAMILY: 黑体; COLOR: #010f21; FONT-SIZE: 16px; TOP: 1px">【稻城】</span></strong><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">稻城，古名为“稻坝”，系藏族音译，意为“山口开阔地”。清朝光绪三十三年</span><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">（1907年），曾经在此试种稻谷，为了祝其成功，故取名为“稻成”。</span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">早餐后，乘车翻越【高尔寺山】海拔4412米，抵雅砻江畔―康巴小县城</span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【雅江】海拔2900米，后翻越两座大山【剪子弯山】海拔4659米【卡子拉山】</span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">海拔4718米，中餐在【大河边】自费（120元）品尝有美味佳肴的高山无鳞冷水</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">鳕鱼，冷水鳕鱼一年最多长2两肉质细嫩，汤更加鲜美。后抵高原明珠、世界</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">高城【理塘】海拔4014米，远观【毛垭大草原】风光，翻越形似兔子的【兔子山】、</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">经青藏高原最大的古冰体遗迹，即“稻城古冰帽” 平均海拔4500米左右【海子山自然保护区】，抵【稻城县】（古名稻坝，意为山谷沟口开阔之地），海拔3700米，稻城晚餐后入住宾馆，（此天行程整天都在海拔高的高山上行走,路程远,比较辛苦,用餐时间相对较晚)</span></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><strong><span style="FONT-FAMILY: 宋体"></span></strong></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【高尔寺山】【雅江】【剪子弯山】【卡子拉山】【理塘】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【毛垭大草原】【兔子山】【稻城县】</span></p><p style="LINE-HEIGHT: 1.5em; MARGIN: 0px 35px 0px 42px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"></span></p><p style="LINE-HEIGHT: 1.5em; TEXT-INDENT: 43px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">自费，参考价：高山无鳞冷水鳕鱼120元。</span></p><p><img src="/ueditor/net/upload/2016-07-14/ee97bacd-156f-42c5-ad83-235877a0e78d.jpeg" title="wKgBpVXATj-AIAibABGlm_2UwEc98.jpeg"/></p>', 2, 72)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (467, N'稻城（148km)→理塘（137km) →新都桥(429公里）/磨西镇', 1, 1, 1, N'新都桥（海拔3700m）/磨西镇（海拔1600m）', N'<p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><strong><span style="POSITION: relative; FONT-FAMILY: 宋体; COLOR: #010f21; FONT-SIZE: 15px; TOP: 1px"></span></strong></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><strong><span style="POSITION: relative; FONT-FAMILY: 宋体; COLOR: #010f21; FONT-SIZE: 15px; TOP: 1px">&nbsp;&nbsp;&nbsp; 【红草地】</span></strong><span style="FONT-FAMILY: 宋体; COLOR: #666666; FONT-SIZE: 15px">&nbsp;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">（季节性景点）</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">红草地距离稻城县15公里，每当秋天的时候，河床上的石头上要长一种红色的苔藓物，</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">再加上背景为黄色、红色的树林，就形成了一副美丽的色彩画，这里是稻城景点的</span></p><p><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">代表之作！</span></p><p><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"></span>&nbsp;</p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【行】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">早餐后前往桑堆镇【红草地】（注：红草地为季节性景观，只有在每年</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">的9月底到10月初才能看到此处的草原呈红色），在秋天的时候，配合上后面泛</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">黄的桦树，倒映在水塘中，是许多书上所介绍的美景。之后翻越古冰川遗迹海子</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">山到达理塘，后经过雅江县、返回到新都桥，抵达新都桥或磨西镇入住酒店休息。</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【游】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【红草地（季节性景观）】<span style="COLOR: black"> 【雅江】【理塘】 【新都桥/磨西镇】</span></span></p><p><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"></span>&nbsp;<img src="/ueditor/net/upload/2016-07-14/1c29071e-31f7-4ad5-b581-6775b2090c2d.jpg" title="高尔寺山3.jpg"/></p>', 5, 72)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (468, N'住宿地→雅安（137km) →成都（420公里）', 1, 0, 0, N'', N'<p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><strong><span style="POSITION: relative; FONT-FAMILY: 宋体; COLOR: #010f21; FONT-SIZE: 15px; TOP: 1px">【雅安】</span></strong><span style="FONT-FAMILY: 宋体; COLOR: #666666; FONT-SIZE: 15px">&nbsp;</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">雅安素有“川西咽喉”、“西藏门户”、“民族走廊”之称，是</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/2871728.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">四川</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">旅游西环线和</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px"><a href="http://baike.haosou.com/doc/1950839.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">香&nbsp;&nbsp;&nbsp; </span></a></span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; COLOR: windowtext; FONT-SIZE: 15px; text-underline: none"><a href="http://baike.haosou.com/doc/1950839.html" target="_blank"><span style="COLOR: windowtext; text-underline: none">格里拉</span></a></span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">环线上的客源枢纽，交通枢纽和重要门户。&quot;雅雨&quot;、&quot;雅鱼&quot;、&quot;雅女&quot;，并称雅</span></p><p style="TEXT-ALIGN: left; LINE-HEIGHT: 3px; MARGIN: 20px 0px 8px; TEXT-AUTOSPACE: "><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">安三雅。</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【行】</span><span style="FONT-FAMILY: 宋体; COLOR: black">早餐后，从住地出发</span><span style="COLOR: black"></span><span style="FONT-FAMILY: 宋体; COLOR: black">欣赏如画的小桥、流水、人家、藏式民居风光，感受</span><span style="FONT-FAMILY: 宋体; COLOR: black">浓郁高原藏家风情。</span><span style="FONT-FAMILY: 宋体">早经雨城雅安、返回成都，结束行程。</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 15px">&nbsp;</span></p><p style="LINE-HEIGHT: 19px; MARGIN: 0px 0px 0px 29px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【游】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">【二O山<span style="COLOR: black">】</span>【雅安<span style="COLOR: black">】</span>【成都<span style="COLOR: black">】</span></span></p><p style="TEXT-INDENT: 29px"><span style="FONT-FAMILY: 宋体; COLOR: #00b050; FONT-SIZE: 21px">【温馨提示】</span><span style="FONT-FAMILY: 宋体; FONT-SIZE: 15px">路途中有堵车情况，请勿预定当天返程交通</span></p><p>&nbsp;</p>', 6, 72)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (648, N'日本人举行人生重要仪式的神宫：明治神宫（约50分钟）～观光公社（约50分钟）～东京最古老的寺庙：浅草雷门观音寺及仲见世商店街（约60分钟）～东京最繁华的商业街：银座(约2小时)～东京珍珠会馆（约40分钟）', 1, 1, 0, N'成田机场或千叶或周边', N'<p style="LINE-HEIGHT: 19px"><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【明治神宫】</span></strong><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">：</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">每年去参拜明治神宫的人数总是日本的最高纪录，尤其在新年伊始，因为据说明治神宫的卦签很灵验。每年都有多场新生儿命名仪式、成人礼、毕业典礼和婚礼等各种人生重要仪式在明治神宫举行。日本很多偶像明星的成人礼就在此举行，吸引了不少拥趸追访，而明治神宫每年上千场的日本传统婚礼，更为这里增添了一道精致美丽的风景。</span></p><p style="LINE-HEIGHT: 19px"><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【浅草观音寺】</span></strong><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">：</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">创建于公元628年的【浅草观音寺】是日本观音寺总堂，来到东京的观光客，一定会到浅草观音寺，仿如未到浅草观音寺，就好象没到过东京一般。瞧瞧漆着『雷门』字样的大红灯笼，就是浅草观音寺的大招牌，来到此地，您将会感受到日本人虔诚的民间信仰，大家不妨也一起来参拜一下，祈求平安吧！要进入【浅草观音寺】前，有一条著名的商店街『仲见世通』，贩卖着千变万化，各具特色的纪念品，同时也有远近驰名的『人形烧』点心，当「伴手」送给亲朋好友，也是不错的选择喔！在这里可以远眺东京新电视塔“天空树”。</span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">世界第一高塔</span><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【东京天空树】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">于</span><span style="FONT-SIZE: 12px"><span style="FONT-FAMILY: Times New Roman">2012</span></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">年</span><span style="FONT-SIZE: 12px"><span style="FONT-FAMILY: Times New Roman">2</span></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">月</span><span style="FONT-SIZE: 12px"><span style="FONT-FAMILY: Times New Roman">29</span></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">日建成。该塔高度为</span><span style="FONT-SIZE: 12px"><span style="FONT-FAMILY: Times New Roman">634</span></span><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">米高，被吉尼斯世界纪录认定为世界第一自立式电波高塔。</span></p><p><strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体; COLOR: red">【银座】</span></strong><span style="FONT-SIZE: 12px; FONT-FAMILY: 宋体">：银座是东京最主要的繁华商业街。以其华丽高雅、雍容大方、充满成熟浪漫的气息而着称。银座的地价在世界上屈指可数，物价为世界之最。既有百年老店，也有令人目不暇接的新潮店铺。假日的步行者天国，又让人感受银座的自由和惬意。</span></p><p>&nbsp;<img title="u=4293359577,2214693192&amp;fm=21&amp;gp=0.jpg" src="/ueditor/net/upload/2016-07-16/0fe83571-66e2-4a54-869a-ad811cd023a2.jpg"/></p>', 5, 69)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (619, N'海龙寺+精油spa', 1, 1, 1, N'普吉国五星酒店（Wyndham Sea pearl villas phuket或同级）', N'<p><span style="font-size: 16px; font-family: 宋体, SimSun;">早餐后前往<strong>【海龙寺】</strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><strong><img src="/ueditor/net/upload/2016-07-15/ce1b6da1-8ad5-459b-b16a-b11c171d1edd.jpg" title="big_231728_1426224393226.jpg"/></strong></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;">欣赏泰国特色的佛教文化。继而参观世界最大金氏纪录，而后将带领各位贵宾前往本公司特别安排的<strong>【泰式spa】</strong></span><span style="font-size: 16px;"><strong><span style="font-family: 微软雅黑; color: rgb(255, 0, 0);">『约60m』</span></strong></span><span style="font-size: 16px; font-family: 宋体, SimSun;">。</span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><img src="/ueditor/net/upload/2016-07-15/005c3ccf-25d5-438a-96cd-709168252552.jpg" title="20140815163224_5386_4378.jpg"/></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;"><img src="/ueditor/net/upload/2016-07-15/bb49d392-5bd0-486f-95b5-3cf22cce4dc8.jpg" title="4465E76D207386CFB643.jpg"/></span></p><p><span style="font-size: 16px; font-family: 宋体, SimSun;">享受舒筋活骨、消除疲劳之神奇疗效。后送回酒店</span></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p>', 4, 80)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (537, N'成都―【熊猫乐园】―松洲古城―九寨沟', 0, 1, 1, N'九寨沟', N'<p style="margin-left:24px"><span style="font-family: 宋体">◆</span><span style="font-size: 16px;"><span style="font-stretch: normal; font-family: &#39;Times New Roman&#39;;">&nbsp;</span><span style="font-family: 宋体;">早上6:00-7:00由小车免费在成都市三环路以内上门接客人至指定地点，7:30统一出发</span></span></p><p style="margin-left:24px"><span style="font-family: 宋体; font-size: 16px;">【</span><span style="font-size: 16px; font-family: 宋体;">统一集合地点参考：中圳商务酒店门口（地址：四川省成都市青羊区西安中路41号）或成都市金牛区沙湾路1号（一环路沙湾路口汇龙湾生活广场，永辉超市门口)，实际当日集合地点请以头天晚上旅行社工作人员或小车师傅通知为准，敬请理解】</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">◆&nbsp; 【<strong><span style="font-size: 16px; font-family: 宋体; color: rgb(0, 176, 80);">中国大熊猫保护研究中心都江堰基地</span></strong>】{赠送游览时间约60分钟，不含观光车}</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;"><img src="/ueditor/net/upload/2016-07-14/c4cedc98-7ed0-4aee-9c63-c219b8c84b83.jpg" title="u=3025168333,269459&amp;fm=21&amp;gp=0.jpg"/></span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;"><br/></span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">经成灌高速（50公里，车程约1小时左右）到达“中国大熊猫保护研究中心都江堰基地”游览中国三大熊猫保护中心之一的【大熊猫都江堰养殖基地】，参观大熊猫救援馆，乘坐景区观光车上行（自理单程10元/人往返15元）后徒步往下参观大熊猫野外生活区、盼盼园等。走进大熊猫的世界，近距离观赏憨态可掬的大熊猫。</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">熊猫基地简介：中国大熊猫保护研究中心都江堰基地(又名&quot;熊猫乐园&quot;)位处市青城山镇石桥村怀中路，距市区18公里，紧邻大熊猫栖息地世界遗产区域，拥有适宜大熊猫生活的气候和自然环境。</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">◆&nbsp; 一路沿岷江逆流而上，沿途观赏成都平原风光。车观 “ 地震壁画 ”。沿途车观藏族、羌族的独特民居、碉楼、吊桥等建筑</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">◆&nbsp; 【</span><span style="font-size: 16px; font-family: 宋体;"><strong><span style="font-size: 16px; font-family: 宋体; color: rgb(0, 176, 80);">午餐-特色羌婚宴</span></strong></span><span style="font-size: 16px; font-family: 宋体;">】{标准30元/人}</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">◆&nbsp; 【</span><span style="font-size: 16px; font-family: 宋体;"><strong><span style="font-size: 16px; font-family: 宋体; color: rgb(0, 176, 80);">松洲古城</span></strong></span><span style="font-size: 16px; font-family: 宋体;">】{赠送游览约30分钟，不含登古城墙费}</span></p><p style="text-indent:24px"><span style="font-size: 16px; font-family: 宋体;">零距离抚摸城砖，感悟岁月沧桑，让您感受汉唐时期古松州悠久历史和灿烂的文化，带给您不一样的震撼！</span></p><p style="margin-left:24px"><span style="font-size: 16px; font-family: 宋体;">◆&nbsp;【</span><span style="font-size: 16px; font-family: 宋体;"><strong><span style="font-size: 16px; font-family: 宋体; color: rgb(0, 176, 80);">藏家欢乐家访</span></strong></span><span style="font-size: 16px; font-family: 宋体;">】{约60~90分钟，赠送项目不去不退}</span></p><p style="text-indent:30px"><span style="font-size: 16px; font-family: 宋体;">前往藏家享用丰盛的藏式晚餐（藏式土火锅+手抓肉）及体验载歌载舞的藏家风情</span></p><p style="margin-left:30px"><span style="font-size: 16px; font-family: 宋体;">特色藏家家访简介：让您身临其境的融入藏家生活，感受藏族的建筑、风土人情、宗教信仰，与藏族帅哥美女一起参与锅庄狂欢，品尝具有藏羌特色的烤羊、青稞酒、酥油茶等。</span></p><p><span style="font-size: 16px; font-family: 宋体;">&nbsp; ◆下榻酒店；集合乘车（旅游巴士），前往指定酒店入住，后自由活动至次日出发</span></p><p><br/></p><p style="text-indent:30px"><span style="font-size: 16px; font-family: 宋体;"><strong><span style="font-size: 16px; font-family: 宋体; color: rgb(0, 176, 80);">指定区域：藏家春天、东藏明珠、亚西山庄、安多或同级</span></strong></span></p><p><span style="font-size: 16px; font-family: 宋体;">&nbsp; &nbsp;</span><span style="font-size: 16px; font-family: 宋体;">&nbsp;</span><span style="font-size: 16px; font-family: 宋体;">酒店类型：均为当地性价比高、经济舒适型酒店，均是按照藏式民居风格打造的藏式特色酒店，其中东藏明珠、藏家春天地处九寨沟漳扎镇沙坝村，周边环境清雅静谧，距离九寨沟景区停车场约7KM，车程20分钟左右。</span></p><p><span style="font-size: 9pt; font-family: 宋体; background: white;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="font-family: 宋体; font-size: 24px; background: white;">&nbsp; &nbsp; <span style="font-family: 宋体; font-size: 24px; color: rgb(255, 0, 0); background: white;"><strong>温馨提示</strong></span></span></span></p><p style="margin-right:3px"><span style="font-size: 16px; font-family: 宋体;">①出团前一天晚上18：00-21:30小车师傅会通知贵宾第二天早上上门的接客时间，请务必保持电话畅通；九寨沟的出发时间都比较早，如果您在成都是入住酒店，请自行提前通知酒店为您提供路餐作为早餐，早上前台处自行领取。自行预定酒店的游客建议尽量选择靠西门附近的酒店，出团比较方便；</span></p><p style="margin-right:3px"><span style="font-size: 16px; font-family: 宋体;">②此日成都―九寨沟全程约400公里，车程需8小时左右，途中可停留休息、停车拍照、上洗手间（全程洗手间均收费1-2元）整个三天行程沿途会有旅游车加水或供游客上厕所方便的路边站点，类似站点下车后属于自由活动时间，当天用完晚餐后也属于自由活动时间，自由活动期间请随身携带贵重物品，自行负责人身及财产安全。</span></p><p><span style="font-size: 16px; font-family: 宋体;">③进入藏区请尊重少数民族风俗习惯，如若你自由活动期间在附近逛街商店或小卖点，请不要讨价还价后而不购买，请不要与当地人争吵及发生冲突，夜间请不要自行外出，需要帮助请及时与随团导游或旅行社联系。</span></p><p><span style="font-size: 16px; font-family: 宋体;">④成都至九寨沟的路程较远，大部分为山路，如您要晕车，请务必携带晕车药。旅游旺季车辆增多，有可能会堵车，导致用餐时间较晚或抵达酒店入住的时间较晚，敬请理解。</span></p><p><br/></p>', 1, 77)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (538, N'乘观光车全天半自助深度游览【九寨沟景区】', 1, 0, 1, N'九寨沟', N'<p style="margin-left:24px"><span style="font-family: 宋体">◆<span style="font-stretch: normal;font-size: 9px;font-family: &#39;Times New Roman&#39;">&nbsp;</span></span><strong><span style="font-size:12px;font-family:宋体;color:teal">【</span></strong><span id="_baidu_bookmark_start_7" style="display: none; line-height: 0px;">?</span><span style="font-size: 16px;"><strong><span style="font-family: 宋体; color: teal;">九寨沟风景区】</span></strong></span><span style="font-family: 宋体; font-size: 16px; background: white;">{门票、观光车已含，全天自助游览}</span></p><p style="text-indent:30px"><span style="font-family: 宋体; font-size: 16px; background: white;">早餐（酒店住宿赠送）后乘车（旅游巴士）从酒店出发；</span><span style="font-family: 宋体; background-color: white;">抵达九寨沟景区停车场，下车步行至游客中心（距离约1000米），等待导游领票分发后进入景区</span><span style="font-family: 宋体; text-indent: 12px; background-color: white;">凭票进入景区，换乘景区内观光车，游览世界自然遗产</span></p><p style="text-indent:30px"><span style="font-family: 宋体; text-indent: 12px; background-color: white;">【九寨沟景区】</span><span style="font-family: 宋体; background-color: white;">九寨沟内推荐游览路线：沟口乘车--原始森林乘车--箭竹海观倒影走栈道或乘车--熊猫海乘车--五花海乘车--珍珠滩走栈道看完瀑布后走栈道至镜海停车场乘车--诺日朗旅游服务中心乘车--长海步行--五彩池乘车--诺日朗瀑布乘车--老虎海走栈道--树正瀑布、水磨房步行至树正群海走栈道--火花海乘车--芦苇海乘车--盆景滩乘车--宝镜岩乘车--出沟</span><span style="font-family: 宋体; background-color: white;">九寨沟风景名胜区简介：九寨集翠海、叠溪、彩林、雪峰和藏族风情于一体，其自成天成、美丽绝伦的自然风光闻名于世。蓝天、白云、雪山、森林、尽融于瀑、河、滩、缀成一串串宛若从天而降的珍珠；其分为则查洼沟、日则沟、树正沟三条沟，呈“丫”字分布；整个构成了一个五彩斑谰、神奇梦幻的人间仙境，一个不见纤尘、自然纯净的“童话世界”。</span></p><p style="margin-left:30px"><br/></p><p>&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;</p><p><img src="/ueditor/net/upload/2016-07-14/94387156-8f68-4deb-8c8a-b12e82357dc3.jpeg" style="float:none;" title="wKgB4lJGRNiAeiXnAA5F-DI5l9463.jpeg"/>&nbsp;&nbsp;<img src="/ueditor/net/upload/2016-07-14/a716e2c4-cb45-4a4c-83d3-14801f5ca3e5.jpeg" title="wKgB4lJhAEiAFW23AA2daEyj-Tg14.jpeg" style="float: none;"/></p><p style="margin-left:24px"><span style="font-family: 宋体">◆<span style="font-stretch: normal;font-size: 9px;font-family: &#39;Times New Roman&#39;">&nbsp;</span></span><strong><span style="text-decoration: underline; font-size: 12px; font-family: 宋体; color: teal;">【藏羌风情晚会】</span></strong><span style="font-family: 宋体; font-size: 16px; background: white;">{约90分钟，赠送项目不去不退}</span></p><p style="margin-left:30px"><span style="font-family: 宋体; font-size: 16px; background: white;">下午按团队约定时间出景区后在景区停车场集合乘车（旅游巴士），前往欣赏-名扬四海的藏羌风情晚会或九寨千古情（赠送）（报名时可根据个人喜好提前选定类别，价格标准详询报名点工作人员）</span></p><p style="margin-left:30px"><span style="font-family: 宋体; font-size: 16px; background: white;">歌舞晚会简介：A:普通类：九寨沟的藏羌民俗表演由来已久，表演内容包括，藏族歌舞、藏族传统活动表演、藏羌传统服饰展示等，演员还会与游客有非常热烈、欢快的互动，可以说去九寨沟旅游不去此地领略一翻藏、羌文化神秘魅力，那你的九寨沟之行是绝对不完整的。B：豪华类：宋城集团倾力打造，大型原生态歌舞《九寨千古情》是全球首创的5D剧院实景演出，优美而神秘的藏羌歌舞带你进入穿越时空之旅，辉煌的大唐皇宫展现了文成公主汉藏和亲的壮举。再现512汶川大地震的惨烈场面；是迄今为止四川省投资最巨、科技含量最高、藏羌原生态文化容量最大的旅游演艺秀。</span></p><p style="margin-left:24px"><span style="font-family: 宋体">◆<span style="font-stretch: normal;font-size: 9px;font-family: &#39;Times New Roman&#39;">&nbsp;</span></span><span style="font-family: 宋体; font-size: 16px; background: white;">下榻酒店，享用晚餐；晚会结束后导游安排乘车入住酒店，在酒店餐厅享用酒店配套团队晚餐（酒店赠送不用不退），后自由活动至次日出发</span></p><p><strong><span style="font-size:12px;font-family:宋体;color:teal">&nbsp;</span></strong><span style="font-size: 16px;"><strong><span style="font-family: 宋体; color: teal;"> 指定区域：</span></strong><span style="font-family: 宋体; color: teal;">藏家春天、东藏明珠、亚西山庄、安多或同级</span></span></p><p><span style="font-size: 16px; font-family: 宋体; color: teal;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong><span style="font-family: 宋体; font-size: 24px; color: rgb(255, 0, 0);">温馨提示</span></strong></span></p><p><span style="font-family: 宋体; background-color: white;"><span style="color:#008080">&nbsp;&nbsp;</span>①</span><span style="font-family: 宋体; background-color: white;">九寨沟分为三条沟，单程35公里，沟内每个景点都有一个观光车上下的车站，凭观光车票当日可在任一站点自由换乘，每辆观光车都有讲解导游。</span><span style="font-family: 宋体; background-color: white;">游览景区内三条沟的先后顺序，完全根据观光车调度人员临时安排分沟游览，这样也能更好的分散游客，让您在拍照的时候舒心一些。</span><span style="font-family: 宋体; background-color: white;">观光车一般是到每条沟最高景点下车，然后往下走游览，所以走路也不会觉得累。</span></p><p><span style="font-family: 宋体; font-size: 16px; background: white;">&nbsp; ②九寨沟景区最佳的游览方式还是自由活动，乘坐的观光车运行方式和城里的公交车是一样的，因此导游也无法全程陪同。</span></p><p><span style="font-family: 宋体; font-size: 16px; background: white;">&nbsp; ③九寨沟天气变化频繁；请备好保暖衣物、雨伞、防晒霜，太阳镜等物品；可自备感冒药、治腹泻和创口贴等药品。</span></p><p><span style="font-family: 宋体; font-size: 16px; background: white;">&nbsp; ④九寨沟景区内诺日朗餐厅用餐，自助餐60元/人起，用餐比较简单，而且会影响您的游览时间，建议您自带干粮进沟。</span></p><p><span style="font-family: 宋体; font-size: 16px; background: white;">&nbsp; ⑤九寨景区禁止吸烟，有吸烟习惯的客人请忍耐或是到专门的吸烟区，否则会受到高额罚款。九寨沟是世界自然遗产，爱它您就保护它。</span></p><p style="margin-right:3px"><span style="font-family: 宋体; font-size: 16px; background: white;">&nbsp; ⑥家访和晚会导游可以根据具体情况来调整日期、时间先后顺序安排，服务内容和质量不会改变，敬请谅解。</span></p><p><span style="font-size: 16px;">&nbsp; <strong><span style="font-family: 宋体; color: red;">赠送晚会的类别游客需在出团报名时确认清楚，我社严禁团上导游进行额外收费升级</span></strong></span></p>', 2, 77)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (961, N'成都-45KM都江堰-65KM汶川-285KM红原（约400KM，行车约7-8小时）', 0, 1, 1, N'红原/唐克', N'<p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 24px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; PADDING-TOP: 0px">早上成都三环内接，途经千年古堰――<strong>都江堰</strong>、<strong>紫坪埔水利工程</strong>，走都汶高速，车览5.12地震中“映秀”，</span><span style="PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: black; PADDING-TOP: 0px">可以看见512大地震给当地人民带来的巨大灾难景象，震撼的公路、倒塌的桥梁、一栋栋被破坏的楼房、还有各地政府为灾区人民地震后重建的一栋栋崭新的新居，</span><span style="PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; PADDING-TOP: 0px">经汶川县城，尔后领略米亚罗峡谷风光，穿越雄伟的鹧鸪山隧道（4420米）， 经红军会师地――刷马路口，翻越黄河与长江的分水领――<strong>查真梁子山</strong>景观，领略“才饮长江水，又食黄河鱼”的美妙情怀<span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: red; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">（查真梁子海拔4345米，游客切勿剧烈运动，以免引发严重的高原反应）</span>，抵达龙日坝草原，眼前呈现出的是一望无垠的草原风光：蔚蓝的天空、洁白的云彩、青青的草原、星星点点的格桑花、成群的牛羊、纯朴悠闲的牧民．．．．．．进入红原大草原，游览红原第一景――<strong>月亮湾</strong>，因草原上的河流弯曲像弯月，故名“红原月亮湾”。大自然就把景色布置在这片草滩上，这一景色恰似朝鲜族演员舞蹈时帽子上晃动的长长飘带，画出一个又一个弯曲的弧线，当柔和的阳光洒向水面的时候，沉静的草滩顿时亮丽起来，水光泛金，蜿蜒如金龙起物，</span><span style="PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: black; PADDING-TOP: 0px">听着导游讲述月亮湾美丽动人的传说</span><span style="PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; PADDING-TOP: 0px">，抵</span></span><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 24px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: black; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px">达高原不夜城红原入住酒店</span><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 24px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: #ff0000; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px">(晚餐后参加走进藏家，体验藏家生活，此项目为赠送，不参加不退费，也不等价交换，如当天未安排则第二天晚上在川主寺安排)。</span></p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 24px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: #ff0000; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p><p style="TEXT-ALIGN: center"><img title="19128042.jpg" src="/ueditor/net/upload/2016-08-10/2e62f750-4534-45a9-8c9e-fabbd050a786.jpg"/></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/28px 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><strong><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: rgb(255,102,0); BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">温馨提示：</span></strong></span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px 3px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.75em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">1、本行程沿线属藏羌地区，用餐及住宿硬软件条件有限，限电限水，限时供应热水，请不要以城市的标准来衡量；沿线提倡绿色环保，请游客自带毛巾及洗漱用品等，若对住宿条件有更高要求的游客建议住宿升级。</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px 3px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.75em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">2、该线路景区景色优美世人向往，但沿途地处少数民族聚居地，用餐简单，建议您自备干粮或可口的榨菜等自己家乡佐料。</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px 3px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.75em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">4、整个三天行程沿途会有旅游车加水或供游客上厕所方便的路边站点，类似站点下车后属于自由活动时间，上厕所会有当地人收费（自费：一般为1元/人/次），自由活动期间请随身携带或寄存贵重物品，自行负责人身及财产安全。</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.75em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">5、进入藏区，请尊重少数民族风俗习惯，如若你自由活动期间在附近逛街商店或小卖点，请不要讨价还价后而不购买，请不要与当地人争吵及发生冲突，夜间请不要自行外出，需要帮助请及时与随团导游或旅行社联系。</span></p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 24px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: #ff0000; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p><p style="TEXT-ALIGN: center"></p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 24px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: #ff0000; BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p>', 1, 93)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (962, N'红原-130KM若尔盖-44KM花湖-180KM川主寺（约360KM，行车约5-6小时）', 1, 0, 1, N'川主寺/松潘', N'<p><span style="TEXT-ALIGN: left; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; DISPLAY: inline !important; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; FLOAT: none; LETTER-SPACING: normal; COLOR: rgb(77,77,77); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">早餐后乘车到高原明珠――若尔盖县，到达国家重点保护湿地、也是世界上保护最好的高原湿地、黑颈鹤栖息地――</span><strong style="TEXT-ALIGN: left; LINE-HEIGHT: 21px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); FONT-VARIANT: normal; FONT-STYLE: normal; TEXT-INDENT: 0px; FONT-FAMILY: 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); FONT-SIZE: 16px; WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">花湖</strong><span style="TEXT-ALIGN: left; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; DISPLAY: inline !important; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; FLOAT: none; LETTER-SPACING: normal; COLOR: rgb(77,77,77); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">（</span><strong style="TEXT-ALIGN: left; LINE-HEIGHT: 21px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); FONT-VARIANT: normal; FONT-STYLE: normal; TEXT-INDENT: 0px; FONT-FAMILY: 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); FONT-SIZE: 16px; WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">门票已含，电瓶车30元/人自理</strong><span style="TEXT-ALIGN: left; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; DISPLAY: inline !important; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; FLOAT: none; LETTER-SPACING: normal; COLOR: rgb(77,77,77); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">，游览时间约2小时），花湖是一片未被污染的处女地，鱼儿游弋，湖畔五彩缤纷，感受明镜般的湖水影照下的蓝天白云，领悟大自然的无私给予。花湖因水中开满花朵而得名，这里是中国最大最平坦的湿地草原，四周数百亩水草地就是“高原湿地生物多样性自然保护区”――被喻为“地球的肺”，保护区以黑颈鹤为主，可以走上浮桥，陶醉在湖水和鲜花的美景</span><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px">中，与水鸟嬉戏，寻找黑颈鹤的足迹。后抵<strong>镰刀坝草原</strong>，在这里有游玩方式齐备的牧家乐――品烤全羊、喝酥油茶、尝藏式土火锅、体验牧家生活，骑中国三大名马之一的河曲马，纵马驰骋，感受游牧族策马扬鞭的马背生活......<span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: red; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">（费用150-200元/人自理，视参加项目多少而定）</span>，后<span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: black; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">沐浴夕阳乘车到达川主寺入住酒店。</span><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: red; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">（晚上参加藏羌歌舞晚会，此项目为赠送，不参加不退费，也不等价交换）。</span></span></p><p style="TEXT-ALIGN: center"><img title="20116171322347989.jpg" src="/ueditor/net/upload/2016-08-10/0c44e598-dc6c-4462-b5b6-e78a50df46d3.jpg"/></p><p></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/28px 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><strong><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; COLOR: rgb(255,102,0); BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">温馨提示：</span></strong></span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.5em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">1、户外游览时间较长，日照充足，紫外线较强，请客人备好雨伞，防晒霜，太阳镜等物品，景区内禁止抽烟，严禁乱丢垃圾，违者最低罚款500元起。</span><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">2、骑马是一项危险的运动，保险公司不承保请一定注意安全，如发生意外由客人全权承担，切记！&nbsp;</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.5em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">3、沿线海拔3500米左右，游客切记不要剧烈运动，以免引发严重的高原反应。</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.5em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">4、沿途均有野生动物，请不要往窗外扔任何物品。</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/1.5em 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">5、大草原为游牧部落安多藏族的天堂，大多风俗和汉族不同，草原上听从导游安排，避免和当地人发生口角，尊重他们的风俗习惯，不要和当地人谈政治等敏感问题。</span></p><p></p>', 2, 93)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (963, N'川主寺-18KM松潘-138KM茂县-147KM成都（约300KM，行车约6小时）', 1, 1, 0, N'不含', N'<p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px">早餐后感受安多藏族、嘉绒藏族、羌族不同的民族风情，游览<strong>藏族文化特色村寨</strong>（约90分钟，近距离了解藏族民居特色、建筑艺术及生活方式，体验民族地区文化特色及经济建设成就），参观<strong>高原土特产展销中心</strong>（约60分钟）,参观</span><strong style="TEXT-ALIGN: left; LINE-HEIGHT: 21px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); FONT-VARIANT: normal; FONT-STYLE: normal; TEXT-INDENT: 16px; FONT-FAMILY: 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); FONT-SIZE: 16px; WORD-SPACING: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">丝绸博物馆</span></strong><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px">，了解古丝绸之路厚重的历史文化和复杂的制作工艺，可参与丝绸的拉丝和剥茧等互动活动，后乘车经历史名城――<strong>松潘古城</strong>，观</span><span style="TEXT-ALIGN: left; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; DISPLAY: inline !important; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; FLOAT: none; LETTER-SPACING: normal; COLOR: rgb(77,77,77); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px">1933</span><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px">年茂县地震遗址――<strong>叠溪海子</strong>，重温岷江峡谷风光，感受5.12大地震给沿线带来的巨大重创，以及灾后重建的繁荣，经汶川、映秀返成都结束愉快的旅程！</span></p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p><p style="TEXT-ALIGN: center"><img title="201504271448094104.jpg" src="/ueditor/net/upload/2016-08-10/079c1041-a726-4010-94c7-1518813c2160.jpg"/></p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/21px 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px"><strong><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体; COLOR: rgb(255,102,0); BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">温馨提示：</span></strong></span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/24px 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">1、此天行车时可能会遇上堵车等情况，行车时间较长，请不要购买当天返程的机票或火车票。<br/>2、景区内配套的商店有旅游纪念品及当地特色产品出售，景区讲解员可能会为游客推荐一并解说，若感兴趣，请您根据自身所需谨慎选购，记得索要发票。</span></p><p style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 14px/28px 微软雅黑, tahoma, 宋体, Arial; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"><span style="BORDER-BOTTOM: 0px; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT-FAMILY: 宋体, SimSun; FONT-SIZE: 16px; BORDER-TOP: 0px; BORDER-RIGHT: 0px; PADDING-TOP: 0px">3、今天是本次大草原之旅游程的最后一站，如若你对此次行程有任何异议，请告诉我们，我们会在第一时间根据你反馈的情况落实调查并及时处理。</span></p><p><span style="BORDER-BOTTOM: 0px; TEXT-ALIGN: left; BORDER-LEFT: 0px; PADDING-BOTTOM: 0px; WIDOWS: 1; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 16px; MARGIN: 0px; OUTLINE-STYLE: none; OUTLINE-COLOR: invert; PADDING-LEFT: 0px; OUTLINE-WIDTH: medium; PADDING-RIGHT: 0px; FONT: 16px/21px 宋体, SimSun; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(77,77,77); BORDER-TOP: 0px; BORDER-RIGHT: 0px; WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-stroke-width: 0px"></span>&nbsp;</p>', 3, 93)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (539, N'九寨沟-【藏寨】--川主寺―【高原土特产展销中心】、【德吉梅朵】―成都', 1, 1, 0, N'不含', N'<p style="margin-left:24px"><span style="font-family: 宋体">◆<span style="font-stretch: normal;font-size: 9px;font-family: &#39;Times New Roman&#39;">&nbsp;</span></span><span style="font-family: 宋体; font-size: 16px;">早餐（酒店住宿赠送）后乘车（旅游巴士）从酒店出发</span></p><p style="margin-left:24px"><span style="font-family: 宋体">◆</span><span style="font-family: 宋体; font-size: 16px;">&nbsp;【<strong><span style="font-family: 宋体; font-size: 16px; color: rgb(0, 176, 80);">特色藏寨</span></strong>】{赠送游览约1.5小时，配藏寨讲解员}</span></p><p style="text-indent:24px"><span style="font-family: 宋体; font-size: 16px;">前往参观特色藏寨（游览时间约1.5小时）,了解少数民族地区生活习俗及经济建设成就</span></p><p style="margin-left:24px"><span style="font-family: 宋体; font-size: 16px;">特色藏寨简介：这里是藏民族聚居村寨，边远之地还保留国内少有的一妻多夫的婚姻制度。这里五色经幡如祥云般缭绕在山间，美丽的草原醉人心脾，静谧和谐的藏族小村庄静卧在蓝天下。具有自然人文景观优美、民俗风情纯朴和民族宗教文化浓郁等特色。</span></p><p style="margin-left:24px"><span style="font-family: 宋体; font-size: 16px;">◆&nbsp;【</span><span style="font-family: 宋体; font-size: 16px;"><strong><span style="font-family: 宋体; font-size: 16px; color: rgb(0, 176, 80);">高原土特产展销中心</span></strong></span><span style="font-family: 宋体; font-size: 16px;">】</span><span style="font-family: 宋体; font-size: 16px;">{约1.5小时}</span></p><p style="text-indent:18px"><span style="font-family: 宋体; font-size: 16px;">本着消费自愿的原则，您可根据自身需求和个人喜好捎带一些高原土特产作为伴手礼，回家馈赠亲朋好友，分享旅程喜悦。</span></p><p style="margin-left:24px"><span style="font-family: 宋体; font-size: 16px;">◆&nbsp;【</span><span style="font-family: 宋体; font-size: 16px;"><strong><span style="font-family: 宋体; font-size: 16px; color: rgb(0, 176, 80);">德吉梅朵风情街</span></strong></span><span style="font-family: 宋体; font-size: 16px;">】{包含丝绸博物馆、茶马古道互市，赠送游览约1.5小时，配景区讲解员}</span></p><p style="text-indent:30px"><span style="font-family: 宋体; font-size: 16px;">德吉梅朵―（是高原上最美丽、吉祥的花朵）参观当年丝绸之路与茶马古道的繁荣热闹景象</span></p><p style="margin-left:30px"><span style="font-family: 宋体; font-size: 16px;">德吉梅朵简介：位于松潘县川主寺镇的德吉梅朵风情街，在打造中将茶马古道文化、丝绸之路溯源、藏传佛教文化以及岷江源文化相融合，从多方位、多角度的向游客展示了阿坝州的历史文化背景和藏族文化韵味，形成了吃、住、行、游、购、娱为一体的旅游综合服务区，成为了松潘县文化生态旅游产业发展的示范区。行走于德吉梅朵风情街上，穿梭在鳞次栉比的藏寨屋舍之间，观赏着独具韵味的藏家舞蹈，听着拥有淳朴乡音的讲解，仿佛一切都在如梦的画里；</span></p><p style="margin-left:24px"><span style="font-family: 宋体; font-size: 16px;">◆&nbsp;【</span><span style="font-family: 宋体; font-size: 16px;"><strong><span style="font-family: 宋体; font-size: 16px; color: rgb(0, 176, 80);">午餐―豪庭臧宴</span></strong></span><span style="font-family: 宋体; font-size: 16px;">】{标准30元/人，具体用餐地点由导游根据当日实际情况安排}</span></p><p style="margin-left:24px"><span style="font-family: 宋体; font-size: 16px;">◆&nbsp;稍作休息，统一集合乘车（旅游巴士）经都江堰地界返回成都结束此次愉快的旅途！</span></p><p style="text-indent:24px"><span style="font-family: 宋体; font-size: 16px;">成都统一散团地点参考：茶店子地铁口（客运站）附近（具体地点以当日实际散团为准）</span></p><p><span style="font-family: 宋体; font-size: 16px;">散客拼团回程均为统一地点散团，不提供免费回送其他指定地点的服务，敬请理解；如需回送指定地点，请报团时联系工作人员另行支付费用可安排代订此业务（费用根据回送地点距离不同而有所差异）</span></p><p></p><p><img src="/ueditor/net/upload/2016-07-14/63fddf66-e574-4845-ab0f-246b5ffe4fdf.jpg" title="川主寺1.jpg" style="float: none;"/></p><p><span style="font-family: 宋体; font-size: 16px;"><strong><span style="font-family: 宋体; font-size: 24px; color: rgb(255, 0, 0);">温馨提示</span></strong></span></p><p><span style="font-family: 宋体; font-size: 16px;">&nbsp; ①当天返回成都时间大约在下午的21：00左右，由于高原路段有可能堵车，建议客人不要预定当天返程的火车或是飞机，以免因为堵车给您带来没有必要的损失。</span></p><p><span style="font-family: 宋体; font-size: 16px;">&nbsp; ②行程中参观的藏寨、高原土特产展销中心、德吉梅朵风情街等，所有景区内的配套的商店有旅游纪念品及当地特色产品出售，包括但不限于土产、丝绸、银饰品、矿产等商品。景区讲解员可能会为游客推荐一并解说，如若感兴趣，请您根据自身所需及喜好自由选购，记得索要发票。</span></p><p><span style="font-family: 宋体; font-size: 16px;">&nbsp; ③今天是本次九寨之旅游程最后一站，如若你对此次行程有任何异议，请告诉我们，我们会在第一时间根据你反馈的情况落实调查并及时处理；这次旅程是一次心灵的洗礼，人生观的再次升华。</span></p><p><span style="font-family: 宋体; font-size: 16px;">&nbsp; ④此日早上出发时间会比较早（5:30-6:00左右），故早上可能无法在酒店餐厅享用早餐，届时酒店会安排打包路早供游客带走食用。敬请理解！晚餐不含，请自理</span></p><p><br/></p>', 3, 77)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (950, N'红原-若尔盖大草原-薰衣草庄园-川主寺', 1, 0, 1, N'川主寺/松潘', N'<p><span style="font-family: 宋体, SimSun; font-size: 16px;"><span style="font-family: 宋体, SimSun; color: rgb(77, 77, 77); line-height: 21px; background-color: rgb(255, 255, 255);"></span></span></p><p><span style="font-size: 16px;"><span style="font-family: 宋体;">早餐后乘车到周恩来总理亲笔题写的“红军走过的大草原“――</span><span style="font-family: 宋体, SimSun;"><strong style="color: rgb(77, 77, 77); line-height: 21px;"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: red; background-color: rgb(255, 255, 255);">日干乔沼泽地</span></strong></span><span style="font-family: 宋体;">观光。后抵镰刀坝草原，在这里有游玩方式齐备的牧家乐――品烤全羊、喝酥油茶、尝藏式土火锅、草原摩托，酸奶，放风筝，滚铁环，荡秋千，体验牧家生活，骑中国三大名马之一的河曲马，纵马驰骋，感受游牧族策马扬鞭的马背生活......</span><strong><span style="font-family: 宋体, SimSun; border: 0px; outline: none; margin: 0px; padding: 0px; line-height: 21px; color: red; background-color: rgb(255, 255, 255);">（费用200元/人自理），</span></strong><span style="font-family: 宋体;">这里蓝天白云，绿草繁花，帐篷炊烟，牛羊骏马，盘旋的雄鹰，下午乘车到尕里台草原，后前往</span><strong style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 21px;"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: red; background-color: rgb(255, 255, 255);">薰衣草庄园基地</span></strong><span style="font-family: 宋体;">游览（门票40元/人，费用已含，游览时间2h），薰衣草庄园介绍：又到草长莺飞的季节，在美丽的松潘，占地3000余亩的薰衣草正相继绽放，汇成一片紫色花海，被誉为高原的“普罗旺斯”。“今年紫色的花海会比去年更加壮观，更有普罗旺斯薰衣草花海的感觉。”据介绍，从今年5月下旬开始，在海拔3000米的松潘县川主寺镇，薰衣草已陆续绽放，相对去年400余亩的规模，今年的薰衣草已扩种至3000余亩，7-8月将进入全面盛开期，花期长达4个月。堪比普罗旺斯的成片紫色花海，无疑是九环线的一道独特的风景，吸引游客纷纷驻足拍照留影，后入住酒店</span><strong><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 21px; color: red; background-color: rgb(255, 255, 255);">（晚上参加藏羌歌舞晚会）</span></strong><span style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 21px; background-color: rgb(255, 255, 255);">。</span></span></p><p><span style="font-size: 16px;"><span style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 21px; background-color: rgb(255, 255, 255);"><br/></span></span></p><p><span style="font-size: 16px;"><span style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 21px; background-color: rgb(255, 255, 255);">&nbsp; &nbsp;&nbsp;<img src="/ueditor/net/upload/2016-08-10/b206f9c7-e9e4-4059-a66a-e96f02f7db92.jpg" title="QQ图片20160810145414.jpg"/></span></span></p><p><span style="font-size: 16px;"><span style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 21px; background-color: rgb(255, 255, 255);"><br/></span></span></p><p><span style="font-size: 16px;"><span style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 21px; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 21px;  color: blue; background-color: rgb(255, 255, 255);"><strong>温馨提示：</strong></span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 21px;  color: rgb(0, 0, 153); background-color: rgb(255, 255, 255);">户外游览时间较长，日照充足，紫外线较强，请客人备好雨伞，防晒霜，太阳镜等物品，请遵守景区内管理制度禁止抽烟，乱丢垃圾，违者最低罚款500元起。</span></span></span></p>', 2, 57)
INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (951, N'川主寺-17km松潘-149km茂县-190km成都', 1, 1, 0, N'不含', N'<p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">早餐后参观具</span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 24.5px; font-size: 16px; background-color: rgb(255, 255, 255);"><span style="outline: none; margin: 0px; padding: 0px; color: black; font-family: 宋体, SimSun; line-height: 24.5px; background-color: rgb(255, 255, 255);">有藏族文</span></span><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 24.5px; background-color: rgb(255, 255, 255);"><span style="outline: none; margin: 0px; padding: 0px; color: black; line-height: 24.5px; background-color: rgb(255, 255, 255);"></span><span style="outline: none; margin: 0px; padding: 0px; color: black;">化的<strong>特色</strong></span></span><strong style="color: rgb(77, 77, 77); font-family: 宋体, SimSun; line-height: 24.5px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="border: 0px; outline: none; margin: 0px; padding: 0px; color: black;">藏寨</span></strong><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">，近距离了解藏族民居特色、建筑艺术及生活方式，体验民族地区文化特色及经济建设成就；参观当地<strong>土特产展销中心，丝绸博物馆</strong>，乘车经历史名城――松潘古城，后经老虎嘴（堰塞湖），沿“震中”大峡谷、茂县、汶川返回成都，结束愉快美妙的之旅。</span></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);"><br/></span></p><p><span style="border: 0px; outline: none; margin: 0px; padding: 0px; font-family: 宋体, SimSun; line-height: 24.5px;  background-color: rgb(255, 255, 255);">&nbs