INSERT [dbo].[LineContent] ([Id], [title], [morn], [noon], [night], [accom], [content], [daySort], [lineId]) VALUES (877, N'成都-都江堰-茂县-松潘-若尔盖镰刀坝草原-川主寺-九寨沟', 0, 1, 1, N'藏式特色酒店 ', N'<p style="LINE-HEIGHT: 115%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: Tahoma"></span></p><p style="margin-right:2px"><span style="font-family: 宋体, SimSun; font-size: 16px; background: rgb(204, 255, 204);"></span></p><p style="margin-right:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style="FONT-FAMILY: 宋体">早餐后乘车从成都出发,沿途观赏成都平原风光，经过都江堰市、映秀、汶川、茂县、松潘，午餐后抵达游览</span><span style=";font-family:微软雅黑;font-size:16px">【</span><strong><span style="font-family: 微软雅黑;font-size: 16px">若尔盖镰刀坝大草原</span></strong><strong><span style="font-family: 微软雅黑;font-size: 16px">】</span></strong><strong><span style="font-family: 微软雅黑;font-size: 16px">，</span></strong><span style="FONT-FAMILY: 宋体">感受“天苍苍，野茫茫，风吹草地现牛羊”的</span><span style="FONT-FAMILY: 宋体">自然风光，在草原上您可以纵马驰骋（骑马自理80元/人），之后驱车前往人间仙境、童话世界九寨沟，到达后</span><span style=";font-family:微软雅黑;color:rgb(255,0,0);font-size:16px">晚餐升级一餐藏族土火锅</span><span style="FONT-FAMILY: 宋体">参加</span><strong><span style="font-family: 微软雅黑;font-size: 16px">免费赠送的走进藏家家访+锅庄晚会（不参加不退费）</span></strong><span style="FONT-FAMILY: 宋体">，让您身临其境的感受藏家生活、藏族文化和藏族风情与少数民族朋友举手狂欢锅庄晚会！</span></p><p style="margin-right:2px"><span style="font-family: 宋体, SimSun; font-size: 16px;"></span><br/></p><p style="LINE-HEIGHT: 115%; MARGIN-RIGHT: 2px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #ff6600">镰刀坝草原：</span><span style="FONT-FAMILY: 宋体">因在空中俯瞰整个草原形状犹如镰刀，故称镰刀坝草原。是若尔盖草原的一个分支，海拔3280米，这里留下过以前红军不少的足迹，也有很多红军长眠在了这片土地上。这里有抹不去的红色记忆。在这里可以骑马，可以踢球，还可以感受到天苍苍，野茫茫的大草原之景。</span></p><p style="LINE-HEIGHT: 115%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: Tahoma">&nbsp;</span></p><p style="LINE-HEIGHT: 115%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: Tahoma">&nbsp;</span></p><p><img title="f20150513180222163933.png" src="/ueditor/net/upload/2016-08-04/e95889f7-3efa-4cdd-93c3-833a27badfba.png"/></p><p><br/></p><p style="LINE-HEIGHT: 115%; MARGIN: 0px 0px 13px"><span style="FONT-FAMILY: &#39;微软雅黑&#39;,&#39;sans-serif&#39;; COLOR: #ff6600">藏式土火锅宴介绍：</span><span style="font-family: 宋体, SimSun;">让您身临其境的融入藏家生活，感受藏族的建筑、风土人情、宗教信仰，与藏族帅哥美女一起参与锅庄狂欢，品尝具有藏羌特色的烤羊、青稞酒、酥油茶等。</span></p><p><img title="6d7a524f2d1b00.jpg_r_640x420x90_425b8de5.jpg" src="/ueditor/net/upload/2016-08-04/63118657-a0a6-41be-a7ab-97d8c4cc3503.jpg"/><span style="font-family: 宋体, SimSun;"></span></p><p><br/></p>', 1, 90)
